package com.example.schoollanguage;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.content.Intent;
import android.os.Bundle;

import com.example.schoollanguage.Adapters_and_itemTitle_class.AdapterSyntaksys;
import com.example.schoollanguage.Adapters_and_itemTitle_class.ItemTitle;

import java.util.ArrayList;

public class SyntaksysActivity extends AppCompatActivity implements AdapterSyntaksys.OnItemClickListener   {
    private AdapterSyntaksys adapterSyntaksys;
    private RecyclerView recycler;
    private ArrayList<ItemTitle> listSyntaksys;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_syntaksys);
        recycler = findViewById(R.id.recycler_syntaksys);
        LinearLayoutManager manager = new LinearLayoutManager(this);

        listSyntaksys = new ArrayList<>();
        listSyntaksys.add(new ItemTitle("Синтаксис жөнүндө жалпы маалымат"));
        listSyntaksys.add(new ItemTitle("Сүйлөмдүн жалпы классификациясы"));
        listSyntaksys.add(new ItemTitle("Сүйлөмдүн түрлөрү"));
        listSyntaksys.add(new ItemTitle("Сүйлөмдүн айтылыш максатына карай бөлүнүшү: жай, суроолуу, илептүү,буйрук сүйлөмдөр"));
        listSyntaksys.add(new ItemTitle("Сүйлөмдүн мүчөлөрү"));
        listSyntaksys.add(new ItemTitle("Сүйлөмдүн бир өңчөй мүчөлөрү"));
        listSyntaksys.add(new ItemTitle("Чакчыл жана атоочтук түрмөктөр"));
        listSyntaksys.add(new ItemTitle("Грамматикалык жактан сүйлөмгө мүчө боло албаган сөздөр"));
        listSyntaksys.add(new ItemTitle("Татаал сүйлөм"));
        listSyntaksys.add(new ItemTitle("Татаал сүйлөмдүн түрлөрү"));
        listSyntaksys.add(new ItemTitle("Төл жана бөтөн сөз"));


        adapterSyntaksys = new AdapterSyntaksys(listSyntaksys);
        recycler.setLayoutManager(manager);
        recycler.setAdapter(adapterSyntaksys);
        adapterSyntaksys.setOnItemClickListener(SyntaksysActivity.this);

    }

    void openSecond(String theme, String data) {
        Intent intent = new Intent(SyntaksysActivity.this, ActivityThemes.class);
        intent.putExtra("key", data);
        intent.putExtra("key1", theme);
        startActivity(intent);
    }

    @Override
    public void onItemClick(int position) {
        switch (position) {
            case 0:
                openSecond("Тема: Кыргыз тилинин синтаксиси жана анын изилдөө объектиси.","Маселелер:\n" +
                        "1. Синтаксис жөнүндө жалпы маалымат.\n" +
                        "2. Сөз айкашынын синтаксиси.\n" +
                        "3. Синтаксистик байланыштын түрлөрү.\n\n" +
                        "1.Тилдин грамматикасы эки бөлүктөн – морфология жана\n" +
                        "синтаксистен турат. Морфология менен синтаксистик грамматиканын өз ара байланыштагы бөлүктөрү болгону менен, алардын изилдей турган, үйрөтө турган объектилери ар башка. Морфология жеке сөздөрдүн структурасын (негизги жана кошумча морфемалар), сөз өзгөртүүчү синтетикалык формаларды, алардын өнүгүү, өзгөрүү эрежелерин жана закондорун изилдейт. Ал эми синтаксис болсо жеке сөздөр, сөз өзгөртүүчү формалар эмес, сөз менен сөздүн айкашуусун, ал айкашуудан келип чыгуучу өтө эле ар түркүн конструкцияларды (сөз айкашы, сүйлөм), алардын грамматикалык түзүлүштөрүн, составдык бөлүктөрүн жана синтаксистик милдеттерин изилдеп, үйрөтөт.\n" +
                        "\nСинтаксис – грек сөзү (Syntaxis), бизче «түзүү», «кошуу» дегенди билдирет; ал кыргыз тилиндеги сөз айкашы менен сүйлөмдүн түзүлүш мыйзамдарын изилдейт да, байланыштуу кептин түзүлүшүн үйрөтүүчү грамматиканын бөлүгү болуп эсептелет.\n" +
                        "Грамматиканын бир бөлүгү болгон синтаксис сөздөрдүн бири-бири менен болгон өз ара байланышын, ал байланышуудан пайда болгон ар түркүн синтаксистик бирдиктерди (сөз айкашы, сүйлөм), аларды уюштуруучу тилдик каражаттарды, андай синтаксистик конструкциялардын грамматикалык түзүлүштөрүн, түрлөрүн изилдейт жана үйрөтөт.\n" +
                        "Синтаксис изилдей турган объектилерине ылайык эки бөлүмдөн турат: сөз айкашынын синтаксиси, сүйлөмдүн синтаксиси.\n" +
                        "\n2.Тил коомдун мүчөлөрүн бири-бири менен катнаштырууда, пикир алыштырууда аларды өз ара байланыштыруунун негизги каражаты катары дайыма сүйлөмдү пайдаланат.\n" +
                        "Сүйлөм болсо, тигил же бул тилдин мыйзамына ылайык грамматикалык жагынан уюшулган кептин бир бүтүн бирдиги жана адамдын оюн уюштуруучу, ал жөнүндө башкаларга кабар берүүчү негизги каражат болуп эсептелет. Сүйлөм аркылуу турмуштагы чындык жана ага карата болгон сүйлөөчүнүн мамилеси билдирилет. Сүйлөм дайыма сөздөрдүн жана сөз айкаштарынын ошол тилдин грамматикалык эрежеси жана мыйзамдары боюнча бири-бири менен өз ара байланышып келиши аркылуу уюшулат. Сүйлөм ар кандай эле сөздөрдүн кокустан болгон жыйындысынан түзүлө бербейт. Адатта, айтылуучу ойдун максатына ылайык сүйлөм куруу үчүн тандалып алынган сөздөр бири-бири менен мааниси жана грамматикалык жактан байланышып келип, эки же андан артык сөздөн турган синтаксистик бирдиктерди, сөз айкаштарын түзүшөт; кийин ал синтаксистик бирдиктер өз ара байланышып келип, бир бүтүн сүйлөмдү уюштурат. Демек, сүйлөмдү уюштурууда сөз айкаштары жана айрым сөздөр зор ролду ойнойт. Чындыктагы бардык көрүнүштөр өз ара тыгыз байланышкан, бири-бири менен шартташкан абалда болорлугу белгилүү. Ал эми ошол көрүнүштөр жөнүндө кабар берүүчү сүйлөмдөрдүн курамындагы сөздөр бири-бири менен байланышып келип, дайыма чындыктагы ошол заттардын, көрүнүштөрдүн, кыймыл- аракеттердин арасындагы байланыштарды билдиришет. Мисалы: Алар чак түштө бийик жондун үстүндөгү көк жайыкка жетишти. Бул сүйлөмдүн ичиндеги «чак түштө жетишти» деген синтаксистик бирдик мезгилдик катышты, «бийик жондун», «көк жайыкка» - сыпаттык катышты, «жондун үстүндөгү жайык» - орундук катышты билдирип турат. Сүйлөм ичиндеги сөздөрдүн маани жагынан жана грамматикалык жактан өз ара байланышкан мындай синтаксис бирдиктери ар дайым толук маанидеги эки же андан артык сөздөрдүн багыныңкы байланышынан түзүлөт.\n" +
                        "Толук маанидеги эки же андан артык сөздүн багыныңкы байланышынан түзүлгөн ал сөздөр туюндурган түшүнүктөрдүн ортосундагы ар түрдүү катышты билдирүүчү сөздөрдүн семантика- грамматикалык бирдиги сөз айкашы деп аталат. Мисалы: темир дарбаза, жаңы клуб, жыйырма кой, окуучунун китеби, чөпту үйүү ж.б. Бул сөз айкаштары эки сөздүн багынынкы байланышынан түзүлүп, тутумундагы сөздөр билдирген түшүнүктөрдүн ортосундагы ар кандай маанилик катышты: заттын эмнеден жасалгандыгын (темир дарбаза), сапаттык белгисин (жаңы клуб), сандык белгисин (жыйырма кой), таандык катышты (окуучунун китеби), аракет багытталган объектини билдирип турат (чөптү үйүү).\n" +
                        "Бирок сөздөрдүн ар кандай эле грамматикалык бирикмеси сөз айкашы боло бербейт; бири-бири менен маани жагынан жана грамматикалык жактан байланыштуу келген эки же андан көп толук маанилүү сөздөрдүн бирикмеси гана сөз айкашын уюштура алат. Себеби сүйлөм ичиндеги сөздөрдүн ортосундагы ар түрдүү синтаксистик мамиле толук маанидеги сөздөрдүн айкашы аркылуу гана берилет, толук маанилүү сөздөрдүн өз ара байланышы гана мааниси жана түзүлүшү жагынан бирдиктүү сөз айкашын уюштура алат: Мисалы, жашыл жайлоо, бийик үй, клубда иштөө, планды аткаруу ж.б.\n" +
                        "Бул сөз айкаштары толук маанидеги сөздөрдөн уюшулгандыктан, аныктоочтук, бышыктоочтук, объектилик катышты туюндуруп, структурасы жана мааниси жагынан толук уюшулган бир бүтүндүктү билдирип турушат. Ал эми толук маанилүү сөз менен жардамчы сөз байланышса, структуралык жана маани жагынан толук уюшулган өзүнчө бир сөз айкашын уюштура албайт. Мисалы: тоого карай, иш жөнүндө, үймөктүн түбүндө, эң кубаттуу ж.б. Мында толук маанидеги сөздөр менен жандоочтор, жардамчы атоочтор, бөлүкчөлөр айкашкан. Бирок алар өзүнчө сөз айкашы боло алышпайт, себеби структуралык жагынан жана маани жагынан толук уюшулуп бүтпөгөн. Бул өңдүү айкаштар экинчи бир сөз менен байланышып келгенде гана толук сөз айкашы уюшулат: тоого карай жөнөө, иш жөнүндө кам көрүү, үймөктүн түбүнө отуруу, эң кубаттуу электр станциясы ж.б. Сөз айкаштары багындыруучу сөзгө анын маанисин айкындоочу багыныңкы сөздүн кошулуп айтылышы менен уюшулат: костюм - жаңы костюм (костюм - багындыруучу сөз жаңы - багыныңкы сөз), галстук - окуучунун галстугу (галстугу - багындыруучу сөз, окуучунун - багыныңкы сөз), коргоо - Мекенди коргоо (коргоо -багындыруучу сөз, мекенди - багыныңкы сөз).\n" +
                        "Жогоруда белгиленгендей, сөз айкаштарынын тутумундагы сөздөрдүн бири экинчисине мааниси жана грамматикалык жактан багынып турат. Багындыруучу маанидеги сөзү сөз айкашынын багындыруучу түгөйү (багындыруучу компоненти) деп аталат да, ага багыныңкы абалда турганы багыныңкы түгөйү (багыныңкы компонент) деп аталат. Сөз айкашынын багыныңкы түгөйү багындыруучу түгөйүнүн ар кандай белгисин көрсөтүп, аныктап турат: Мисалы, атлас көйнөк, жыгач аяк, кызыл желекче, бешинчи катар ж.б.\n" +
                        "Мында багыныңкы түгөйү (биринчи сөз) өзүнөн кийинки багындыруучу түгөйү менен ыкташып байланышып, ал билдирген заттын кандай материалдан жасалгандыгын, өңүн, санын көрсөтүп турат: менин палътом, окуучунун дептери ж.б. - бул сөз айкаштары таандык байланыш жолу менен уюшулуп, заттын бирөөгө тиешелүүлүгүн билдирип турат: пахтаны терүү, бакты кыюу, курортко жөнөө, шаардан келүү - бул сөз айкаштарынын терүү, кыюу, жөнөө, келүү деген багындыруучу түгөйлөрү өзүнүн алдындагы сөздөрдү табыш, барыш, чыгыш жөндөмөлөрүнө келтирип башкарып, багыныңкы түгөйлөрү кыймыл-аракет багытталган объектини жана орундук мамилени билдирип турат.\n" +
                        "\n\nЭркин жана туруктуу сөз айкаштары.\n\n" +
                        "Сүйлөмдүн ичиндеги сөздөр дайыма грамматикалык жактан өз ара байланышып, бири-бири менен айкашып келишет. Сөз айкаштарынын компоненттеринин ортосундагы лексико-семантикалык жана грамматикалык байланыштар, ал байланыштардын мүнөзү түрдүүчө болот. Ошол өзгөчөлүктөрүнө карай сөз айкаштары эркин сөз айкаштары жана туруктуу сөз айкаштары болуп эки топко бөлүнөт.\n" +
                        "Эркин сөз айкаштарынын компоненттери сөз жүрүп жаткан учурда гана айтылуучу ойго ылайык тандалып алынат да, грамматикалык жактан өз ара байланышат. Эркин сөз айкаштарынын компоненттеринин ортосундагы байланыш белгилүү бир жагдайга карата пайда болгон убактылуу байланыш болуп эсептелет, ошондуктан анын тутумундагы сөздөр айтыла тур-ган ар бир жаңы ойдун мазмунуна ылайык улам алмашылып, жаңы сөздөр менен эркин айкаша берет. Эркин сөз айкашын уюштурган сөздөр өзүнүн жеке тургандагы фонетикалык, грамматикалык, семантикалык касиеттерин, лексикалык өз бетинчелигин толук сактайт, өзүнүн негизги түз маанисинен четтеп кетпейт. Мисалы: 1) жаңы үй, жаңы пальто, жаңы турмуш, жаңы китеп; 2) алыс жол, таштуу жол, тайгак жол, жалгыз аяк жол. Бул мисалдарда биринчи топтогу сөз айкаштарынын жаңы деген багыныңкы компоненти айтылуучу түшүнүктүн мазмунуна карай улам башка сөздөр менен (үй, пальто, турмуш, китеп) айкашып, жаңы сөз айкаштары түзүлдү. Экинчи топтогу сөз айкаштарында жол деген багындыруучу компоненти айтылуучу түшүнүктүн мазмунуна ылайык улам башка сөздөр (алыс, таштуу, тайгак, жалгыз аяк) менен айкашып, жаңы сөз айкаштары уюшулду. Экинчи жагынан, бул сөз айкаштарынын тутумундагы ар бир сөз өзүнүн алгачкы фонетикалык, грамматикалык, лексикалык өз бетинчелигин толук сактаган. Мындай сөз айкаштарынын компоненттери өзүнүн лексикалык маанисин толук сактагандыктан, алардын ар бири сүйлөмдүн айрым-айрым мүчөсүнүн милдетин аткарат: Сенин кызың ушуну туура чечти (Ч.А.).\n" +
                        "Эркин сөз айкаштарынын түгөйлөрүнүн ортосундагы байланыш анчалык бекем болбогондуктан жана бири-биринен оңой ажырап тургандыктан, алардын арасына айрым башка сөздөрду кошуп айта берүүгө болот. Мисалы: планды аткаруу - планды ашыра аткаруу, экскурсияга жөнөө - экскурсияга эрте жөнөө ж.б.\n" +
                        "\n\nКомпоненттери өз ара эркин байланышып, айтылуучу ар бир жаңы ойдун маз-мунуна ылайык улам жаңыдан түзүлүп турган сөз айкаштары эркин сөз айкаштары деп аталат.\n\n" +
                        "Айрым сөз айкаштары айтылуучу ойдун мазмунуна карай сүйлөм тизмегинде жаңы уюшулбастан, сүйлөм ичине бир бүтүн даяр биримдик (единица) катары киргизилет. Мисалы: Атасы Жакып - колунан көөрү төгүлгөн темир уста (Ш. Б.). Көчөдө көзгө сайса көрүнгүс караңгылык (К. Бек.). Мындагы «колунан көөрү төгүлгөн», «көзгө сайса көрүнгүс» деген идиомалар сүйлөм ичине мурунтан даяр бүтүн биримдик катары киргизилген.\n" +
                        "\n\nКомпоненттери бири-бири менен өтө тыгыз байланышып, маани жактан өз ара ширелишип келип жалпыланган бир бүтүн жаңы маанини туюндурса, туруктуу сөз айкаштары деп аталат\n\n. Мисалы: жүрөгү жок (коркок), беш өрдөгүн учуруу (калп айтуу) ж.б. Бул сөз айкаштарынын компоненттери өз ара тыгыз байланышкан, ошондуктан алардын ордун алмаштырууга, же арасына сөз кошуп айтууга болбойт. Экинчи жагынан, булардын тутумундагы сөздөр өзүлөрүнүн тике лексикалык маанисинде колдонулбай, маани жактан ширелишип келип, жалпыланган бир бүтүн жаңы маанини билдирип турушат.\n" +
                        "Эркин сөз айкаштары менен туруктуу сөз айкаштары төмөнкүдөй өзгөчөлүктөрү менен бири-биринен айырмаланышат:\n" +
                        "\n1) Эркин сөз айкаштары айтылуучу ар бир жаңы ойдун мазмунуна карай сүйлөм ичинде улам жаңыдан түзүлүп турса, туруктуу сөз айкаштары сөз жүрүп жаткан учурда жаңыдан уюшулбастан, сүйлөм ичине даяр бир бүтүн биримдик катары киргизилет. Мисалы: Райакимдин төрагасынын чоң кабинети элге жык толгон (Ч.А.). Бул сүйлөмдөгү райакимдин төрагасынын, чоң кабинети, төрагасынын кабинети, элге толгон, жык толгон деген эркин сөз айкаштары ушул сүйлөмдү айткан учурда гана анын маанисине ылайыкталып тандалып алынган сөздөрдүн бири-бири менен айкалышынан түзүлгөн. Каратай отурган топко келип кошулду (Ч.А.) десек, бул сүйлөмдүн маанисине карай отурган топко, топко келип кошулду деген жаңы сөз айкаштары уюшулду. Ал эми: Алар - ичинен таруу айланбаган кара ниеттер (М.Аб.). Тажыбай - колунан көөрү төгүлгөн атактуу уста (К.Бек.). Битин сыгып, канын жалаган байлар зили түбү жарыбайт го (К.Ж.) деген сүйлөмдөрдөгү кара менен терилген тизмектер -туруктуу фразеологиялык айкаштар. Булар ушул сүйлөмдөр айтылган учурда эле тиешелүү сөздөрдүн өз ара байланышынан жаңыдан түзүлгөн жок. Алар айтылып жаткан ойдун мазмунуна ылайыкталып, тилдик каражаттардын ичинен даяр, бир бүтүн биримдик катары тандалып алынып, сүйлөм тизмегинде колдонулду.\n" +
                        "\n2) Эркин сөз айкаштарында ар бир компонентинин лексикалык мааниси толук сакталат жана андай сөз айкаштарынын жалпы грамматикалык мааниси компоненттеринин жалпыланган лексико-семантикалык маанилеринин өз ара катышынан түзүлөт: жаңы мектеп, көрктөнгөн кыштак, кызылчаны отоо, чарбада иштөө. Мындагы 1,2 - сөз айкаштары, жалпысынан аныктоочтук катышты билдирип, багыныңкы компоненти багындыруучу компонентинин сыпаттык белгисин көрсөтүп турат; «кызылчаны отоо», «чарбада иштөө» дегендер объектилик жана бышыктоочтук катышты билдирип, кыймыл-аракет багытталган затты жана орундук белгини туюндуруп турат.\n" +
                        "Туруктуу сөз айкаштарынын тутумундагы сөздөр өзүнүн лексикалык өз бетинчелигин, тике маанисин толук сактабай, өтмө мааниге өтүп, маани жактан өз ара ширелишип келип, жалпыланган бир бүтүн жаңы маанини туюндурат. Алар билдирген бирдиктүү жаңы маани менен компоненттеринин баштапкы тике маанисинин ортосунда түздөн-түз байланыш жоктой сезилип турат. Мисалы: жүрөгү жок - коркок, өтүмү жок, ит жандуу - чыдамкай, итке минген - абдан жарды, беш өрдөгүн учуруу - калп айтуу, битин сыгып, канын жалаган - сараң, зыкым.\n" +
                        "\n3) Эркин сөз айкаштарынан айырмаланып, туруктуу сөз айкаштарынын компоненттери бири-бири менен өтө тыгыз байланышкан болот. Ошол себептүү алардын тутумундагы сөздөрдүн ордун алмаштырууга, же алардын арасына жаңы сөздөрдү кошуп айтууга болбойт. Мисалы: төбө чачы тик туруу (абдан коркуу), өлбөгөн төрт шыйрагы калуу (абдан катуу уялуу), ат тезегин кургатпай (тынымсыз, тез-тез).\n" +
                        "\n4) Эркин сөз айкаштарынын компоненттери өзүнүн лексикалык маанисин толук сактагандыктан, сүйлөм ичинде алардын ар бири айрым-айрым мүчөлүк милдетти аткарары белгилүү.\n" +
                        "Туруктуу сөз айкаштарында болсо, компоненттери өз ара тыгыз байланышып, ширелишкен бир бүтүн маанини билдиргендиктен, алар бирдиктүү түрдө туруп, сүйлөмдүн бир мүчөсүнүн гана милдетин аткарат. Мисалы: Атасы Жакып - колунан көөрү төгүлгөн уста (Ш.Б.) (аныктооч). Токмоктун көчөлөрүн беш колундай билүүчү (К.Бек.) (сыпат бышыктооч). Алар оту менен кирип, күлү менен чыгышат (А.Т.) («эртеден кечке кыңк этпей иштөө» деген мааниде) (баяндооч). Туруктуу сөз айкаштары көпчүлүк учурда жеке сөздүн эквиваленти катары келет: таш тырмак (сараң), жел өпкө (мактанчаак), жер карама (уяң, жалтаң) ж.б.\n" +
                        "Туруктуу сөз айкаштарын (фразеологизмдерди) тил илиминин бир бөлүгү болгон фразеология илими изилдейт. Ал эми эркин сөз айкаштарынын табиятын синтаксис илими изилдейт.\n" +
                        "\n3. Кыргыз тилинде сүйлөм ичиндеги сөздөрдү өз ара тутумдаштыруучу синтаксистик байланыштын түрлөрүнө ээрчишүү, ыкташуу, башкаруу, таандык байланыш кирет.\n\n" +
                        "Ээрчишүү байланышы. Кыргыз тилинде сүйлөмдөгү сөздөрдүн ээрчишүү байланышы, негизинен, баш мүчөлөрдүн (ээ, баяндооч) бири-бири менен болгон грамматикалык карым катнашында колдонулат. Сүйлөмдү уюштурууда ээ менен баяндооч чечүүчү мааниге ээ, анткени ал экөөнүн семантика-грамматикалык жактан байланышып келишинен сүйлөм түзүлөт да, алар сүйлөмдүн грамматикалык негизи болуп саналышат. Сүйлөмдүн предикативдүүлүк белгисин билдиришет. Ээрчишүүнүн принциби боюнча сүйлөм тутумунда өз ара айкашып турган сөздөрдүн багындыруучу түгөйүнө багыныңкы түгөйү жак, сан боюнча, кээде түр боюнча да окшошуп турууга тийиш.\n" +
                        "Эки тутумдуу сүйлөмдөрдүн тутумунда грамматикалык ээ жана баяндооч дайыма бири-бири менен синтаксистик байланышта болот. Мындай учурда сүйлөм ээси грамматикалык жактан багындыруучу, ал эми баяндооч анын түрдүү белгилерин билдирүүчү мүчө катары багынычтуу түгөй болуп эсептелет. Натыйжада, сүйлөм ээси жак, сан, кээде түр боюнча грамматикалык кандай формада айтылса, баяндооч да ошол эле формада ээге окшошуп, аны ээрчип турууга тийиш. Эми алардын (ээ, баяндоочтун) ортосундагы мыйзамды байкап көрөлү: 1. Сен бул жерге ар качан башкача болуп келчүсүң. (Ч.А.) 2. Биз өзүңөрдөй эле колхозчу элбиз. (Т.С.) 3. Сиз ак ниет, кичипейил адамсыз, ошол үчүн элдин баары сизди жакшы көрүшөт. (К.Ж.)\n" +
                        "1-сүйлөмдө грамматикалык ээси – сен, жекелик санда, экинчи жакта айтылган; баяндоочу – келчүсүң, бул да жекелик санда, экинчи жакта колдонулган. Ушул эле сыяктуу 2-3-сүйлөмдөрдүн баш мүчөлөрү да (биз, сиз, баары – сүйлөм ээлери; элбиз, адамсыз, көрүшөт – баяндоочтор) жак, сан, түр боюнча бири-бирине окшошуп турат. Эгерде жогорудагы мисалдагы мүчөлөр жак боюнча ээрчишпесе, анда сүйлөм түзүлбөс эле. Сүйлөм тутумунда баш мүчөлөр дайыма эле грамматикалык формасы боюнча (жак, сан, түр) бири-бирине толугу менен окшошо бербейт: айрым учурларда сүйлөмдүн конструкциясынын талабына, стилдик нормага ылайык, кээде баш мүчөлөрдүн семантикалык- синтаксистик жагдайына, морфологиялык табиятына жараша толук окшошсо, кээде толук эмес, жарым-жартылай гана (жак боюнча ээрчишсе, сан боюнча ээрчибей калышы мүмкүн, же тескерисинче) окшошуп айтылышы мүмкүн; айрым шарттарда алар жак, сан, түр боюнча да бири-бирине окшошпой, мааниси боюнча гана байланышкан учурлары кездешет.\n" +
                        "Сүйлөмдүн баш мүчөлөрүнүн ээрчишүү байланышы эки тараптуу, башкача айтканда, ал үчүн ээнин да, баяндоочтун да морфологиялык табияты эске алынууга тийиш. Ушул негизде, сүйлөм ээсинин кайсы сөз түркүмүнөн экендиги (зат атооч, ат атоочтор ж.б.)канчалык маанилүү болсо, баяндоочтун түрү да – этиштик, же атоочтук баяндооч экендиги ошончолук маанилүү.\n" +
                        "Кыргыз тилинде сүйлөм ээсинин милдетин 1-2- жактагы ат атоочтор аткарса, баяндооч ээни жак боюнча да, сан боюнча да, түр боюнча да толук ээрчип айтылат. Байкап көрөлү: Мен дүйнөдө үч нерсени жаман көрөм: биринчиси – калп айтуу; экинчиси – кытмырлык; үчүнчүсү – ичи тарлык. (Н.Б.) Акылыбыз, медерибиз өзүңсүң, сен жеткирдиң бизди бактыга. (А.Т.) Сиз турмутун ар кандай тоскоолдуктарын жеңе билиңиз, ошондо гана сиздин кайраттуу экендигиңиз байкалат.(Н.Б.)\n" +
                        "Сүйлөм ээсинин милдетин үчүнчү жактагы ал деген жактама ат атооч аткарса, этиштик баяндоочтор кээде ээни жак боюнча ээрчисе, кээде жак боюнча ээрчибей эле, маани жактан байланыша берет. Бул кырдаалды так жана туура байкоо үчүн алды менен этиштик баяндоочтун чагын аныктоо зарыл: эгерде этиштик баяндооч учур чакта, же келер чакта колдонулса, анда ал сүйлөм ээсин жак боюнча ээрчип айтылат; эгерде этиштик баяндооч өткөн чакта турса, анда сүйлөм ээсин жак боюнча ээрчибейт да, мааниси боюнча гана байланыш түзүп турат. Анткени этиштин өткөн чагынын үчүнчү жакты билдирүүчү жак мүчөсү кыргыз тилинде жок. 1) Ал жакы иштеп жатат. 2) Ал жакшы иштеди. 3) Ал жакшы иштейт.\n" +
                        "1-2-сүйлөмдөрдө баш мүчөлөр жак боюнча да, сан боюнча да бири-бирине окшош. 3-сүйлөмдө болсо алар (баш мүчөлөр) сан боюнча окшошуп турганы менен жак боюнча окшош эмес. 3- сүйлөмдүн өткөн чакта колдонулган этиштик баяндоочунун жак мүчөсү жок. Ал эми 1-2-сүйлөмдөрдүн учур чак, келер чакта турган атоочтор, сын атоочтор, эсептик, иреттик сан атоочтор, шилтеме, сурама, аныктама ат атоочтор, атоочтуктар, чакчылдар, тактоочтор, тууранды сөздөр: темир меш, илимий конференция, отуз пионер, ушул киши, кайсы бала, аткарылган план, күлө сүйлөө, тез келүү, тарс- тарс атылуу ж.б.\n" +
                        "Кээде, көбүнчө ырларда, уйкаштыкты сактоо максатында ыкташуу жолу менен уюшулган сөз айкаштарынын багыныңкы түгөйү инверсияланып, багындыруучу түгөйүнөн кийин келип калат: Ааламда алп сыяктуу туруп бекем, «Азезил» капсалаңдан жалтанбаган. Тамыры барган сайын тарап терең, Таалайга маяк болуп асмандаган (К. Акаев).\n" +
                        "\nСөз айкашынын багындыруучу түгөйү өзүнүн лексико- грамматикалык мааниси-не карай алдындагы багыныңкы компо- нентти башкарып, аны барыш, табыш, жатыш же чыгыш жөндөмөлөрүнүн би-рине келтирип байланышса, башкаруу байланышы деп аталат.\n" +
                        "\nБашкаруу жолу менен зат атоочтон, ат атоочтон, эсептик сан атоочтон жана затташып кеткен башка сөздөрдөн (сын атооч, атоочтук, сан атоочтун айрым түрлөрү ж.б.) турган багыныңкы компоненттер байланышат. Андай багыныңкы компоненттерди көбүнчө этиштен, кээде айрым атооч сөздөрдөн турган багындыруучу компоненттер башкарат: бакка чыгуу, алтыга бөлүү, улууну урматтоо, эч кимге айтпоо, сураганга берүү, таштан оор, малга бай, бизден артта ж.б.\n" +
                        "\nТаандык формадагы багындыруучу сөзгө багыныңкы түгөйү илик жөндөмөдө туруп байланышып, таандык маанини туюндурса, таандык байланыш деп аталат.\n" +
                        "\nТаандык байланыштагы сөз айкаштарынын компоненттери бири-бирине кайчы багынып байланышат: биринчи компоненти илик жөндөмөдө тургандыктан, кийинки компонентине тиешелүү таандык уланды жалганат; экинчи жагынан, ал таандык формадагы экинчи компоненти алдындагы сөздүн илик жөндөмөдө турушун талап кылат. Ушинтип, мындай структурадагы сөз айкаштарынын компоненттерин бири-бири менен илик жөндөмөнүн мүчөсү менен таандык уланды байланыштырат да, мааниси жагынан, негизинен, бир заттын экинчи бирөөгө таандык экендигин билдирет: мектептин багы, үйдүн эшиги, биздин огородубуз ж.б.\n" +
                        "Мындай сөз айкаштарынын тутумундагы илик жөндөмөдөгү сөз - багыныңкы түгөйү, таандык формадагы сөз - багындыруучу түгөйү болот. Таандык байланыштагы сөз айкаштарынын компоненттери атооч сөздөрдөн жана заттык маанидеги сын атоочтон болот: айылдын клубу, сенин пикириң, үчтүн экиси, баланын күлгөнү, жакшынын шарапаты ж.б.");
                break;
            case 1:
                openSecond("Тема: Сүйлөм-пикир алышуунун негизги куралы.","Маселелер:\n" +
                        "1. Сүйлөмгө берилген аныктамалар тууралуу.\n" +
                        "2. Сүйлөмдүн негизги белгилери.\n" +
                        "3. Сүйлөмдүн жалпы классификациясы.\n" +
                        "\n1. Сүйлөм бизге белгилүү болгон тилдин башка бирдиктеринен, тактап айтканда, сөз, сөз айкашынан айырмасы эмнеде? Эң негизги айырмасы – айрым бир ойду билдирип, байланыш- катнаш каражатынын кызматын аткаргандыгында. Сүйлөм – бул пикир алышуунун кичине бирдиги. Адатта, фонетикада, морфологияда, ар бир сөз да – баары тилдин мыйзамдарына ылайык сүйлөмгө биригишип, коммуникативдүү функцияны аткарууга жардамдашат. Башкача айтканда, биз сүйлөмсүз башкалар менен өз ара пикир алыша албайбыз, кандайдыр бир ой-пикирди, ар кандай сезимдерди, чындыкка карата болгон мамилелерди, иш-аракеттерди билдирүүгө мүмкүн эмес. Демек, сүйлөм гана коомдо жашаган адамдарды өз ара байланыштыруу кызматын жүзөгө ашыра алат. Ошентип, жалпы тил илиминде сүйлөм ары татаал, ары көп кырдуу маселе катарында каралат. Ошон үчүн анын критериясы ар кандай көз карашта, ар түрдүү аспектиде: грамматикалык, семантикалык, логикалык, коммуникативдик, предикативдүүлүк, сүйлөмдөгү сөздөрдүн байланышы жактан каралып келе жаткандыгы белгилүү. Мына ушундай кырдаал сүйлөм жөнүндөгү аспектилерди өзүнчө бөлүп кароону талап кылат. Ушуга байланыштуу тил илиминде сүйлөмгө берилген үч жүзгө жакын аныктаманы кезиктирүүгө болот. Бул болсо сүйлөмдүн синтаксистик түзүлүшү канчалык татаал экендигинин далили боло алат. Тил илиминде синтаксисти изилдөө көп кылымдардан бери улантылып келет.\n" +
                        "Ал эми кыргыз тил илиминде синтаксис деп аталган маанилүү маселеге арналган эмгектерди бир нече окумуштуулардын: И.А.Батманов, К.К.Сартбаев, Ы.Жакыпов, А.Жапаров, А.Турсунов, М.Мураталиев, А.Иманов, Б.Тойчубекова, Ж.Жусаев, Б.Өмүралиев сыяктуу илимпоздордун эмгектерин белгилөөгө болот. Кыргыз тилин ар тараптан изилдөө 50-жылдардан баштап кеңири жүргүзүлүп, анын натыйжасында бир катар эмгектер жарык көрүп, кыргыз окумуштууларынын чоң тобу өсүп чыккан. Ы.Жакыпов (1951), К.Дыйканов (1954), А.Жапаров (1955), Ж.Мукамбаев (1955), Д.Майрыков (1956), А.Иманов (1990). Кыргыз тил илиминдеги мындай жетишкендиктер жогорку окуу жайлары үчүн окуу куралдарын түзүүгө, сүйлөм жана анын классификацияланышындагы чаташкан маселелерди туура чечүүгө жардам берген. Мисалга алсак, 1958-жылы Ы.Жакыповдун «Азыркы кыргыз тилинин жөнөкөй сүйлөмүнүн синтаксиси» деген окуу куралы (жогору окуу жайлар үчүн) басмадан чыгат. Автор кыргыз тилиндеги фактыларга таянуу аркылуу, мүмкүн болушунча, жөнөкөй сүйлөмдүн синтаксиси жөнүндөгү маселелерди кенен-кесир чечүүгө аракет жасаган. Анда сүйлөмдүн негизги критериялары туура чечилип, сүйлөмгө мындайча аныктама берген: «Сүйлөм... белгилүү бир ойду түшүндүрөт». Мындай аныктама көпчүлүк көрүнүктүү орус лингвистеринин (В.В.Виноградов, А.Т.Руднев ж.б.) эмгектеринде да чагылдырылган. Окуу китебинде сүйлөмдүн негизги белгилери катарында мына булар көрсөтүлөт: «Сүйлөмдү аныктоодогу башкы критериянын бири – анын бүткөн ойду билдириши. Экинчи критерия баш мүчө болуп саналат, алар аркылуу тыянактуу ой түшүндүрүлөт. Үчүнчү негизги критерия интонация болот. Чынында эле, ансыз сүйлөм түзүлбөйт. Төртүнчү критерия сүйлөмдө ой жүргүзүү маселеси. Ал эми акырында сүйлөм деген түшүнүк айрым кезде өзүнүн алды же артындагы башка бир катар сүйлөмдөр менен байланыштуулукта чечилет».\n" +
                        "Жалпы тил илиминдеги сүйлөмгө берилип келаткан аныктамаларды эске алсак, анда жогорку критерийлердин ичинен биринчи үчөө туура көрсөтүлгөн. Китепте грамматикалык түзүлүшүнө карай жөнөкөй сүйлөмдүн төмөнкүдөй түрлөрү жөнүндө түшүнүк берилген: толук, кемтик, жалаң, жайылма, атама, жактуу сүйлөм, ээсиз сүйлөм, жагы ачык-так болбогон ээсиз сүйлөм, жалпыланган жактуу сүйлөм – баардыгы тогуз. Аталган окуу китеп 1975-жылы кайрадан басылып чыгып, анда сүйлөмгө берилген мурдагы критерийлер көрсөтүлгөн эмес, жөнөкөй сүйлөмдүн түрлөрү дагы бир жолу такталып, анын алты түрү көрсөтүлгөн: жалаң, жайылма, толук, кемтик, атама, ээсиз.\n" +
                        "Кыргыз тилинин синтаксиси боюнча илимий жактан иштелип чыккан окуу куралдарынын негизинде сүйлөмдүн табияты, анын классификацияланышы андан ары дагы такталып, калыптана баштады. Муну биз профессор А.Жапаровдун «Кыргыз тилинин синтаксиси» (жогорку окуу жайлары үчүн, 1979) деген окуу китебинен байкайбыз. Сүйлөмгө аныктама берүүдө мурдатан берилип келе жаткан жана калыптанып адатка айланган аныктаманы колдойт: «Айрым бир бүткөн ойду билдирүү, өз ара пикир алышуу иштери сүйлөмдөр аркылуу жүзөгө ашырылат».\n" +
                        "Демек, пикир алышуу - турмушта ар дайым болуп тура турган зарыл нерсе. Коомдун бардык мүчөлөрүн бири-бири менен байланыштыруучу, пикир алмаштыруучу курал болуп эсептелген тил катнашуунун негизги формасы катары дайыма сүйлөмдү пайдаланат.\n" +
                        "\nСүйлөм тилдик закон боюнча грамматикалык жагынан уюшулган кептин бир бүтүн бирдиги жана адамдын оюн уюштуруучу, ал жөнүндө башкаларга кабар берүүчү негизги каражат болуп эсептелет.\n" +
                        "\nСүйлөм дайыма сөздөрдүн жана сөз айкаштарынын ошол тилдин грамматикалык эрежелери жана закондору боюнча бири-бири менен өз ара байланышып келиши аркылуу уюшулат. Сүйлөм ар кандай эле сөздөрдүн кокустан болгон жыйындысынан уюшула бербейт. Сүйлөм түзүүдө, адатта, айтылуучу ойдун мазмунуна, максатына ылайыкталып, мааниси боюнча бири-бири менен айкаша ала турган сөздөр тандалып алынат да, алар өз ара грамматикалык жактан байланышып келип, натыйжада, бир бүтүн сүйлөм уюшулат.\n" +
                        "\n2. Жогоруда биз кыргыз тилинин синтаксистик түзүлүшү боюнча жарыкка чыккан айрым окуу куралдарында сүйлөмдүн аныктамасы жана негизги белгилери кандай даражада каралып келгендиги жөнүндө жалпы сөз болду. Эми болсо сүйлөмдүн негизги белгилеринин ар бирине токтололу.\n" +
                        "\nЭгерде жалпы тил илиминде, анын ичинде кыргыз тилинде баяндалган илимий материалдарды жалпыласак, анда сүйлөмдүн негизги белгилери деп төмөнкүлөрдү эсептөөгө болот: коммуникативдүүлүк, предикативдүүлүк, модалдуулук жана интонация. Ушулардын өз ара тыгыз байланышып келишинин натыйжасында гана сүйлөм уюшулуп, тил өзүнүн пикир алышуу функциясын жүзөгө ашырат.\n" +
                        "\nКоммуникативдүүлүк – сүйлөмдүн негизги белгиси. Тил пикир алышуунун куралы, бул абдан туура. Себеби, тилдин күндөлүк турмушта, тиешелүү чөйрөдө, кандайдыр бир максатта колдонгон учурда гана ал пикир алышуунун, өз ара байланыш-катнаш жасоонун каражаты боло алат. Демек, тилди турмушта колдонуу деген эмне? Бул ой-пикирди, ар кандай сезимдерди, дүйнө тааным билимдерди ж.б. оозеки же жазуу түрүндө башкаларга билдирүү жана алар менен пикир алышуу, байланыш жасоо. Бул ити жүзөгө аырууда сүйлөм колдонулат да, андан текст түзүлүп, байланыштуу кеп пайда болот. Чынында, кеп өзүнөн-өзү пайда боло бербейт, бул үчүн тиешелүү арттын, кырдаалдын жана себептин болушу зарыл. Уундай учурда гана тиешелүү сүйлөмдүн, кептин пайда болушу талап кылынат да, тил өзүнүн коммуникативдүү, башкача айтканда, адамдар менен байланы-катыш жасоо кызматын аткарат. Ошентип, сүйлөм – өз ара пикир алышууну камсыз кылууга жөндөмдүү болгон синтаксистин негизги маселеси. Ошондуктан орто мектептен баштап жогорку окуу жайланыа чейинки окуу китептеринин баарында сүйлөмгө төмөнкүдөй бир түрдүү аныктама берилип келе жатат: «Бүткөн бир айрым ойду билдирген сөз же сөздөрдүн тизмеги сүйлөм деп аталат» Бул аныктама адатка айланым, калыптанып калды.\n" +
                        "Пикир алышуу – грамматикалык жана психологиялык татаал процесс, анын өзүнө тиешелүү мыйзамдары бар. Коммуникативдүүлүк категориясынын мааниси – башка адамдарга бир нерсе жөнүндө кабар берүүгө ниеттенүү, ал эми кабар берүү үчүн сүйлөм түзүү талап кылынат. Демек, сүйлөм ойду билдирүү менен байланыш жасоо милдетин аткарат, бул биринчиден. Экинчиден, коммуникация (communicatio) латын сөзү, кыргызча мааниси – байланыш-катнаш, кабар берүү, билдирүү. Мына ушундан улам, тил коммуникативдүүлүк функцияны аткарат, бакача айтканда, тил –байланыш-катнаш жасоонун каражаты дейбиз. Ушул айтылганды профессор А.Жапаровдун аныктамасы менен бекемдөөгө болот: «Пикир алышуу адамзат коомунда туруктуу зарылчылык болуп саналат. Тил өзүнүн бул маанилүү функциясын сүйлөм аркылуу жүзөгө ашырат. Ошентип, сүйлөм бүткөн ойду билдирип, катнаш куралы катары кызмат кылат»1 Демек, сүйлөмдүн ушул кызматы анын тилдин башка системаларынан айырмалап турат. Жогоруда айтылгандарды эске алсак, анда сүйлөмдүн белгилеринин бири – жыйынтыктуу ойду билдирүү. Ошондуктан сүйлөмдүн коммуникативдүүлүк деген белгиси кыргыз тилинде эң керектүү мааниге ээ.\n" +
                        "Сүйлөмдүн белгилеринин дагы бирөө – предикативдүүлүк. Бул ээ менен баяндоочтун ортосундагы байланыш. Жалпы эле алганда, сүйлөмдү түзүп турган сөздөр бири-бири менен маанилик жана грамматикалык жактан байланыып турат, себеби сөздөрдүн кайдыгер эле тизмеги сүйлөмдү уюштура албайт. Байкап көрөлү: айыл, кечээ, келүү, мен – бул сүйлөм эмес, анткени сөздөрдүн ортосунда байланыш жок. Сөздөр тилдин ички мыйзамдары боюнча байланышып келгенде гана сүйлөм түзүлөт да, белгилүү бир ой билдирилет: Мен кечээ айылдан келдим. Сөздөр өз ара ички маанилик жактан байланышканда алардын бирөө экинчи сөздүн маанисин толуктап, тиешелүү суроого жооп берет да, синтаксистик бир кызмат аткарат. Ушуга байланыштуу сүйлөмдүн курамындагы сөздөр өздөрүнүн семантикалык- синтаксистик кызматы боюнча баш (ээ жана баяндооч) жана\n айкындооч (аныктооч, толуктооч, бышыктооч) мүчөлөр деп аталышат. Сөздөрдүн ортосундагы ушундай катыштар ыкташуу, башкаруу, таандык, ээрчишүү сыяктуу синтаксистик байланыштын түрлөрү аркылуу жүзөгө ашырылат.\n" +
                        "Предикативдүүлүктү уюуштурууда чак жана жак категорияларынын мааниси чоң. Сүйлөм аркылуу билдирип жаткан окуя, кыймыл-аракет, иш-аракеттердин баары белгилүү бир убакыт, мезгил жана үч жактын бирөө менен байланыштуу болот, ансыз белгилүү бир ойдун уюштурулушу мүмкүн эмес. Мисалы: Биз шаардан келдик. Биз – ээ, биринчи жакта, көптүк санда; келдик – баяндооч, биринчи жакта, көптүк санда жана өткөн чакта (кел+ди+к). Эгерде этиштик баяндоочко уланган өткөн чак (-ды) менен биринчи жактын (-к) мүчөсүн алып таштасак, түүнүксүз болуп, сүйлөм түзүлбөй калар эле: Биз шаардан кел... Оентип, сүйлөмдүн предикативдүүлүк деген белгисин уюштурууда жак жана чак категориялары колдонулат.\n" +
                        "Эгерде баш мүчөлөрдүн экөө тең катышпаса, анда жалаң\n" +
                        "айкындооч мүчөлөрдөн сүйлөм түзүлбөйт. Мисалы: Адамды сүйө\n" +
                        "албаган адам ата-энени да урматтай албайт. – адамды сүйө албаган...\n" +
                        "ата-энени да... Демек, «предикативдүүлүк – сүйлөмдүн эң негизги\n" +
                        "белгиси, ал – ээ менен баяндоочтун ортосундагы синтаксистик\n" +
                        "1\n" +
                        "катыш». Ошентип, сүйлөмдү сүйлөм кылып турган анын\n" +
                        "предикативдүүлүк белгиси.\n" +
                        "Дагы бир негизги белгилердин бири – модалдуулук.\n" +
                        "Сүйлөөчү адам ар кандай түзүлүштөгү сүйлөмдү колдонуп, айтылып жаткан ойго карата өзүнүн ар түрдүү мамилесин билдириши мүмкүн: окуянын накта чындык экендиги же чындык эместиги, кыймыл- аракеттин жүзөгө ашары же ашпай тургандыгы, же анын иш жүзүнө ашарына ишенүү, үмүттөнүү, божомолдоо ж.б. Уунун баары сүйлөмдүн модалдуулук белгисин билдирет. Мисалы: 1. Мен туулуп өскөн айылыма барам. Мында кыймыл-аракеттин чындык экендиги этиштин келер чагы аркылуу билдирилди. 2. Албетте, мен туулуп өскөн айылыма барам. Айтылып жаткан ойду бекемдөө. 3. Балким, мен туулуп өскөн айылыма барам. Айтылып жаткан ойго бүдөмүк, так эмес маани берди. 4. Мен туулуп өскөн айылыма баруум керек. Жоопкерчилик, милдеттүүлүк маанисин берди. 5. Мен туулуп өсөкөн айылыма барсам экен. Каалоо, ниеттенүү маанисин берди. Демек, сүйлөмдүн модалдуулук белгиси дегенде айтылып жаткан ойго карата сүйлөөчүнүн мамилесин билүү керек. Кыргыз тилиндеги модалдуулук категориясы жөнүндөгү\n" +
                        "маселе профессор Ж.Жусаевдин «Азыркы кыргыз тилиндеги\n" +
                        "модалдуулук категориясын билдирүүчү грамматикалык\n" +
                        "каражаттар»(2000) деген эмгегинде абдан терең изилденген. Автор\n" +
                        "модалдуулук категория сүйлөмдүн белгилеринин бири болорун\n" +
                        "теориялык жактан ишенимдүү далилдеп, төмөнкүдөй жыйынтыкка\n" +
                        "келет. Сүйлөмдүн негизги жана грамматикалык белгилери болуп\n" +
                        "төмөнкү категориялар эсептелет: коммуникативдүүлүк, модалдуулук,\n" +
                        "предикативдүүлүк жана интонация. Практикада сүйлөмдүн аталган\n" +
                        "белгилери өз ара тыгыз байланышта колдонулат да сүйлөм деген\n" +
                        "түшүнүккө кызмат кылышып, аны уюштурушат. Бул категориялардын\n" +
                        "ар биринин өзүнө тиешелүү өзгөчөлүгү бар.Алсак,\n" +
                        "коммуникативдүүлүк пикир алышуу үчүн кырдаал, шарт түзөт;\n" +
                        "модалдуулук сүйлөмдүн семантикалык мазмунун камсыз кылат;\n" +
                        "предикативдүүлүк сүйлөмдүн грамматикалык түзүлүшүн камсыз\n" +
                        "кылат; интонация – сүйлөмдү уюштурат. Акырында келип сүйлөм\n" +
                        "1\n" +
                        "А.Иманов «Кыргыз тили» деген эмгегинде модалдуулукту предикативдүүлүк менен бирдикте караган: «Предикативдүүлүктүн грамматикалык жалпы мааниси модалдуулук, ал синтаксистик чак, жак аркылуу туюндурулат... Ошентип, модалдуулук жана синтаксистик чак деген грамматикалык комплекс предикативдүүлүктү түзөт»2\n" +
                        "Көпчүлүк окумуштуулардын пикири боюнча сүйлөмдүн дагы бир белгиси – интонация, ал – сүйлөмдү уюштуруучу, анын модалдык, эмоционалдык-экспрессивдик жагын көрсөтүүчү каражат жана кептин ритмикасы. Интонациянын жардамы менен сүйлөмдүн коммуникативдүү белгиси жүзөгө ашырылат. Интонация латын сөзү (intonare – сүйлөө, көтөрүңкү сүйлөө), ал оозеки речке мүнөздүү, анын жардамы менен тиешелүү ойду жана ар түрдүү эмоцияны, сезимдерди билдирүүгө болот, т.а. ыраазы болгондукту, нааразычылыкты, кубангандыкты, өкүнүүнү, какыктоону, мыскылдоону ж.б.\n" +
                        "Профессор Ж.Жусаев интонация тилдик бай көрүнүш экендигин жана ал сүйлөмгө ар кандай маанилик түс берерин аныктап келип, «Ал – студент.» деген сүйлөм өзүнүн грамматикалык\n" +
                        "1 Жусаев Ж. Азыркы кыргыз тилиндеги модалдуулук категориясын билдирүүчү грамматикалык каражаттар.–Бишкек, 2000. –76-бет, -216- беттер.\n" +
                        "2 Иманов А. Кыргыз тили. – Фрунзе, 1990. –69-71-беттер.\n" +
                        "пайда болот».\n" +
                        "Сүйлөмдүн модалдуулук белгиси жөнүндө профессор" +
                        "түзүлүшүн өзгөртпөстөн туруп эле, интонациянын жардамы аркылуу модалдык ар кандай маанилерди билдирүүгө жөндөмдүү экендигин көрсөтөт, мисалы: аныктоо, көңүл кош болуу, таң калуу, жактырбоо,\n" +
                        "1\n" +
                        "көралбастык, кубануу, канааттануу, кызыгуу, ишенбөө ж.б. Ошентип, интонация – сүйлөмдү уюуштуруу менен бирге анын айтылып бүткөндүгүн да көрсөтүүчү каражат катары эсептелет, себеби оозеки речте ар бир сүйлөмдөн кийин тыным болот да, жазууда ал жерге тиешелүү тыныш белгиси коюлат. Жарыкка чыккан эмгектерде жана окуу куралдарында интонациянын компоненттери катары төмөнкүлөр көрсөтүлүүдө: а) мелодика – үн ыргагынын көтөрүңкү, басыңкы кыймылы; б) басым – сүйлөмдө кээ бир сөздөр ар кандай деңгээлдеги басым менен айтылат; в) кептин темпи – үндүн ылдамдыгы же акырындашы; г) пауза – сүйлөө учурундагы тынымдар; д) интенсивдүүлүк – айрым тыбыштардын, муундардын күчү; ж) кептин тембри – сүйлөөдөгү кыймыл-аракет.\n" +
                        "Жогорудагы айтылгандардын баарын жыйынтыктасак, мына буларды белгилөөгө болот:\n" +
                        "- Синтаксистин эң башкы проблемасы сүйлөм, себеби ал адамдардын өз ара пикир алышуу кызматын тейлейт; өзүнүн көп аспектүүлүгү жана мыйзамдуулугу менен тилдин башка бөлүмдөрүнөн кескин түрдө айырмаланып турат;\n" +
                        "- Сүйлөмдүн негизги жана милдеттүү грамматикалык белгилери болуп төмөнкүлөр эсептелет: коммуникативдүүлүк, предикативдүүлүк, модалдуулук, интонация; булардын ар бири практикада өзүнчө жеке колдонулбастан, тескерисинче өз ара ажырагыс түрдө байланышып келип, сүйлөмдү уюштурат.\n" +
                        "\n\n3. Сүйлөмдүн классификацияланышына токтоло турган болсок, жалпы тил илиминде сүйлөмдү айрым топторго бөлүштүрүүдө семантикалык жана грамматикалык принциптер колдонулат.\n" +
                        "Сүйлөмдүн мааниси, анын түзүлүшү өтө татаал, көп кырдуу маселе. Кыргыз тилинде сүйлөмдүн ар кандай түрлөрү колдонулат. Алар айтылыш максатына жана түзүлүш өзгөчөлүктөрүнө (структурасына) карай бир нече топко бөлүнөт:\n" +
                        "\n1. Сүйлөмдөр айтылыш максатына карай жай, суроолуу, буйрук сүйлөмдөр болуп, ар түрдүү ички сезимди билдиришине (эмоционалдык маанисине) карай илептүү сүйлөм болуп бөлүнөт.\n" +
                        "\n2. Сүйлөмдүн бир же бир нече жөнөкөй сүйлөмдөн уюшулгандыгына карай жөнөкөй сүйлөм жана татаал сүйлөм болуп эки топко бөлүнөт.\n" +
                        "\n3. Жөнөкөй сүйлөмдөр сүйлөмдү уюштуруучу борбор болуп эсептелген баш мүчөлөрдүн (ээ, баяндоочунун) бирөөнүн же экөөнүн тең бар экендигине карай эки тутумдуу жана бир тутумдуу сүйлөмдөргө бөлүнөт.\n" +
                        "\n4. Сүйлөмдүн баш мүчөлөрү менен байланышкан айкындооч мүчөлөрүнүн бар же жок экендигине негизделип, жалаң жана жайылма сүйлөмгө ажыратылат.\n" +
                        "\n5. Сүйлөм үчүн зарыл керектүү болгон мүчөлөрүнүн толук катышышы, же контекстке, ар түрдүү жагдайга карай айрым сөздөрдүн (сүйлөм мүчөсүнүн) айтылбай, кемип берилишине карай толук сүйлөм жана кемтик сүйлөм деп бөлүнөт.");
                break;
            case 2:
                openSecond("Тема: Жөнөкөй сүйлөмдүн синтаксиси","Маселелер:\n" +
                        "1. Жөнөкөй сүйлөм жөнүндө жалпы түшүнүк.\n" +
                        "2. Эки тутумдуу жөнөкөй сүйлөмдөр.\n" +
                        "3. Бир тутумдуу жөнөкөй сүйлөмдөр.\n" +
                        "\n1. Жөнөкөй сүйлөмдүн синтаксиси. Сүйлөм адамдардын ортосунда өз ара пикир алышуу кызматын аткарат. Ал аркылуу бир нерсе жөнүндө кабар берилет, же суроолор менен бирөөгө кайрылабыз, же ички сезимдер билдирилет.\n" +
                        "Сүйлөмдүн айтылыш максаты жана грамматикалык түзүлүшү ар түрдүү. Алсак, айтылыш максаты боюнча жай, суроолуу, илептүү, буйрук сүйлөмдөргө бөлүнөт.\n" +
                        "Грамматикалык түзүлүшү боюнча сүйлөмдөр экиге бөлүнөт. 1) Жөнөкөй сүйлөмдөр; 2) Татаал сүйлөмдөр.\n" +
                        "Жөнөкөй сүйлөмдөр бир гана сүйлөмдөн турат.\n" +
                        "Жөнөкөй сүйлөм деп – бир гана сүйлөмдөн, же болбосо бир гана грамматикалык негизге ээ болгон сүйлөмдөрдү айтабыз.\n" +
                        "Жөнөкөй сүйлөмдө бир эле грамматикалык негиз (баш мүчө) болот, ал эми татаал сүйлөм эки же андан көп грамматикалык негиздерден турат. Мисалы: 1. Күн ачылат. 2. Күн жылып, кар эрийт, жер жылыйт. 1-сүйлөм – жөнөкөй, мында бир эле баш мүчө бар. 2- сүйлөм – татаал, ал үч сүйлөмдөн түзүлдү, анда үч баш мүчө бар.\n" +
                        "Жөнөкөй сүйлөмдөрдүн грамматикалык түзүлүшүнүн эң зарыл негизин баш мүчөлөр – ээ жана баяндооч түзөт. Мына ушул баш мүчөлөрдүн сүйлөмдө толук же толук эмес катышкандыгына байланыштуу жөнөкөй сүйлөмдөр экиге бөлүнөт:\n" +
                        "1. Эки тутумдуу сүйлөмдөр.\n" +
                        "2. Бир тутумдуу сүйлөмдөр.\n" +
                        "\n\n\n2.Тутумунда эки баш мүчөсү тең (ээ, баяндоочу) бар сүйлөм эки тутумдуу сүйлөм деп аталат. Эки тутумдуу сүйлөмдөр тилде кеңири колдонулат. Мисалы: Той бүттү. Журт тарады (К.Б.). Асия уктай албай жатты (Ч.А.). Түн ортосу ооп кетти (Ч.А.).\n" +
                        "Көпчүлүк учурларда бул сыяктуу ээ менен баяндоочтун гана айкашынан түзүлгөн жөнөкөй сүйлөмдөрдүн ар бир мүчөсүнө тиешелүү айкындооч мүчөлөрү да болот: Амангелдинин чабандык иши Тоң районунда даңазаланып келатат. Кыргызстандын жумушчу табы жылдык планды ашыра орундатуу үчүн бардык күчүн жумшоодо. Эки тутумдуу сүйлөмдөрдүн баш мүчөлөрү сүйлөм ичинде дайым эле толук катыша бербейт. Кээде стилдик талапка жараша жана контекстке карай сүйлөмдүн ээси айтылбай да калат. Бирок мындай кырдаал ал сүйлөмдүн түзүлүшүнө (структурасына), мазмунуна таасир этпейт. Тактап айтканда, ал эки тутумдуу сүйлөм боюнча эле калат. Анткени андай структурадагы сүйлөмдүн ээси кайсы экендиги баяндоочунун аягындагы жак мүчөдөн же андан мурун айтылган сүйлөмдөрдүн мазмунунан ачык билинип турат. Мисалы: Ал шордууну кечээ жөнөттүм (Т.С.). Лагерге эртең саат ондо жөнөйсүңөр. Сөзмөр киши экен, Торгой чал. Түнү менен билген акылын айтып чыкты (Ч.А.). Мында акыркы сүйлөмдүн ээси экендиги өзүнөн мурунку сүйлөм аркылуу белгилүү болуп турат.\n" +
                        "\n\nЖалаң жана жайылма сүйлөм.\n" +
                        "Жөнөкөй сүйлөмдөр тутумунда айкындооч мүчөлөрдүн бар экендигине же жок экендигине карай жалаң сүйлөм жана жайылма сүйлөм болуп бөлүнөт.\n" +
                        "\nБаш мүчөлөрдөн гана, башкача айтканда, ээ менен баяндоочтон гана түзүлгөн сүйлөмдөр жалаң сүйлөм деп аталат.\n Мисалы: Кыш да түшүп келе жатат (К.Б.). Биз даярданып калдык (К.Б.). Эч ким жок (Ч.А.). Ажар чочуп кетти. Жүрөгү туйлай баштады (К.Б.). Бул сүйлөмдөр жалаң ээ менен баяндоочтун катышы менен гана түзүлдү.\n" +
                        "\nБаш мүчөлөр менен айкындооч мүчөлөрдүн байланышынан түзүлгөн сүйлөмдөр жайылма сүйлөм деп аталат.\n\n Мисалы: Амангелди былтыр ирилештирилген чабандык бригада түзгөн. Көпчүлүк райондордо чарбалык ички эсеп түзүк өздөштүрүлгөн. «Красная заря» чарбасынын чабандары мелдештин жыйынтыгын чыгарды. Түндүн биринчи жарымы күндөгүдөй капкараңгы (Ч.А.). Бул сүйлөмдөрдө баш мүчөлөрдөн тышкары айкындооч мүчөлөрү да катышты.\n\n" +
                        "\n3.Бир тутумдуу сүйлөмдөр. Бир тутумдуу сүйлөмдө баш мүчөлөрдөн бирөө эле катышат, тактап айтканда, же ээ же баяндооч. Мисалы: 1.Жер шаары. 2.Түнүчүндө учуп баратабыз.\n" +
                        "1-сүйлөмдө заттын аты жөнүндө кабар берилди, бирок баш мүчөлөрү белгисиз. 2-де баш мүчөлөрдөн баяндооч гана катышты (Эмне кылып баратабыз? – учуп баратабыз – баяндооч. Качан? – түнүчүндө – бышыктооч), ээ катышкан жок.\n" +
                        "Эки тутумдуу сүйлөмдөрдүн баш мүчөлөрү сүйлөм ичинде дайым эле толук катыша бербейт. Кээде стилдик талапка жараша жана контекстке карай сүйлөмдүн ээси айтылбай да калат. Бирок мындай кырдаал ал сүйлөмдүн түзүлүшүнө (структурасына), мазмунуна таасир этпейт. Тактап айтканда, ал эки тутумдуу сүйлөм боюнча эле калат. Анткени андай структурадагы сүйлөмдүн ээси кайсы экендиги баяндоочунун аягындагы жак мүчөдөн же андан мурун айтылган сүйлөмдөрдүн мазмунунан ачык билинип турат. Мисалы: Ал шордууну кечээ жөнөттүм (Т.С.). Лагерге эртең саат ондо жөнөйсүңөр. Сөзмөр киши экен, Торгой чал. Түнү менен билген акылын айтып чыкты (Ч.А.). Мында акыркы сүйлөмдүн ээси экендиги өзүнөн мурунку сүйлөм аркылуу белгилүү болуп турат.\n" +
                        "Жалаң жана жайылма сүйлөм.\n" +
                        "Жөнөкөй сүйлөмдөр тутумунда айкындооч мүчөлөрдүн бар экендигине же жок экендигине карай жалаң сүйлөм жана жайылма сүйлөм болуп бөлүнөт.\n" +
                        "Баш мүчөлөрдөн гана, башкача айтканда, ээ менен баяндоочтон гана түзүлгөн сүйлөмдөр жалаң сүйлөм деп аталат. Мисалы: Кыш да түшүп келе жатат (К.Б.). Биз даярданып калдык (К.Б.). Эч ким жок (Ч.А.). Ажар чочуп кетти. Жүрөгү туйлай баштады (К.Б.). Бул сүйлөмдөр жалаң ээ менен баяндоочтун катышы менен гана түзүлдү.\n" +
                        "Баш мүчөлөр менен айкындооч мүчөлөрдүн байланышынан түзүлгөн сүйлөмдөр жайылма сүйлөм деп аталат. Мисалы: Амангелди былтыр ирилештирилген чабандык бригада түзгөн. Көпчүлүк райондордо чарбалык ички эсеп түзүк өздөштүрүлгөн. «Красная заря» чарбасынын чабандары мелдештин жыйынтыгын чыгарды. Түндүн биринчи жарымы күндөгүдөй капкараңгы (Ч.А.). Бул сүйлөмдөрдө баш мүчөлөрдөн тышкары айкындооч мүчөлөрү да катышты.\n" +
                        "\n\nБир гана баш мүчөнүн негизинде уюшулуп, бүткөн бир ойду туюндурган, экинчи баш мүчөнүн болушун талап кылбаган сүйлөм бир тутумдуу сүйлөм деп аталат. Бир тутумдуу сүйлөмдөрдүн бардык түрлөрү үчүн мүнөздүү болгон жалпы структуралык өзгөчөлүк, жогоруда көрсөтүлгөндөй, аларда грамматикалык ээ болбойт жана анын сүйлөмдө катышышынын зарылчылыгы да жок болот. Жөнөкөй сүйлөмдүн бул түрү бир гана баш мүчөнүн (көбүнчө баяндоочтун) базасында уюшулуп, кандайдыр бир кыймыл-аракет, көрүнүш, бир нерсенин абалы, белгиси тууралуу кабар берет. Бирок бир тутумдуу сүйлөмдөрдүн семантикасы (мааниси), тутумундагы сөздөрдүн синтаксиситик жактан жиктелүү мүмкүнчүлүгү, баш мүчөсүнүн (баяндоочтун) уюшулуу жолу, кыймыл-аракетти аткаруучу жактын мүнөзү ар түрдүү болот. Мисалы, кээ бир сүйлөмдөр кыймыл-аракет белгисиз бирөө тарабынан аткарылгандыгын билдирет, экинчисинде кыймыл-аракетти аткаруучу субъект жалпыланган мүнөздө болуп, үч жакты тең ичине камтыйт, айрымдарында аракет кылуучу жак таптакыр эле белгисиз болот ж.б. Ошондуктан кыргыз тилиндеги бир тутумдуу сүйлөмдөр семантикалык жана грамматикалык өзгөчөлүктөрүнө карай белгилүү жактуу сүйлөм, белгисиз жактуу сүйлөм, жалпылама жактуу сүйлөм, жаксыз сүйлөм, атама сүйлөм, кемтик сүйлөм, баяндоочу кыскарылган сүйлөм болуп бөлүнөт.\n" +
                        "\n\n\nБелгилүү жактуу сүйлөм.\n\n" +
                        "Бир тутумдуу сүйлөмдүн бул түрү да, башка түрлөрү сыяктуу эле, сүйлөмдүн баяндооч мүчөсүнүн тобунан уюшулат; сүйлөм тизмегинде грамматикалык ээси катышпайт жана аны катыштырып айтууга мүмкүн да болбой калган. Ушуну менен бирге, белгилүү жактуу сүйлөм бир тутумдуу сүйлөмдүн башка түрлөрүнөн айрым белгилери боюнча айырмаланып да турат. Мисалы, белгисиз жактуу сүйлөмдө кыймыл-аракеттин белгисиз, кимдир бирөө тарабынан аткарылгандыгы сезилип турса, жалпылама жактуу сүйлөмдө кыймыл- аракетти аткаруучу жак жалпыланган мааниде болуп, үч жакты тең ичине камтыйт, жаксыз сүйлөмдө аракет кылуучу жак такыр эле белгисиз болот. Белгилүү жактуу сүйлөмдөрдө да кыймыл-аракетти аткаруучу жак, б.а., грамматикалык ээси катышпайт. Анткени менен ээнин кайсы сөз экендиги сүйлөмдүн баяндоочунун аягындагы жак мүчө аркылуу белгилүү болуп турат. Бирок, ошондой болсо да, андай түзүлүштөгү сүйлөмдөргө тиешелүү ээсин катыштырып айтуунун зарылчылыгы жок болуп калган жана аны катыштырып айтууга мүмкүн да болбой калган. Анткени мындай структурадагы сүйлөмдөрдө грамматикалык ээнин катыштырылбай айтылышынын өзү белгилүү бир нормага айланып, калыпташып кеткен. Эгерде мындай сүйлөмдөрдө ээсин күч менен сүйлөм ичине кошуп айтсак; анда сүйлөм стилдик жагынан жана түзүлүшү жагынан айрым өзгөрүүлөргө дуушар болот. Мисалы: Малдын башын урдум! Дүйнөнү сууга салдым, журт! (Т.С.). Маани жагынан өз ара байланыштуу келген эки сүйлөмдө тең грамматикалык ээси катыштырылбай айтылган, бирок баяндоочунун аягындагы биринчи жактын -м мүчөсү аркылуу аракет кылуучу жак I жакты билдирүүчү «мен» деген жактама ат атооч экендиги белгилүү болуп турат.\n" +
                        "\n\nЭэси катышпай, баяндоочтун тобунан гана түзүлгөн, бирок ээнин кайсы экендиги баяндоочунун аягындагы жак мүчөдөн белгилүү болуп турса да, анын сүйлөм ичинде катыштырылып айтылышы мүмкүн бол-бой калган бир тутумдуу сүйлөмдөр белгилүү жактуу сүйлөмдөр деп аталат.\n" +
                        "\n\n\nБелгисиз жактуу сүйлөм.\n\n" +
                        "Бир тутумдуу сүйлөмдүн айрым бир түрлөрүндө кыймыл- аракетти аткаруучу грамматикалык ээси сүйлөмдүн мазмунунан ачык эмес, бүдөмүк элестелип турат да, сүйлөмдүн структуралык негизин III жактагы этиштик баяндоочу жана аны менен байланышып келген айкындооч мүчөлөрү (баяндоочтун тобу) түзөт. Мисалы: Жаңы тигилген көчөттү сындырып кетиптир. Биздин чарбада аны алдыңкы саанчы катары билишет («СК»). Бул сүйлөмдөр III жактагы этиштик баяндоочтун жана ал баяндооч менен байланышып айтылган айкындооч мүчөлөрдүн (баяндоочтун тобунун) катышуусу менен уюшулган. Бул эки сүйлөмдө тең баяндооч билдирген кыймыл- аракетти аткаруучу грамматикалык ээ (субъект) жок, ал ачык көрсөтүлгөн эмес жана аны конкреттүү көрсөтүүнүн зарылчылыгы да жок. Жогорку сүйлөмдөрдүн баяндоочторунун III жакта тургандыгына карап, алардын грамматикалык ээси III жак маанисиндеги ат атооч болот деп кесе айтууга да болбойт. Анткени бул сүйлөмдөрдө кыймыл-аракетти аткаруучу жак ачык, конкреттүү көрсөтүлгөн эмес. Ошол себептүү, мисалы, «Жаңы тигилген көчөттү сындырып кетиптир» деген сүйлөмдүн грамматикалык ээси ал деген ат атоочту, же болбосо балдар, мал, Асан ж.б. деген өңдүү зат атоочтордун бири болобу - ал биз үчүн белгисиз көрүнүш. Ошондой эле «Биздин чарбада аны алдыңкы саанчы катары билишет» деген сүйлөмдө да ээнин милдетин «жумушчулар» же «эл», же болбосо, «чарба жетекчилери» деген сөздөрдүн бири аткарат деп ачык айтууга болбойт. Мындай структурадагы сүйлөмдөрдө, адатта грамматикалык ээси белгисиз бирөө экендиги оюбузда бүдөмүк түрдө элестетилип турат.\n" +
                        "\n\nГрамматикалык ээси катышпаган жана баяндоочу билдирген кыймыл-аракетти аткаруучу жак белгисиз бирөө экендиги оюбузда бүдөмүк элестетилип турган сүйлөм белгисиз жактуу сүйлөм деп аталат.\n" +
                        "\n\n\nЖалпылама жактуу сүйлөм.\n\n" +
                        "Бир тутумдуу сүйлөмдөрдүн айрым бир түрлөрүнүн баяндоочу кайсы жакта, кандай санда турса да, маани жагынан жалпыланып, үч жакка тең таандык болуп турат. Ошондуктан мындай маанидеги баяндоочтор билдирген кыймыл-аракет да конкреттүү бир жакка эмес, кандайдыр бир жалпыланган жакка (үч жакка тең) тиешелүү болуп айтылат. Мисалы: Мектеп, мугалим, тарбия деген үч сөздү атаганда тээ артта калган балалык курбалыңды аргасыздан эске түшүрөсүң («Муг. газ.»). Адамды ишинен тааныйт (макал). Бул сүйлөмдөрдүн баяндоочтору II жана III жакта, жекелик санда турат, бирок алар билдирген кыймыл-аракет конкреттүү экинчи же үчүнчү жакка таандык болуп айтылган жок, ошол себептүү бул сүйлөмдөрдүн грамматикалык ээси сен, ал деген ат атоочтор болот деп айтууга да болбойт. Бул сүйлөмдөрдүн баяндоочтору туюндурган иш-аракет кандайдыр бир жалпыланган жакка тиешелүү болуп айтылып жатат. Ошол себептүү мындай сүйлөмдөрдө конкреттүү грамматикалык ээси болбойт жана анын сүйлөм тизмегинде катышышынын зарылчылыгы да сезилбейт. Эгерде, мисалы, жогоруда көрсөтүлгөн сүйлөмдөрдүн биринчисинин тутумуна сен деген сөздү кошуп айтсак, анда сүйлөмдүн структурасы өзгөрүп, эки тутумдуу сүйлөмгө өтүп кетер эле: Мектеп, мугалим, тарбия деген үч сөздү атаганда, сен тээ артта калган балалык курбалыңды аргасыздан эске түшүрөсүң. Ал эми экинчи сүйлөмдүн баяндоочунун III жакта турушуна карап, анын тутумуна «ал» деген ат атоочту грамматикалык ээ катары зордоп кийирүүгө болбойт, анда сүйлөмдүн структурасы да, мааниси да өзгөрүлүп кетет.\n" +
                        "\nБаяндоочу билдирген кыймыл-аракет кандайдыр бир жалпыланган жакка (үч жакка тең бирдей) тиешелүү болуп айтыл-са, жалпылама жактуу сүйлөм деп аталат. \n" +
                        "\n\nЖаксыз сүйлөм.\n\n" +
                        "Жаксыз сүйлөм да – бир тутумдуу сүйлөмдүн бир түрү. Жактуу сүйлөм сыяктуу, бул сүйлөмдө да ээ катышпайт, баяндооч гана бар. Кыскасы, ээнин таптакыр жок болушу, сүйлөмдүн негизин жалгыз гана баяндоочтун түзүп турушу – жаксыз сүйлөмдүн өзгөчөлүгү болуп саналат.\n" +
                        "Мисалы: Күндүз ызаатта, түндө кызматта. (Таб.) Уулду төрөйт, уятын кошо төрөбөйт. (Мак.)\n" +
                        "Баяндооч III жактагы этиштен же башка сөз түркүмүнөн болот.\n" +
                        "Жаксыз сүйлөмдөрдүн баяндоочторунун да белгилүү өлчөмдө түзүлүш бөтөнчөлүктөрү бар:\n" +
                        "\nа) Баяндоочу зат атоочтон болот, же ага кызматчы сөздөр тизмектешип айтылат: Оозуңа май, кагылайын. Үйдө баатыр, жоодо жок. (Мак.) Кайда болсо да бир өлүм. (Мак.)\n" +
                        "\nб) Сын атоочтук баяндооч аркылуу уюшулат: Сага оңой болгону менен, мага кыйын.Суудай суюк, уудай ачуу. (Таб.)\n" +
                        "\nв) Негизги баяндоочко мүмкүн, тийиш, керек, эмес, экен деген көмөкчү милдет аткаруучу сөздөр тизмектешүү менен: Кары адамга катуу айтууга мүмкүн эмес. Бир жылда эмес, он жылда да бүтпөөгө тийиш.\n" +
                        "\nг) Баяндоочтун милдетин –бас мүчөсү аркылуу жасалган атоочтук аткарганда: Жаактуудан кепшебес, аркалуудан оонабас. (Таб.) Арыса да, ачса да, бермейинче суу ичпес, жем жебес. (Таб.)\n" +
                        "\nд) Негизги баяндоочко жарайт, жарабайт, болот, болбойт, туура келет деген көмөкчү этиштер тизмектешкенде: Алымдарды ээрчитип тарууга болбойт. Эми ушинтип жашоого туура келет экен да. (К.Ж.)\n" +
                        "\n\n\nАтама сүйлөм.\n\n" +
                        "Кубулуштарды, окуяны, мезгилди, орун-мейкиндиктин аттарын атоо иретинде айтылган бир тутумдуу сүйлөмдөр атама сүйлөм деп аталат.\n" +
                        "Мындай сүйлөм атооч жөндөмөсүндөгү сөздөн жана сөз айкашынан жасалат. Мисалы: Түптүз талаа. Канча караган менен уч- кыйрына көз жетпейт. Түн. Ай жарык. Бак жактан музыканын үнү угулат. Мында атама сүйлөмдөр: түптүз талаа (эмне?) – орун- мейкиндик; түн (эмне) – жаратылыштын мезгили; ай жарык (кандай) – кубулуштун атын билдирди.\n" +
                        "Атама сүйлөмдө аны менен байланышта айтылган сүйлөмдүн мааниси зор, анткени ал аркылуу атама сүйлөмдөгү ой андан ары улантылат, тереңдетилет. Мисалы: Согуш. Согуш дегениң – кан төгүү, жакырчылык, кайгы-муң.\n" +
                        "Атама сүйлөм сүйлөм мүчөлөрүнө ажыратылбайт. Сүйлөмдүн бул түрү көркөм адабияттарда кеңири колдонулат.\n" +
                        "\n\n\nКемтик сүйлөм.\n\n" +
                        "Сүйлөмдүн баш же айкындооч мүчөлөрүнүн бирөө же экөө айтылбай калган, бирок анын кайсы сөз экендиги контексттен, жалпы жагдайдан белгилүү болуп турган сүйлөм кемтик сүйлөм деп аталат.\n" +
                        "\nСүйлөмдүн бул түрү өзгөчө оозеки кепте жана көркөм адабий чыгармаларда көп кездешет да, ойду кыскача, эркин жана көркөм баяндоого мүмкүнчүлүк берет. Кемтик сүйлөмдөрдүн колдонулуш тагдыры дайыма өзүнүн алдындагы толук сүйлөм менен, контекст менен, кептик жагдай менен тыгыз байланыштуу болот. Эгерде кемтик сүйлөмдү контекст менен, өзүнүн мурунку толук сүйлөм менен байланыштырбай, алардан бөлүп алып карасак, кемтик сүйлөмдүн грамматикалык структурасы толук болбогондуктан, ойду жеткиликтүү түшүндүрө албай калат, ал турсун, таптакыр түшүнүксүз болуп калышы да мүмкүн. Мисалы: «Андан бери айдалбаптыр» деген сүйлөмдү өзүнчө бөлүп алсак, кандайдыр бир толук түшүнүктү туюндура албайт. Эми бул сүйлөмдү өзү тутумунда айтылган контекст менен бирдикте алып көрөлү: Илгери бир кездерде Ак-Сайда кошчулар эгин эккен экен. Андан бери айдалбаптыр (Ч.А.). Мында, биринчиден, көптөн бери айдалбай жаткан жер Аксай галаасы экендиги алдыңкы сүйлөмдөн белгилүү болуп турат. Экинчиден, жогорку аталган кемтик сүйлөмдү өзүнөн мурунку толук сүйлөм менен бирдикте алганда, Ак-Сайдын көптөн бери айдалбай жаткандыгы тууралуу ачык маалымат берип турат. Ошондой эле «Жорголугу» дегенди жеке бөлүп алсак, бир нерсе тууралуу толук кабар бербейт, эмне жөнүндө айтылгандыгы түшүнүксүз. Эгерде аны тиешелүү диалогдук кептин тутумунда колдонсок, анда ал белгилүү бир ойду туюндура алат.\n" +
                        "Мисалы: - Бу тайдын эмнеси артык экен? - Жорголугу (Ч.А.).\n" +
                        "Ушинтип, кемтик сүйлөмдөр дайыма контекстте, өзүнөн мурунку толук сүйлөмдөр менен байланышта айтылат. Мындай учурда, өзгөчө оозеки кепте алдыдагы толук сүйлөмдүн тутумундагы кээ бир сөздөрдү кийинки кемтик сүйлөмдө да кайталап айтып отуруунун зарылчылыгы болбой калат. Натыйжада, андай сүйлөмдөрдө зарыл, керектүү мүчөлөрүнүн бирөө же экөө айтылбай калат. Бирок ал кемип калган мүчөсү кайсы экендиги сүйлөөчүгө да, угуучуга да жалпы контексттен ачык белгилүү болуп турат.\n" +
                        "Ар түрдүү шарттарга карата диалогдук кемтик сүйлөмдөрдө да сүйлөмдүн баш жана айкындооч мүчөлөрүнүн айрымдары айтылбай калат, бирок анын кайсы сөз экендиги өзүнөн мурунку толук сүйлөмдүн мазмунунан белгилүү болуп турат.\n" +
                        "\nа) Сүйлөмдүн ээси айтылбай калат. Мындай учурда баяндоочтун милдетин III жактагы этиш же атооч сөздөр аткарып, үчүнчү жактагы грамматикалык ээ менен байланышта айтылат. Мындай структурадагы сүйлөмдөрдүн ээси айтылбай калганда, кемтик сүйлөм уюшулат да, ал кемип калган ээси кайсы экендиги өзүнүн алдында турган толук сүйлөмдөн белгилүү болуп турат:\n" +
                        "- Бүгүн жыйналыш болобу?\n" +
                        "- Болот (Н.Б.)\n" +
                        "- Зоотехник кайда кетти?\n" +
                        "- Малчыларга кетти.\n" +
                        "- Ысмыңыз ким болот?\n" +
                        "- Кулжыгач уулу Жума (А.Т.)\n" +
                        "\nб) Кемтик сүйлөмдөрдө баяндоочу айтылбай калат: - Ким керек эле, келиндер?\n" +
                        "- Башкарма (Т.С.)\n" +
                        "в) Сүйлөмдүн ээси менен баяндоочу айтылбай калат:\n" +
                        "- Керимбек кайда кетти?\n" +
                        "- Малчыларга (Н.Б.).\n" +
                        "- Райондо канча мектеп бар?\n" +
                        "- Отуз алты.\n" +
                        "Рахмат аке. Ал аял каерде турат экен? Акунжан. Райцентрде (М.Тойб.).\n" +
                        "\nг) Кээде айкындооч мүчөлөрдүн бири айтылбай калат: - Эрежени билесиңби?\n" +
                        "- Билебиз («ЛЖ») («эрежени» деген толуктооч айтылбай калган).\n" +
                        "- Муну жыйналышка чейин неге бүтүр-гөн эмессиңер?\n" +
                        "- Жетише албай калдык (Н.Б.). («бүтүрүүгө» деген толуктоочу айтылбай калган).\n" +
                        "\nКыскартылган баяндоочту сүйлөм.\n" +
                        "Баяндоочу айтылбай калса да, өз алдынча туруп толук ойду түшүндүргөн жана кемиген баяндоочунун кайсы сөз экендиги өзүнүн эле мазмунунан белгилүү болуп турган сүйлөмдөр кыскартылган баяндоочтуу сүйлөм деп аталат.\n" +
                        "\nСүйлөмдүн бул түрү макал-лакаптарда көп кездешет . Кыскартылган баяндоочтуу сүйлөмдөрдө ээнин аракетин, ар кандай белгисин көрсөтүү эмес, өзгөчө анын кыймыл-аракетинин ордун, мезгилин, объектисин жана башка кырдаалын көрсөтүүгө көңүл бурулат. Ошол себептүү мындай сүйлөмдөрдө баяндоочту сөзсүз катыштырып айтуунун зарылчылыгы болбой калат да, сүйлөм ээ менен айкындооч мүчөлөрдүн катышуусунан уюшулат. Алардын сүйлөм ичинде жайгашышы эки түрдүү болот: ээ айкындооч мүчөдөн мурун орун алат, же андан кийин келип калат. Мисалы: Жакшы - эли менен, жер - кени менен. Арачыга - алты таяк (макал). Кыскартылган баяндоочтуу сүйлөмдөр грамматикалык ээ менен бышыктоочтун, толуктоочтун же аныктоочтун катышуусу менен уюшулат: Эт - этке, сорпо - бетке. Эр - тууган жерине, ат - тойгон жерине. Адам - сүйлөшкөнчө, жылкы - кишенешкенче. Жакшынын - шарапаты, жамандын - кесепети (макалдар). Сүйлөмдүн бул түрүндө баш мүчөлөрдүн негизги бир түрү болгон баяндооч катышпагандыктан, баяндоочу кыскарган сүйлөмдөрдү бир тутумдуу сүйлөм катары кароого болот.\n" +
                        "Сүйлөмдүн бул түрү ойду кыска жана так айтып түшүндүрүүгө өтө ийкемдүү келет. Ошондуктан ушундай моделде түзүлгөн кыскартылган баяндоочтуу сүйлөмдөр азыркы учурда да өзгөчө публицистикалык стилде активдүү колдонулат: Механизация - фермаларга. Эгин - мамлекетке. Илимдин жетишкендиктери - өндүрүшкө. Эмгегибиз - Родинага («СК»).\n" +
                        "Ээ жана айкындооч мүчөнүн катышуусу менен уюшулган кыскартылган баяндоочтуу сүйлөмдү жазууда ээ менен айкындоочтун арасына сызыкча коюлат; сызыкча баяндоочтун түшүрүлүп айтылгандыгын туюндурат. Эр азыгы - элден, бөрү азыгы - жолдон (макал).\n");
break;
            case 3:
                openSecond("Тема: Сүйлөмдүн айтылыш максатына карай бөлүнүшү","Маселелер:\n" +
                        "1. Жай сүйлөм.\n" +
                        "2. Суроолуу сүйлөм.\n" +
                        "3. Буйрук сүйлөм.\n" +
                        "4. Илептүү сүйлөм.\n" +
                        "\n1. Тилдик пикир алышуу процессинде сүйлөөчү өз алдына ар түрдүү максатты коюшу мүмкүн: же ал турмушта болгон кандайдыр бир окуя жөнүндө жайынча баяндайт, же угуучуга тиешелүү суроо берип, өзү билбеген, ачык эмес маселелер боюнча жооп алгысы келет, же болбосо, өзүнүн буйрук - өтүнүчүн, ар түрдүү эмоциялуу ички сезимин баяндайт. Ушундан улам бардык сүйлөмдөр айтылуу максатына карай жай сүйлөм, суроолуу сүйлөм, буйрук сүйлөм жана илептүү сүйлөм болуп төрт топко бөлүнөт.\n" +
                        "\n\n\nЖай сүйлөм.\n\n" +
                        "Турмушта болгон ар түрдүү иштер, көрүнүштөр, окуялар, кубулуштар ж.б. жөнүндө кабарлоо иретинде айтылган сүйлөмдөр жай сүйлөм деп аталат.\n" +
                        "Жай сүйлөмдөр үчүн өзгөчө баяндоо интонациясы мүнөздүү: Сүйлөмдүн маанилүү бир нерсе тууралуу кабар берген мүчөсү көтөрүңкү үн (тон) менен айтылат да, сүйлөмдүн аяк ченинде үн акырындык менен басаңдайт. Жай сүйлөмдүн аягына дайыма чекит коюлат.\n" +
                        "Жай сүйлөмдөр өзүнүн маанилик өзгөчөлүктөрүнө ылайык тилибизде өтө кеңири колдонулат. Айтылуучу ойдун мазмунуна карай жай сүйлөмдүн структуралык түзүлүшү да ар турдүү болот. Ал тилибизде эки тутумдуу жана бир тутумдуу, жалаң жана жайылма, жөнөкөй жана татаал сүйлөм түрүндө кенири кездешет.\n" +
                        "Мисалы: Басмачылар Ноокатты көздөй бет алышты (Т.Кас). Жумуштун камын көрүш керек (Ч.А.). Июнь айы. Дарактар да, талаа да жашыл (Ж.Мав.). - Болуптур. Чоңдор менен сүйлөшүп көрөйүн (Ч.А.). Жапар кубанды (К.Б.). Ал Жамиланын келишимдүү келбетинен көзүн албады (Ч.А.). Тамак желди, дасторкон жыйналды, колдор жуулду (К.Б.). Уулумдун акыры бир күнү көп катары аскерге кетерин билип эле жүргөм, ошентсе да бул кабарды укканда муундарым бошой түштү (Ч.А.).\n" +
                        "Жай сүйлөмдүн туюндурган мааниси да өтө бай жана ар түркүн. Мисалы:" +
                        "\n\n1) Бир нерсенин кыймыл-аракетин, абалын баяндайт, бул учурда баяндоочу баяндагыч ыңгайда (келер чак, учур чак, өткөн чакта) айтылат; же абалды билдирүүчү сөздөрдөн болот: Төмөнкү белесте беш-алты киши көр казып жатат (К.Ж.). Электр станциясына суу агызчу өстөн казабыз (Т.С.). Ал кадимки акын өңдүү төгүп чертти (Н.Б.). Ажар көзцнцн жашын мөндүрдөй төгүп отурат (К.Б.). Мемиреп табияттын бардык көрүнүшү тынчтыкта (К.Ж.).\n" +
                        "\n2) Сүйлөмдө кабар берилген нерсенин сыпаттык, сандык белгисин көрсөтөт: Чүйдүн талаасы сапсары алтындай... (А.Т.). Аскерге жөнөп жаткандар толкундуу, кызуу (Т.С.). Аба ысык, күн ачык (К.Ж.). Биздин чарбанын саны он миң.\n" +
                        "\n3) Каалоо, тилек, ниет, шарт жана болжолдоо, күмөндүүлүк же аныктоо ж.б. маа-нилерин да берет: Эртең Токтогул ГЭСинин куруучуларына барайын. Эмгекчилерибиз быйыл да планын ашыра орундаткай эле. Мен эртең шайлоо участкасына бармакчымын. Жылдык планды ашыра орундатсаң, айыл чарба көргөзмөсүнө барасың. Ал бүгүн үйүндө болуу керек (А.Т.). Балким, үйдө орун да жоктур (Т.С.). Мырзакул аны түшүндү окшойт (Ч.А.). Эгер мындан куласа, сөзсүз өлөт (Н.Б.).\n" +
                        "Кыргыз тилиндеги жай сүйлөмдөрдүн дагы бир мүнөздүү белгиси: алардын тутумундагы сөздөр тилибизде көндүм болуп калыпташып калган түз орун тартиби боюнча жайгашышат. Мисалы: Албетте, Дүйшөн өзү да менин бул абалымды жакшы билип жатты (Ч.А.). Мында сүйлөм ичиндеги сөздөр түз орун тартибинде жайланышкан: ээси баяндоочтон мурда, айкындоочтору аныкталып турган сөздөрдөн мурун, толуктоочу аны башкарган этиштик баяндоочтон мурда орун алган. Бирок айрым учурларда стилдин талабына ылайык, ырларда болсо уйкаштыкты сактоо максатында жай сүйлөмдөгү сөздөр мурунтадан көндүм болуп калган түз багыттагы ордун өзгөртүп, башкараак тартипте орун алышы да мүмкүн. Бирок мындай жай сүйлөмгө мүнөздүү болгон анын баяндоо мазмуну жана бир калыптагы баяндоо интонациясы өзгөрүлүп кетпейт, тек гана орду алмашылган сөздөрдүн бири логикалык басым менен айтылат. Мисалы: Эки энем урат мени (К.Б.). Ушул Айдиштин сөзү өтүп кетти. Ырас эле алдынан тосуп чыкчы канкордун (Т.С.). Аңгыча эт көтөргөн элүү мерген шоп этип чыга калды аркы белден (А.Т.).\n" +
                        "\n\n\n2.Суроолуу сүйлөм.\n\n" +
                        "Сүйлөөчүнү кызыктырган ар түрдүү нерселер жөнүндө угуучунун ой-пикирин билүү максатында айтылган суроо маанисиндеги сүйлөм суроолуу сүйлөм деп аталат. \n" +
                        "\nСуроолуу сүйлөмдөр өзгөчө интонацияга ээ. Адатта сүйлөм тутумундагы башка сөздөргө караганда суроо маанисиндеги сөз көтөрүңкү үн (тон) менен айтылат: Каапырыңдын минтерин ким билиптир? («Ала-Тоо»). Бектурган турмушта барбы, бар болсо ал кайда олтурат? (А.Т.). Сен аны билесиңби? (А.Т.).\n" +
                        "Ошондой эле сүйлөм ичиндеги логикалык басым түшкөн сөз да көтөрүңкү үн менен айтылат: Чогулушка малчылар да катыштыбы? Бул жерден булак чыгабы? Суроолуу сүйлөмдөрдүн аягына дайыма суроо белгиси коюлат. Суроолуу сүйлөмдөрдү уюштуруучу негизги лексика-грамматикалык каражаттарга төмөнкүлөр кирет:\n" +
                        "1) Суроолуу сүйлөмдөр көбүнчө сурама ат атоочтордун жардамы менен уюшулат. Ким, эмне деген сурама ат атоочтор аркылуу уюшулган суроолуу сүйлөмдөр кандайдыр бир заттын, ар түрдүү абалдын аталышын тактоо максатында айтылат. Мисалы: - А жогорку командирди ким шайлайт? (Ч.А.). - Анатайдын эмнеси кем экен? (Ч.А.). Алыстан бүлбүлдөп көрүнгөн эмне? А сен өзүң кимден үйрөндүң? (К.Б.). Салый, сенин колуң эмне болгон?\n" +
                        "Кандайдыр бир нерсенин ар кандай белгиси жөнүндө сурамжылаган сүйлөмдөр көбүнчө кандай, кайсы, канча, канчанчы, неченчи деген ат атоочтордун катышуусу менен уюшулат: Атаке, апамды кайсы жерге көмдүңөр? (М.Э.). Айыл чарба ишине канча киши жөнөтүлдү? Силердин квартираңар неченчи кабатта?\n" +
                        "Айтуучу үчүн белгисиз болгон ар түрдүү кырдаал (мезгил, орун, максат, себеп, сыпат) тууралуу сурап билүүчү сүйлөмдөр качан, кайда, кайдан, кантип, эмнеге, эмне үчүн, кандай деген сурама ат атоочтордун жардамы менен түзүлөт: Анын атасы кайда иштейт? (Ч.А.). Ушунчалык ишти кантип бүтүрдүңөр? Башта эмне үчүн ойлободу? (Ч.А.). Султан кандай иштеп жүрөт? Ормуштар качан көчүп келишкен?\n" +
                        "Суроолуу сүйлөмдүн баш жагына эмне ат атоочу кошулуп айтылса, сүйлөмдүн суроолуу маанисин күчөтүп, ага бир аз кекетүү маанисин да кошумчалап турат: Эмне, башкалар жөтөлүп жатканда, ал жөтөлсө болбойт бекен? (Ч.А.). Эмне, Макишти бийлей албайт дейсиңерби? (Т.С.). Эмне, мени коркот дейсиңби? (Ч.А.).\n" +
                        "2) Суроолуу сүйлөмдөрдү уюштурууда суроо уландысы -бы активдүү катышат: Пристанга жөнөп жатасыңарбы? (К.Б.). Силер балдарды окутууга каршысыңарбы? (Ч.А.). - Таякем келдиби? (Т.С.).\n" +
                        "3) Айрым учурда суроолуу сүйлөмдөн кийин сурама ыя бөлүкчөсү келип, анын суроолуу маанисин дагы да тактап, күчөтүп көрсөтөт. Бул бөлүкчө кээде сүйлөмдүн башында келип, анын ички маанисине карай кекээрлеп - суроо маанисин берет: - Ар бириңе үч жуптан даярдап берсем жетпейби, ыя? (Ч.А.). - Бу эмне деген кеп, ыя? (Т.С). - Андайлар сенин шакиртиң болбойбу, ыя? (А.Т.). - Ь1я, сен кайсы элчи элең? - Ыя, кайсы Ыманбайсың? (Т.С.).\n" +
                        "4) Сурама ээ бөлүкчөнүн катышуусу менен да суроолуу сүйлөм уюшулат: Кызык, ушундай да адам болот экен ээ! («Ала-Тоо»). - Тайдын этинен да артык экен ээ? (Ч.А.). - Эркинбек, сен былтыр кош айдагансың ээ? (Ч.А.).\n" +
                        "5) Кээде күмөндүү го бөлүкчөсүнүн, өтүнүү, сурануу маанисиндеги -чы мүчөсүнүн жардамы менен да суроолуу сүйлөм уюшулат. Андай сүйлөмдөр суроо мааниси менен бирге болжолдоо, өтүнүү маанисин да кошо камтыйт. Мындай түзүлүштөгү сүйлөмдөрдүн суроолуу мааниге жетишүүсүндө суроо интонациясы өзгөчө мааниге ээ. Мисалы: - Чамасы, небереси болосуң го? (Т.С.). - Ушул кубанычыңа караганда, Касейиниңден кат келген го сага? (Т.С.). Көзүңөр курчуңар жакшылап байкагылачы? (Т.С.).\n" +
                        "6) Бейм (бы+де+й+м), беле (бы+эле), бекен (бы+экен) жардамчы сөздөрү да баяндоочтун тутумунда айтылып, суроолуу сүйлөм уюштурат, ал дайыма суроо интонациясынын коштоосу менен айтылат: Сиз байкабай каласыз бейм? (К.Б.). Күлкүгө көтөрүлө түштү бейм? (Т.С). - Агай, сиз самолёттон секирдиңиз беле? (Ч.А.). Сен киного барат белең? Карааныңды бир көрөр күн болор бекен? - деп, желден сурайт (Т.С). Ал Жалал-Абад курортуна барат бекен?\n" +
                        "7) Айрым учурда «чыгар» модал сөзү баяндоочтун тутумунда айтылып, суроолуу сүйлөм уюшулат, суроо интонациясы менен айтылат: Атаң экөөңдүн мага көрсөткөн кордугуңар эсиңде чыгар? (К.Ж.). Ал жөнүндө массалык - саясий иштер жүргүзүлүп жаткан чыгар? (Н.Б.). Бирок тутумунда суроо маанисин берүүчү жогоркудай грамматикалык жана лексикалык каражаттары бар сүйлөмдөрдүн баары эле суроолуу сүйлөм боло бербейт. Ошондуктан андай структурадагы сүйлөмдөрдүн маанисине этияттык менен көңүл буруу керек. Эгерде андай сүйлөмдөр экинчи бирөөдөн жооп алуу максатында суроо маанисинде жана суроолуу интонация менен айтылбай, бир нерсе тууралуу жайынча гана баяндама берип турса, жай сүйлөм болуп калат.\n" +
                        "\n\n\n3.Буйрук сүйлөм.\n\n" +
                        "Сүйлөмдүн бул түрү угуучуга кандайдыр бир ишти аткарууну буйруйт, өтүнөт. Буйрук сүйлөм айтуучунун ар түрдүү ички эркин (буйруу, талап кылуу, өтүнүү, чакырык ж.б.) билдирет. Сүйлөмдө көбүнчө ээ катышпайт, көпчүлүк учурда каратма сөз кошулуп айтылып, аракет, буйрук кимге багышталгандыгын көрсөтүп турат. Буйрук сүйлөмдөр түзүлүшү жагынан кыска келип (көбүнчө бир-эки гана сөздөн болот), ойду так, жыйынтыктуу, көркөм туюндурат.\n" +
                        "Буйрук сүйлөмдөр өзгөчө буйруу интонациясы менен айтылат: эгерде сүйлөм буйрук берүү, чечкиндүү талап кылуу маанисинде колдонулса, бийик үн (тон) менен айтылат: Жогол, түбөлүккө карааның өчсүн! Жогол! (Ч.А.). Эгерде өтүнүү, кеңеш берүү маанисинде колдонулса, сүйлөм пасыраак үн (тон) менен айтылат: - Андан көрө саман апкелип, от жакчы (Ч.А.). Силер саат экиге келгиле.\n" +
                        "Буйрук сүйлөмдөр, негизинен, төмөнкүдөй жолдор менен уюшулат:\n" +
                        "1. а) Буйрук сүйлөмдүн баяндоочу, адатта экинчи жак, жекелик санда одоно түрүндөгү буйрук ыңгайдан болот, мындай этиштер көбүнчө уңгу түрүндө, же -гын формасында колдонулат да, сүйлөмгө буйруу, талап кылуу маанисин берет: Адамдын акысын жер кайтарат. Чарбанын жумушуна чык. Огородуңду таштаба. Балага сак бол. Жаңылыктарды үзбөй жаз (Т.С.). - Нургазы, таякең келген тура. Бар, эшик ач (Ч.А.).\n" +
                        "б) Эгерде сүйлөмдүн баяндоочу буйрук ынгайдын -гын формасында айтылса, анда буйруу жогоркудай кескин түрдө эмес, жумшагыраак формада айтылат: Ырым болсун, баланын киндигин сен кескин, кагылайын (Т.С). Көк чөөгүгө суу алып келгин (Т.С.). - Ой, Зуура, сен да камынгын (Т.С.).\n" +
                        "в) Буйрук сүйлөмдүн баяндоочунун милдетин -гыла формасындагы 2-жак, көптүк одоно түрүндөгү буйрук ыңгай аткарганда, буйруу мааниси жумшак формада берилет: Эл барыпатат, силер да ошерде болгула (Ч.А.). - Келгиле, кыздар. Ниетиңер ак экен, чайга олтургула (Т.С.). Четинен кармап ала бергиле (Ч.А.). Силер эми өз сапарыңарды билгиле. Найза сунуп жоо качырышты билгиле (Т.С.).\n" +
                        "Айрым убактарда мындай түзүлүштөгү сүйлөмдөрдө буйруу кескин түрдө да айтылат. Мындай учурда баяндооч сүйлөм башында келип, көтөрүңкү үн (тон) менен айтылат:\n" +
                        "- Токтоткула чууңарды! (Ч.А.). Чык эшикке, айбан. Күнүң бүттү сенин (Ч.А.).\n" +
                        "2) Эгер буйрук сүйлөмдүн баяндоочу 2-жак, жекелик, же көптүк сылык түрүндөгү буйрук ыңгайдан болсо, буйруу сылык формада айтылат:\n" +
                        "- Ме, энеке, эми карындын ооз ун өзүңүз бузуңуз (Т .С.). Алыстан келген конок экенсиз, кезегимди алыңыз (Т.С.). - О Текем тура. Алып келиңиз чаначыңызды (Т.С.). - Ооба, акелер, эми сагынышабыз. Тез көрүшөбүз десеңиздер, мыкты иштеңиздер. Күчкө күч кошуңуздар (Т.С.).\n" +
                        "2) Буйрук сүйлөмдүн баяндоочунун милдетин -сын формасындагы 3-жак, жекелик, көптүк түрүндөгү буйрук ыңгай аткарат; үчүнчү жакка буйруу, каалоо маанисин берет: Тизмеден аты окулбаган жолдоштор чарбасына кайта беришсин (Т.С.). - Эч кимге оозуңан чыкпасын (Ч.А.). Бирок бу киши буерден кетсин, экинчи жолобосун (Ч.А.). Эч кандай шылтоо болбосун (Ч.А.). - Мейли, жандары аман болушсун (Т .С). Бул сүйлөмдөр көбүнчө каалоо маанисин туюндуруп турат.\n" +
                        "Кээде мындай структурадагы сүйлөмдөр, өзүнүн лексикалык тутумуна ылайык, буйруу маанисинде эмес, жөн гана ырастоо, тактоо маанисинде колдонулуп калат: Элди, жерди ким сагынбасын (Т.С.). («Элди, жерди ким сагынбайт, ар ким сагынат» деген мааниде). - Эй, техник мырзалар! Өйдөлөп баратасыңар. Кийин өстөнүбүздөн суу чыкпай коюп жүрбөсүн (Т.С.). (болжолдоо, бүшүркөнүү маанисинде).\n" +
                        "Айрым убакта мындай түзүлүштөгү буйрук сүйлөм үчүнчү жакка бир ишти аткарууну буйруу экинчи жак аркылуу (ортончу аркылуу) болорун билдирет: Асан бүгүн келбесин. (Мында Асанга келбегин деп экинчи бирөө айтуу керек). Сергей биздин үйдөн даам татып чыксын (Т.С.).\n" +
                        "3) Сүйлөмдүн бул түрүнүн баяндоочу -алы формасындагы 1- жак, көптүк түрүн-дөгү каалоо ыңгайдан да болот да, кандай-дыр бир ишти биргелешип иштөөгө түрткүлөө, козгоо маанисин берет: - Кана эмесе, балдар, сабагыбызды уланталы (Ч.А.). Жүргүлө биздикине, абышка-кемпирдин колунан чогуу даам жейлик (Т.С.). - О, журт! Балдарды токтото бербейлик, жөнөтөлүк (Т.С.).\n" +
                        "4) Баяндоочу -алы, -гыла, -сын формасындагы буйрук ыңгайдан болгон сүйлөм-дөр чыкырык маанисинде да колдонулат: Билим, ынаным, аракет кайра жаралуу ишине кызмат кылсын!\n" +
                        "5) Буйрук сүйлөмдүн баяндоочу аягына сурануу, өтүнүү маанисиндеги -чы мүчөсү жалганган 2-жактагы шарттуу ыңгайдан болот (кээде -чы мүчөсү уланбай да айтыла берет); семантикасы боюнча буйруу-өтүнүү маанисин билдирет: Ошону айтканча сабагыңды жаттасаңчы (Ч.А.). -Ой, Метрей чоң сакал, бассаңчы тез (Т.С.). Кезекти сармерденге берип, тамашага өтсөңөрчү (Т.С.). - Ой, Шаке, менин кымызымдан да ичсең (Т.С.). - Чогулушка сен да келсең.\n" +
                        "Буйрук сүйлөмдөргө баяндооч болуп келген буйрук жана каалоо ыңгайлардан кийин айрым убактарда өтүнүү, сурануу маанисиндеги -чы мүчөсү да жалганып айтылат. Мындай учурда сүйлөмдүн буйрук мааниси жумшарып, кошумча өтүнүү маанисин да берет. Мисалы: Ата, башыңды көтөрчү (Ч.А.). Тынч отуруп, эл катары чайыңызды ичиңизчи (Ч.А.). Бүгүнкү кеңешмеге силер да катышкылачы. Быйыл фольклордук экспедицияга эрте чыгалычы. - Келиндер отун-сууга кол кабыш кылышсынчы (Т.С.).\n" +
                        "Буйрук сүйлөмдөр өзүнүн мааниси боюнча айтуучунун ар кандай ички эркин: буйруу, талап кылуу, өтүнүүнү ж.б. билдиргендиктен, кээде максатка ылайык жогорку сезим менен айтылганда, алар оңой эле илептүү сүйлөмгө өтүп кетишет: - Бол, Зукеш! Жарым мүнөткө жетпей бардыгы даяр болсун! (Т.С.). Аскер иши так болот. Кечиктирбейлик! (Т.С.). - Былк этпегиле! Колуңарды көтөргүлө! (Т.С.). - Сен ушул биздин тоо үчүн, биздин көл, биздин жер үчүн күрөш! (Т.С.).\n" +
                        "\n\n\n4.Илептүү сүйлөм.\n\n" +
                        "Сүйлөмдөр ар түрдүү ички сезимди билдиришине (эмоционалдык маанисине) ка-рай илептүү сүйлөм деп да бөлүнөт. Адамдын ар түрдүү эмоционалдык ички сезимин билдирүүчү сүйлөм илептүү сүйлөм деп аталат. Илептүү сүйлөмдөрдү уюштуруучу атайын грамматикалык-лексикалык каражаттар бар.\n" +
                        "1) Алардын ичинен эң негизгиси ар түрдүү ички сезимди билдирүүчү илептүү интонация болуп эсептелет. Илептүү сүйлөм адатта бийик үн (тон) менен айтылат, өзгөчө анын тутумундагы ички сезимди билдирип турган сөз айрыкча көтөрүңкү үн менен айтылат; сүйлөм аягында үндүн (тондун) басаңдашы жай сүйлөмдөгүдөй акырындык менен болбостон, чукул, тез басаңдайт. Илептүү сүйлөмдөр туюндурган эмоционалдык маанинин, ички сезимдин ар түрдүүлүгүнө карай андай сүйлөмдөр менен бирге айтылуучу илептүү интонациянын түрү, мазмуну да өтө бай, ар түрдүү болот (кубануу, коркуу, таңдануу, каардануу, кейүү, ызалануу, иренжүү ж.б. сыяктуу түрдүү ички сезимди билдирет). Жазууда илептүү сүйлөмдөрдүн аягы- на дайыма илеп белгиси коюлат. Мисалы: - Бали! М беле? Мына азамат! Жок дегенде алыстан болсо да, бир көрбөй кетет деген эмне шумдук! (Ч.А.).\n" +
                        "Эгерде күчтүү эмоция илептүү интонация менен айтылса, жай, суроолуу, буйрук сүйлөмдөр да илептүү сүйлөмгө өтүп кетиши мүмкүн.\n" +
                        "Мисалы, жай сүйлөмдүн илептүү сүйлөмгө өтүшү: Биз өкмөт менен партия алдында берген убадабызды аткарабыз! (Т.С.). Эрдик өлбөйт! Эрдик качан да болсо бааланат! (Т.С.).\n" +
                        "Буйрук сүйлөмдүн өтүшү: Сен менин жылдызымды түшүрбө! (Т.С.). Ала-Тоо өрүшүнө шек келтирбегин! (Т.С.). Суроолуу сүйлөмдүн өтүшү: - Кагылайын Зарылдын айтканынан да балбан сөз барбы?! (Т.С.). -Кагылайын журт! Мен силерден ушундай суук кабар укмак белем?! (Т.С.).\n" +
                        "2) Илептүү сүйлөмдөр эмоционалдуу күчөтүү маанисинде колдонулуучу кандай, канчалык, ушунчалык, эмне деген ж.б. сыяктуу сөздөрдүн катышуусу менен да уюшулат: Бүгүн дүйнө жүзү кандай аппак, кандай таза! (Т.С). Баланын кыялы кандай кызык! (А.Т.) Кандай укмуш ээ! Бийиктиги канчалык! (Ч.А.). Бетме-бет жолугушуу ушунчалык кыйын белем! Ушунчалык таш боор болобу?! - О-о, жасаган! Эмне деген кубаныч! Бул эмне деген шумдук! (Ч.А.).\n" +
                        "3) Илептүү сүйлөмдөрдү уюштурууда сырдык сөздөр өтө активдүү катышат да, айтуучунун ар түрдүү ички сезимин билдирет: Чиркин, жан деген кыйын да! (К.Б.). Баракелде! Булардын эрте келгенин карачы! (Т.С) - Олда кокуй ай, жаман болду ээ! (Т.С). Пай-пай! Шаар эмес бекен! (К.Б.). Ошону билбей калганым, ай! (Т.С).\n" +
                        "Кээде илептүү сүйлөмдү уюштурууга сырдык сөз да, эмоционалдуу маанидеги ат атооч да - экөө тең катышышы мүмкүн: - Ой, шумдук оой, кандай чебер жандар! («Ала-Тоо»). Бах! Кандай эркиндик! (Т.С).\n" +
                        "4) Сүйлөм тутумуна эмоциялуу боёгу бар айрым сөздөрдүн же ошондой маанидеги каратма сөздүн катышып айтылышы ме-нен да илептүү сүйлөм уюшулат: Ала-Тоомдун булагы тунуксуң да, муздаксың! (Т.С). Журтум, балдар үчүн кара баш, жаным кур ман! (Т.С.). Кайран бала, сонун жигит болду эле го! (Т.С.). Ушуга мен да ыраазымын, жаным! (Т.С.). - Энең сенин маңдайыңда турат, жылдызым! (Т.С.). Сага эки дүйнөдө ыраазымын, жаным! (Т.С.). - Э, согуш каары ушундай болот, жарыктык! (Т.С.).\n" +
                        "Илептүү сүйлөмдөрдүн туюндурган мааниси өтө эле бай: алар айтуучунун ар кандай ички сезимин, айтылып жаткан ойго карата сүйлөөчүнүн ар түрдүү эмоционал-дык катышын билдирет (таң калуу, коркуу, ачуулануу, каардануу, боору оору, эркелетүү, сыймыктануу, жактыруу, мактоо, жек көрүү, кыжырлануу, өкүнүү, кайгыруу, кубануу, кейүү, ызалануу, суктануу, ирээнжүү ж.б.). Мисалы: Жарайсың, Алыбек! Азаматсың, досум! (Ч.А.) - Бали, азаматтар, бали! - дейт Дмитрий (Т.С.). Пай-пай... Дүйнө ушундай курулган беле? Биз партиянын бул чакырыгын кубаттайбыз! (Т.С.) - Оозуңа май, атаке! (Т.С.). -Көзүн ачты, Асылбай ата, көзү ачылды! (Ч.А.).\n" +
                        "Илептүү сүйлөмдөр эмоционалдуу сезим менен айтылган ар түрдүү каалоолорду билдирет: Жолуңар ачылсын! Душман таш катсын! (Т .С.). Зоболоңор көтөрүлсүн! Жолуңар узарсын! Бакыт- таалайыңар ачылсын! (Т.С.). Илептүү сүйлөмдөр саламдашууда, коштошууда, тилек сөз сүйлөөдө, буйрук берүүдө колдонулат: Экөөңө тең кызыл туулуу шаңдуу салам айтам! (Т.С). - Салоом алейкум! - Алеки салам, келиңиз! (К.Ж.). - Кош, уулдар! Кош, боорунда уул өстүргөн мээримдүү, салкын Улуу-Тоо! (Т.С). Эл-журт, тоо-таштын эсенчилиги үчүн! Ала-Тоону аман көрүшүң үчүн (Т.С).-Токто, Кемел! Токто! Кайда барасың, токто! (Ч.А.). - Кач, иттин баласы! Кач дейм! Сабап салам! Кач! (Ч.А.).\n" +
                        "Ураан-чакырыктарда да кеңири колдонулат: Жашасын элдердин бир туугандык достугу! Билим, аракет кайра куруу ишине кызмат кылсын!");
                break;
            case 4:
                openSecond("Тема: Сүйлөм мүчөлөрү","Маселелер:\n" +
                        "1. Сүйлөм мүчөлөрү жөнүндө түшүнүк.\n" +
                        "2. Сүйлөмдүн баш мүчөлөрү.\n" +
                        "3. Сүйлөмдүн айкындооч мүчөлөрү.\n" +
                        "\n1. Сүйлөм мүчөлөрү. Сүйлөмдүн жардамы менен биз пикир\n" +
                        "алышабыз, ой-сезимибизди билдиребиз. Сүйлөм болсо өз ара тизмектелген сөз айкаштарынан турат. Ошол сүйлөмдү түзүп турган сөздөр сүйлөм ичинде белгилүү бир суроого жооп берип, ар түрдүү синтаксистик милдет аткарышат.\n" +
                        "Сүйлөм мүчөсүнүн милдетин толук мааниге ээ болгон сөздөр, же ажырагыс сөз тизмектери аткарышат. Ал эми кызматчы сөздөр толук маанилүү сөздөр менен коштолуп айтылганда гана сүйлөм мүчөсүнүн милдетин аткарат. Каратма, киринди, сырдык сөздөр сүйлөм мүчөсүнүн милдетин аткара албайт.\n" +
                        "Сүйлөм мүчөлөрү деп – сүйлөм тизмегиндеги сөздөрдүн кандайдыр бир суроого жооп берип, бир сүйлөм мүчөсүнүн милдетин аткарышын айтабыз.\n" +
                        "\n\n\n2.Сүйлөмдүн баш мүчөлөрү.\n\n" +
                        "Сүйлөмдүн ээси. Кандайдыр бир кыймыл-аракетти жасоо, аткаруу үчүн аны ишке ашыра турган нерсе, кубулуш, адам жана башкалар болууга тийиш. Мына ушул нерсенин, кубулуштун жана башканын атын ээ атап көрсөтөт. Демек, ал – сүйлөмдү түзүүчү негиздердин бири. Сүйлөмдө сөз болуп жаткан жак, зат, окуя, кубулушту билдирүүчү жана баяндоо аркылуу туюндурулган бардык белгилерди ишке ашыруучу баш мүчө ээ деп аталат.\n" +
                        "Ээге төмөнкүдөй белгилер мүнөздүү:\n" +
                        "Сүйлөмдө сөз болуп жаткан нерсенин атын билдирүү;\n" +
                        "Атооч жөндөмөдө туруу, ким?, эмне?, кимдер?, эмнелер?,\n" +
                        "кимиси?, эмнеси? деген суроолорго жооп берүү.\n" +
                        "Сүйлөмдүн башка мүчөлөрүнүн бирөөнө да баш ийбөө,\n" +
                        "тескерисинче, аларды өзүнө багындыруу.\n" +
                        "Сүйлөм ээсинин милдетин толук мааниси бар жеке бир сөз\n" +
                        "түркүмү да (алардын жөнөкөй жана татаал түрлөрү), бир нече сөздөн 41\n" +
                        "Сүйлөм мүчөлөрү сүйлөм ичинде аткарган милдеттерине карай баш мүчөлөр (ээ, баяндооч) жана айкындооч мүчөлөр (аныктооч, толуктооч, бышыктооч) болуп эки топко бөлүнөт. түрмөктөлүп түзүлгөн ажырагыс тизмектер да, туруктуу сөз айкаштары да аткарат. Мына ушуга карата сүйлөм ээси грамматикалык түзүлүшү боюнча жөнөкөй жана тутумда ээ болуп бөлүнөт.\n" +
                        "- Сүйлөм тутумунда грамматикалык ээнин милдетин жөнөкөй сүздөр, же лексикалык бир гана мааниси бар татаал сөздөр аткарса, түзүлүү боюнча жөнөкөй ээ деп аталат.\n" +
                        "Мисалы: Жакшылык бардык карама-каршылыкты жеңип, турмушту көркүнө чыгарат жана чечилбегенди чечип, түнттү жарык кылат. Ар күн сайын ичтен сызып, досунун кайгысын тарткан адам зарыл учурда досу үчүн жанын курмандыкка чалган адамдан артыкчылык кылат. Сары-Жаз, Ак-ыйрак, Ак-Сай, Арпа жана Суусамыр – кыргыз жергесиндеги атактуу жайлоолор. 1-2- сүйлөмдөрдө жөнөкөй ээнин милдетин жөнөкөй зат атоочтор (жакшылык, адам), ал эми 3-сүйлөмдө татаал сөздөр (Сары-Жаз, Ак- Шыйрак, Ак-Сай) жана географиялык жөнөкөй энчилүү аттар (Арпа, Суусамыр) аткарды. Бирок бул татаал сөздөрдө лексикалык бир гана маани (бир гана жайлоонун аты) бар.\n" +
                        "Жыйынтыктап айтканда, толук маанилүү жөнөкөй сөз менен бир гана маанини билдирген көп түгөйлүү татаал сөздөрдүн сүйлөмдө аткарган синтаксистик милдети бирдей болуп эсептелет. Ошол себептүү аларды синтаксисте жөнөкөй түзүлүтөгү сүйлөм мүчөлөрү деп карайт.\n" +
                        "- Ар башка сөз түркүмдөрүнүн ажырагыс тизмектери же фразеологизмдер ээнин милдетин аткарса, грамматикалык түзүлүшү боюнча тутумдаш ээ деп аталат.\n" +
                        "Мисалы: Калп айтуу – өзүнчө бир кесепет. Ал өлүп бара жатса да, бир ооз калп айтып калат. (Ч.А.) Эл сыйлаган эшикте калбайт, ат сыйлаган жөө баспайт. Өтө жаман өзүн мактайт. Бардык эне-ата эле – тарбиячы, бирок кеп алардын түшүнүгүндө, кандай тарбиячы экендигинде. 1-2-сүйлөмдөрдө толук маанилүү сөздөрдүн ажырагыс тизмектери (калп айтуу, эл сыйлаган, ат сыйлаган) ээнин милдетин аткарып турат; калп, эл, ат – зат атоочтор; айтуу, сыйлаган – этиш сөздөр. 3-4-сүйлөмдөрдө ээлик милтетти толук маанилүү сөздөр менен кызматчы сөздөрдүн ажырагыс тизмектери (өтө жаман, эне-ата эле) аткарган: жаман – зат ордуна колдонулган сын атооч; өтө – күчөткүч бөлүкчө; эле – тактама бөлүкчө.\n" +
                        "Ээлик милдетти аткаруучу ажырагыс тизмектер, андай тизмектерди уюштуруучу өзөктөр (таяныч сөздөр) ар түрдүү:\n" +
                        "а) Мекеме, уюм, газета-журналдардын, китептердин, көчөлөрдүн кошмок аттары: И.Арабаев атындагы Кыргыз Мамлекеттик университети – кадрларды даярдоонун устаканасы. Бишкекте Жал кичирайону кийин курулду. Ч.Айтматовдун “Кыямат” романын окудум.\n" +
                        "б) Ар башка жөндөмөлөрдөгү атооч сөздөр менен атоочтуктардын ажырагыс тизмеги: Ачуусу келип турганы мени абдан чочутту. (Жомок.) Ысыккөлдү көрүп жүргөндөр анын кышында тоңбой турганын лакап кылышат экен. Айылыбызга биринчи келген сизсиз, аке.\n" +
                        "в) атооч сөздөр менен “бар”, “Жок” деген сөздөрдүн ажырагыс тизмектери: Кайгысы жок кара сууга семирет. (Макал.) Дасторконунда ашы бар кадырлуу болот.\n" +
                        "г) Атооч сөздөр менен “көп”, “аз”, “ашык”, “мол”, “кем” деген тактоочтордун ажырагыс тизмектери: Эмгеги аз кадыр-баркка жетпейт. (Макал.) Түшүмү мол дарак экен.\n" +
                        "д) Зат атооч менен сын атоочтун ажырагыс тизмеги: Каалгасы кулпулуусу – чоң кампа, анда совхоздун кызылы сакталат. Билеги жоон бирди жыгат, билими күчтүү миңди жыгат. Бою узун балдар Улуттук Гвардияда кызмат өтөшөт.\n" +
                        "е) Атооч сөздөр менен кызматчы сөздөрдүн жана көмөкчү этиштин ажырагыс тизмеги: - Кабар айттыргандардын бешөө эле келди, - деди чабарман. Сүйлөгөн сөзү, убадасы болсо баягы эле, али шертинен жаза элек. (С.Өмүрбаев.)\n" +
                        "ж) Туруктуу сөз айкаштары (фразеологизмдер) тутумдаш түзүлүштөгү ээнин милдетин аткарат: Өпкөсү жок өзүн мактайт. Бир жакадан баш чыгаргандар эле бирдиктүү болушат.\n" +
                        "\n\n\nСүйлөмдүн баяндоочу.\n\n" +
                        "Сүйлөм тутумунда грамматикалык жактан ээге багынып,\n" +
                        "анын ким, эмне экендигин, кыймыл-аракетин, мүнөзүн, абалын, сынын, сыпатын, сан-иретин билдирген баш мүчө баяндооч деп аталат. Мисалы: 1958-жылы Жетиөгүз корук участогу уюштурулган. Анын максаты асыл тукум жылкыларды көбөйтүү. Адам өлөт, искусство эч качан өлбөйт.\n" +
                        "Баяндооч да грамматикалык түзүлүшү боюнча жөнөкөй жана тутумдаш болот. Баяндоочтун милдетин аткарган сөз жөнөкөй сөз болсо да, татаал сөз болсо да сүйлөм тутумунда синтаксистин бир гана милдети, тактап айтканда, баяндоочтук милдетти аткарат. Мына ошол синтаксистик милдеттерине ылайык алар жөнөкөй түзүлүштөгү баяндоочтор болуп эсептелет. Мисалы: Бирөө сени жамандап жатса, аны менен айтышпа. Акылы болсо өзү түшүнүп, акыры кечирим сурайт. (Насаат.) Кыргызсңандын керемет жери – Ысыккөл. Жакшы байкап сүйлөөр, жаман чайкап сүйлөөр. (Макал.) Сабырсыздын иши тамырсыз. Кулак – экөө, ооз – бирөө, көбүрөөк угуп, аз сүйлө. (Макал.) Ал эми толук маанилүү ар башка сөздөр менен кызматчы\n" +
                        "сөздөрдүн ажырагыс тизмектери жана туруктуу сөз айкаштары баяндоочтун милдетин аткарса, тутумдаш түзүлүштөгү баяндооч болуп эсептелет. Мисалы: Сылаганда жапалак шумкар болбос, мактаганда жабагы тулпар болбос. (Макал.) Кемчилигиңди айткан адамды жаман көрбө. Ал сенин бетиңе жуккан көөнү көрсөткөн күзгү болот. (Насаат.) Бардыгы негиздүү боло турган. (Ч.Айтматов.) Учкан мезгил учкан сайын закымдап, эми кошо канат күүлөйт катуулап. (Б.Сарногоев.) Жан-жаныбарлардын шайын оодарган мезгил – узун сары. (К.Жусупов.) Сенин досуң – ак жүрөк, жылдызы ысык, колу ачык, кыскасы, эң мыкты.\n" +
                        "Баяндооч – сүйлөмдүн баш мүчөсү, ээ менен байланышып, анын кыймыл-аракетин, ал-абалын, кимдигин, кандайлыгын, канчалыгын билдирет. Эмне кылды? эмне кылып жатат? ким? эмне? кандай? деген суроолорго жооп берет. Мисалы: Дарактын жалбырактары дирилдеди. Ысыккөл кышында жылуу. Жалбырактары эмне болду? – дирилдеди – баяндооч, заттын кыймыл-аракетин билдирди. Ысыккөл кандай? – жылуу – баяндооч, заттын ал-абалын көрсөттү.\n" +
                        "Баяндоочтун милдетин төмөнкү сөздөр аткарат:\n" +
                        "Зат атооч: Атамдын бир тууганы – Табылды.\n" +
                        "Сан атооч: Кыздын жашы он жетиде.\n" +
                        "Сын атооч: Тяншанң кышта жылуу, жайда салкын. (М.А.)\n" +
                        "Ат атооч: Кыздардын жоодураган көздөрү – ошол мен,\n" +
                        "наристенин ыңаалап ыйлаганы – ошол мен. Этиш: Өзүңдү өзүң мактаба.\n" +
                        "Тактооч: Биздин кашар дагы жогору.\n" +
                        "Жогоруда эскертилгендей, баяндоо сүйлөм ээсине грамматикалык жактан баш ийген, ошону менен бирге анын ар түрдүү белгилерин – ар түрдүү маанидеги кыймыл-аракетин, мүнөзүн, тулкусун, даамын, көлөмүн, иретин, болжолун, ким, эмне экендигин, тегин, материалын билдирген баш мүчө болуп, ээге предикаттык катышта колдонулат. Ушул эле сыяктуу ээнин ар кандай маанидеги белгилерин тутумдаш түзүлүштөгү баяндоочтор да билдирет. Грамматикалык түзүлүшү боюнча баяндооч жөнөкөй жана тутумдаш болот. Жөнөкөй баяндооч бир гана сөздөн, тутумдаш баяндооч бир нече сөздөн түзүлөт: Мен кийинки күнү тоого бардым. Туут башталган\n" +
                        "мезгил. Баяндоочтор: бардым (тутумдаш).\n" +
                        "(жөнөкөй), башталган мезгил Тутумдаш баяндооч төмөнкүдөй сөздөрдүн тизмегинен түзүлөт:\n" +
                        "а) маани берүүчү сөздөрдөн: Биз мамлекетке кызмат кылабыз.\n" +
                        "б) маани берүүчү жана кызматчы сөздөн: Бала баатыр сен элең.\n" +
                        "в) туруктуу сөз тизмегинен (фразеология): Келиндин сөзү ташка\n" +
                        "тамга баскандай.\n" +
                        "Баяндоочтун милдетин кайсы сөз түркүмү аткаргандыгына\n" +
                        "байланыштуу этиштик жана атоочтук баяндооч болуп бөлүнөт. Сүйлөмдө баяндооч этиш сөздөрдөн болсо этиштик баяндооч деп аталат. Мисалы: Таяктан жаңылган турат, тилден жаңылган\n" +
                        "турбайт. (Макал)\n" +
                        "Сүйлөмдө баяндооч, атооч сөздөрдөн (зат атооч, сын атооч, ат\n" +
                        "атооч ж.б.) болсо, атоочтук баяндооч деп аталат. Мисалы: Кыргызстан – тоолуу өлкө. Өнөр алды – кызыл тил.\n" +
                        "\n\n\nЭэ менен баяндоочтун ортосуна сызыкчанын коюлушу.\n\n" +
                        "Ээ менен баяндоочтун ортосуна төмөнкү учурларда сызыкча коюлат:\n" +
                        "а) Ээ да, баяндооч да зат атооч же сан атоочтон болсо: Канымгүл – арыкчырай сулуу келин. Төрт жерде төрт – он алты.\n" +
                        "б) Ээ зат атоочтон, баяндооч кыймыл атоочтон же тескерисинче болсо: Данияр менен Жамийланын милдети – эгинди станцияга жеткирүү. (Ч.А.) Жаш жигиттин ийилгени – сынганы.\n" +
                        "в) Баяндооч заттык маанидеги сын атоочтон болсо: Тон жакшысы – жылуусу. Анын жоолугу – кызылы. Мындай учурларда баш мүчөлөрдүн ортосунда бир аз тыным болот.\n" +
                        "Төмөнкү учурларда баш мүчөлөрдүн ортосунда бир аз тыным болот да, сызыкча коюлбайт.\n" +
                        "а) Зат атоочтон же заттык маанидеги башка атоочтордон болгон баяндоочко кызматчы сөз айкашып айтылса, же аларга жак, суроо мүчөлөрү уланып турса: Сен билимдүү адамсың. Ырыспай кыргыздан чыккан мыкты обончу эле.\n" +
                        "б) Ээ зат атоочтон болуп, сан атоочтон болгон баяндооч анын конкреттүү санын көрсөтсө: Катышып жаткандар он беш.\n" +
                        "\n\n\n3.Сүйлөмдүн айкындооч мүчөлөрү. Айкындоочтор баш мүчөлөрдүн маанисин айкындайт жана толуктайт. Алар өзүлөрүнүн маанилери боюнча аныктооч, толуктооч, бышыктооч болуп бөлүнөт.\n" +
                        "\n\nАныктооч – сүйлөмдүн айкындооч мүчөлөрүнүн бири. Сүйлөмдө зат менен байланышып, анын ар кандай белгисин (сын- сыпатын, сан-иретин, мүнөзүн ж.б.) билдирет. Кандай? кайсы? канча? нече? канчанчы? кимдин? эмненин? деген суроолорго жооп берет. Мисалы: Анын кара чачы жел менен кошо желбиреп турду. Кандай чачы? – кара – аныктооч – заттын өңү-түсү.\n" +
                        "Аныктооч аныкталуучу сөздөн мурда келип, аны менен таандык байланыш жана ыкташуу жолдору аркылуу байланышат. Мисалы: Акылмандын акылынын пайдасын эл көрөт. Кимдин акылы? – акылмандын – аныктооч, илик жөндөмөдө; ал таандык мүчө (-ы) уланган акылы деген сөз менен таандык байланышта байланышты.\n" +
                        "Аныктоочтун милдетин төмөнкү сөздөр аткарат:\n" +
                        "Зат атооч: Тилдин кулпусу – тиш.\n" +
                        "Сын атооч: Ал шайыр адам эле.\n" +
                        "Сан атооч: Эки катар таш койдум, кызыл өгүздү бош койдум. Ат атооч: Булардын эмгеги ак – алгандары аш болсун. Атоочтук: Жаңылбас жаак, мүдүрүлбөс туяк болбойт. Грамматикалык түзүлүшү боюнча аныктооч жөнөкөй жана\n" +
                        "тутумдаш болот. Жөнөкөй аныктооч бир сөздөн турса, ал эми тутумдаш аныктооч бир нече сөздүн тизмегинен түзүлөт. Мисалы: Булактын суусу суусунуңду кандырат. Өтө шайыр мүнөз бардыгына жарашат. Аныктоочтор: эмненин? – булактын (жөнөкөй); кандай мүнөз? – өтө шайыр (тутумдаш).\n" +
                        "Тутумдаш аныктооч төмөнкүдөй сөздөрдүн тизмегинен түзүлөт:\n" +
                        "а) Маани берүүчү сөздөрдөн: Алыстан келаткан адамдардын үндөрү угулду. Кайсы адамдардын? – алыстан келаткан – тутумдаш аныктооч.\n" +
                        "б) Маани берүүчү жана кызматчы сөздөрдөн: Биз сиз сыяктуу адамдардан тарбия алганбыз. Ким сыяктуу адамдардан? – сиз сыяктуу – тутумдаш аныктооч.\n" +
                        "в) туруктуу сөз тизмегинен жана атоочтук түрмөктөн: Ак төөнүн карды жарылган мезгил. Кайсы мезгил? – ак төөнүн карды жарылган – тутумдаш аныктоо. (фразеология) Жайбаракат басып келе жаткан Акман чоң короого бурулду. Кайсы Акман? - жайбаракат басып келе жаткан – тутумдаш аныктооч, атоочтук түрмөк.\n" +
                        "\n\n\nЖандама мүчө\n\n" +
                        "Жандама мүчө аныкталуучу сөздөн кийин келип, анын кесип, жакындык, өнөр, наам, сыпат, мүнөз, даража жагынан болгон белгисин билдирет. Мисалы: Берикбай уста көбүнчө аялдардын күмүш шакегин, билеригин жасар эле. жандама мүчө – уста. Берикбайдын т.а., аныкталгычтын өнөр жагынан болгон өзгөчөлүгүн билдирди.\n" +
                        "Демек, жандама мүчө өзүнөн мурдагы сөз менен бирдикте, сүйлөмдүн бир эле мүчөсү болот. Мисалы: Берикбай уста аялдардын зер буюмдарын жасайт. Ким? – Берикбай уста – ээ.\n" +
                        "\n\nТолуктооч кыймыл-аракеттин эмнеге багытталгандыгын көрсөткөн заттын атын билдирет. Демек, ал кыймыл-аракеттин маанисин толуктап турат жана баяндоочтун тобуна таандык болуп саналат. Мисалы: Бүрөөнү жамандап келген адамды үйүңө жакын жолотпо. Бакыт адамдын сапатын өзгөртпөйт, аны дааналап көрсөтөт.\n" +
                        "Мында адамды, сапатын, аны деген толуктоочтор заттын атын билдирүү менен, жолотпо, өзгөртпөйт, көрсөтөт деген баяндоочтордун (кыймыл-аракеттин) маанисин толуктап көрсөттү.\n" +
                        "Толуктооч – барыш, табыш, жатыш жана чыгыш жөндөмөлөрдүн биринде турат да, өзүнө багындыруучу түгөйү менен башкаруу жолу аркылуу байланышат. Демек, мында байланыштыруунун негизги каражаты – жөндөмө мүчөлөр. Бирок кээде алар өз ара жандоочтор аркылуу да байланышат. Мисалы: Жакы сөзү менен сооротот, жаман токмогу менен ыйлатат.\n" +
                        "Толуктоочтун милдетин төмөнкү сөздөр аткарат:\n" +
                        "Зат атооч: Карыларда касиет бар.\n" +
                        "Ат атооч: Бизде биримдик, ынтымак бар. Сын атооч: Сулууга мерездиктин кереги жок. Сан атооч: Экөөнү куттукташты.\n" +
                        "Атоочтук: Алтайдан келгендерге урмат-сый көрсөтүштү.\n" +
                        "Кыймыл атооч: Барууга шарт жок.\n" +
                        "Грамматикалык түзүлүшү боюнча толуктооч жөнөкөй жана\n" +
                        "тутумдаш болот. Жөнөкөй толуктооч бир сөздөн, тутумдаш толуктооч бир нече сөздүн тизмегинен түзүлөт. Мисалы: Башкаларга карабады. Көзү өтүп кеткендерге куран окулду. Мында башкаларга – жөнөкөй толуктооч, көзү өтүп кеткендерге – тутумдаш толуктооч.\n" +
                        "Баяндоочтун тобуна таандык болуп айтылган айкындооч мүчөлөрдүн дагы бири – бышыктооч. Ал кыймыл-аракеттин түрдүү белгисин (сыпатын, сан-өлчөмүн, ордун, мезгилин, максатын) билдирет. Мисалы: Биз ошол жылы (качан?) абдан жакшы (кандай?) түшүм алдык. Ыраактан (кайдан?) шаркыратманын үнү угулат. Ажар корккондуктан (эмне үчүн?) кадамын тез-тез шилтейт.\n" +
                        "Бышыктооч да түзүлүшү боюнча жөнөкөй жана тутумдаш болот. Мисалы: Жазында (жөнөкөй бышыктооч) дыйкандар эгин эгишет. Ажар акырын (жөнөкөй бышыктооч) сүйлөдү. Герасим оор жумушту кабагым кашым дебей (тутумда бышыктооч) аткарат. Биздин жоокерлер баатырдык менен кармашты.(тутумдаш бышыктооч)\n" +
                        "Бышыктооч өзүнүн багындыруучу түгөйү менен ыкташуу же башкаруу жолу аркылуу байланышат да, кандай? кайда? кайдан? качан? эмне үчүн? эмнеликтен? канча? деген суроолорго жооп берет.\n" +
                        "Бышыктоочтун милдетин төмөнкү сөздөр аткарат: Зат атооч: Мени Москвага окууга жиберишти.\n" +
                        "Сын атооч: Күн жылуу болуп турат.\n" +
                        "Сан атооч: Орток айдаган эгинди өнөгүңдөн ашык алба. Тактооч: Быйыл түшүм начар.\n" +
                        "Чакчыл: Алысты ойлуу тиктеп, Сейит көпкө кыймылсыз\n" +
                        "отурду.\n" +
                        "Бышыктоочтун түрлөрү:\n" +
                        "Берген маанилери боюнча бышыктоочтун төмөнкүдөй түрлөрү бар: Сыпат бышыктооч кантип? кандайча? кандай? деген\n" +
                        "суроолорго жооп берет. Мисалы: Күчүк аянычтуу кыңшылады.\n" +
                        "Орун бышыктооч кайда? кайдан? каякта? каяктан? каерде? деген суроолорго жооп берет. Мисалы: Кыргызстанда кооз жерлер өтө\n" +
                        "көп.\n" +
                        "Мезгил бышыктооч качан? качанга чейин? качантан бери?\n" +
                        "кайсы убакта? деген суроолорго жооп берет. Мисалы: Кечээтен бери күндүн көзү ачылбады.\n" +
                        "Себеп бышыктооч эмне үчүн? эмне себептен? эмнеликтен? деген суроолорго жооп берет. Мисалы: Корккондуктан оозуна сөз келбеди.\n" +
                        "Максат бышыктооч эмне үчүн? эмне максатта? эмнеге? кандай максатта? эмне кылууга? деген суроолорго жооп берет. Мисалы: Жаңы ачылыштарды табуу үчүн зор эмгек жасаган.\n" +
                        "Сан-өлчөм бышыктооч канча? нече? канчанчы? неченчи? нече жолу? деген суроолорго жооп берет. Мисалы: Жылдын бешинчи айы майрамдарга бай.");
                break;
            case 5:
                openSecond("Тема: Сүйлөмдүн бир өңчөй мүчөлөрү.","Маселелер:\n" +
                        "1. Бир өңчөй баш мүчөлөр.\n" +
                        "2. Бир өңчөй айкындооч мүчөлөр.\n" +
                        "3. Жалпылагыч сөз жана анын тыныш белгиси.\n" +
                        "\n1.Сүйлөм тутумунда грамматикалык жактан бирдей\n" +
                        "түзүлүштө туруп, бирдей суроого жооп берип, бир гана сүйлөм мүчөсүнүн милдетин аткарган бир нече окшош мүчөлөр болушу мүмкүн. Бул мүчөлөр сүйлөмдүн бир өңчөй мүчөлөрү деп аталат. Алар өз ара тең укукта болуп, бири экинчисине багынбайт. Мисалы: Майсалбектен да, Субанкулдан да, Касымдан да кат жок. (Ч.А.) Адамга тиешелүү эң мыкты сапаттар – жөнөкөйлүк, ачык-айрымдык, ак пейилдүүлүк.\n" +
                        "Биринчи мисалда Майсалбектен, Субанкулдан, Касымдан деген сөздөр чыгыш жөндөмөсүндө туруп, кимден? деген суроого жооп берди жана алар тактоочтук милдет аткарды. Экинчи мисалда жөнөкөйлүк, ачык-айрымдык, ак пейилдүүлүк деген сөздөр атооч жөндөмөдө туруп, эмне? деген гана суроого жаап берип, сүйлөмдө баяндоочтук милдет аткарды.\n" +
                        "Бир өңчөй мүчөлөргө төмөнкүдөй белгилер мүнөздүү: а) бирдей суроого жооп беришет;\n" +
                        "б) бирдей сүйлөм мүчөлөрү болушат;\n" +
                        "в) бирдей сөз түркүмүнөн жасалышат;\n" +
                        "г) бирдей формада келишет (уңгу же мүчө түрүндө).\n" +
                        "Бирөңчөй мүчөлөр затты, кыймыл-аракетти жана алардын белгилерин санап көрсөтөт, ошондуктан саноо интонациясы менен айтылып, жазууда алардын араларына үтүр коюлат.\n" +
                        "Кыргыз тилинде баш мүчөлөр (ээ, баяндооч) да, айкындооч мүчөлөр (аныктооч, толуктооч, бышыктооч) да бир өңчөй болушат.\n" +
                        "\n\n\nБир өңчөй мүчөлөрдүн байланышуу жолдору жана тыныш белгиси.\n\n" +
                        "Кыргыз тилинде бир өңчөй мүчөлөр өз ара эки түрдүү жол аркылуу байланышат.\n" +
                        "Интонация аркылуу байланышып, мында бирөңчөй мүчөлөр өз ара эч кандай грамматикалык каражаттарсыз санак интонациясы аркылуу гана байланышат: Үнү да өтө эле кырааттуу, ыргактуу, муңайыңкы эле. (Н.Б.) Байбиченин ээк көтөргөнү өтө табышмактуу, салабаттуу. \n" +
                        "Байламталар аркылуу байланышат. Кыргыз тилиндеги бирөңчөй мүчөлөрдү өз ара жана, менен, да, дагы, же, же болбосо, ары, бирде деген байламталар байланыштырат. Мисалы: Акылай ары сулуу, ары кичипейил жан эле. (К.А.) Ал сайманы да, кара жумушту да, сөздү да, акылды да келиштире билет. (Н.Б.)\n" +
                        "Байламталар менен байланышкан бир өңчөй мүчөлөрдө үтүр төмөнкүдөй коюлат:\n" +
                        "а) Байламта бир нече ирет кайталанып, өзүнөн мурдагы сөзгө тиешелүү болсо, үтүр байламтадан кийин коюлат: Сайдагы түн көрктүү да, коркунучтуу да.\n" +
                        "б) Байламта бир нече ирет кайталанып, өзүнөн кийинки сөзгө тиешелүү болсо, үтүр байламтадан мурда коюлат: Айыптан коркпогондой ал же кандын, же бектин тукуму дейсиңби. (Т.С.)\n" +
                        "в) Жана, менен сыяктуу кайталанбас байламта байланыштырса, үтүр коюлбайт: Адабият – элдин сезими, анын рухий турмушунун гүлү жана күзгүсү. Жамийла менен Даниярдын сөздөрү угулуп турду.\n" +
                        "\n\n\nБаш мүчөлөрдүн бир өңчөй болушу.\n\n" +
                        "Бир өңөчөй мүчөлөр, негизинен, алардын бардыгын бириктирип турган кандайдыр бир сөзгө (мүчөгө) катышта турушу менен шартталат. Бирок бир өңчөй мүчөлөр өз ара бири-бирине карай багыныңкы абалда болбойт.\n" +
                        "Бир өңчөй мүчөлөрдүн өз ара айкалышы, биримдиги алардын тең укуктуулугунун, тең байланышта турушунун базасында жүзөгө ашырылат.\n" +
                        "Ошентип, сүйлөмдөр бирдей суроого жооп берип, синтаксистик бирдей милдет аткарып, сүйлөмдүн бир гана мүчөсү менен байланыш-катыш жасаган жана өз ара тең байланышта келген сүйлөм мүчөлөрү бир өңчөй мүчөлөр деп аталат.\n" +
                        "Кыргыз тилинде сүйлөмдүн бардык мүчөлөрү бир өңчөй болот.\n" +
                        "\n\nБир өңчөй ээ. Алгач, бир өңчөй ээлер жөнүндө сөз кылуудан мурда сүйлөмдүн ээси, ага берилген аныктаманы эсибизге түшүрсөк:\n" +
                        "Сүйлөмдө сөз болуп жаткан жак, зат, окуя, кубулуту билдирүүчү жана баяндооч аркылуу туюндурулган бардык ишке ашыруучу баш мүчө ээ деп аталат. Мисалы: Сен ошондо аңызда кык төгүп жүргөн келиниң менен кичүү балаңды сүйүнчүлөп келгенсиң, Толгонай! (Ч.А.) Ынтымактуу үйгө ырыс түнөйт. (Макал) Жашагысы келеген адам эмгектениш керек. Турмуш ошого үйрөтөт. (С.М.) Кетирилген мисалдарда ойдун темасы, предмети болгон жак, затты өтөгөн жана кыймыл-аракетти ишке ашыруучу грамматикалык ээ – сен, ырыс, адам, турмуш.\n" +
                        "\n\n\nАл эми өз ара тең байланышта туруп, сүйлөмдүн баяндоочу менен бирдей катышта, грамматикалык бирдей байланышта болгон ээлер бир өңчөй ээ деп аталат. Мисалы: Үйдүн шаңы, үйдүн көркү, куту Асыл менен Акылай тура. Мында бирин- бири сыйлоо, бир үйдөгү ынтымак, адамгерчилик, чоң сезим бар эле. (Ш.С.)\n" +
                        "\nБир сүйлөмдө эки же андан көп бир өңчөй ээ бир баяндооч менен байланыш жасап, аны (баяндоочту) өздөрүнө ортоктошкон түрдө ээрчитет. Мисалы: Карыган чоң атасы, чоң энеси жана өз энеси, жаш карындашы калыптыр деген сүйлөмдү ээлерге карата: 1) Карыган чоң атасы калыптыр; 2) Чоң энеси калыптыр; 3) Жана өз энеси калыптыр; 4) Жаш карындаы калыптыр сыяктуу түргө келтирүүгө болот.\n" +
                        "Бир өңчөй ээ түр жагынан уңгу жана мүчөлүү болуп келет. Эгерде сүйлөм тизмегиндеги бир өңчөй ээни көптүк санга келтирүү талап кылынса, анда ал катарынан келген ээлердин бардыгы, же акыркы ээси гана көптүк санда колдонулат. Кээ бир учурда бир өңчөй ээни көптүк санга келтирүүдө, ар бир ээге мүчө уланып же болбосо, ар бир ээни санап-санап келип аркасына гана көптүк –лар мүчөсү уланат.\n" +
                        "Мисал келтирели: Сакалдуулардан ыйбаа кылган кыздар, кайнагаларынан качкан келиндер босогонун, туурдуктун капытынан шыкаалашууда. (К.Ж.) Кимдер? – кыздар, келиндер – ээлер. Бул жерлерде марал, тоо эчки, элик, каман, кыргоол, аркарлар бар. Эмнелер? – марал, тоо эчки, элик, каман, кыргоол, аркарлар – ээлер. Алардын ичинде Көбөгөн, Батма, Козубектер бар эле. Кимдер? – Көбөгөн, Батма, Козубектер – ээлер. Алардын тили, үрп-адаты Ажарга жат нерсе. Эмнеси? – тили, үрп-адаты.\n" +
                        "Бир өңчөй ээнин ар бири жекелик санда да, көптүк санда да колдонула берет: Алыстан дүрбүсүз карап турганыбызда кербендердин жайдактап койгон аттары, чөгөрүп койгон төөлөрү, топ-топ кылып жыйып койгон таңгактары өзүнчө эле бир корум таштай болуп көрүндү. Өзгөчө Касым менен Майсалбек куду эле Субанкулдун өзү, куюп койгондой, айныбайт. (Ч.А.)\n" +
                        "Сүйлөмдүн бир өңчөй ээлери бири-бири менен байламталар, байланыштыруучу санак интонация аркылуу байланышат: Акыйкатсыздык менен залимдик жакшылыкка алып барбайт. Куш да, мал да жем жеген жерине келет. (Накыл сөз) Анын жүрүш-турушу, мамилеси, мүнөзү мага жат боло баштады. \n" +
                        "Айрым учурларда жалпылоо маанисинде колдонулганда адам аттарынан уюшулган бир өңчөй ээлердин эң акыркысына көптүктүн – лар мүчөсү жалганат: Алардын ичинде Көбөгөн, Батма, Козубектер бар эле. Биримкул, Узак, Сарыбай, Токтобектер колхоз уюулгандан бери мал багып келе жатышат.\n" +
                        "Бир өңчөй ээлерге таандык байламталар айкалышып айтылышы мүмкүн: Силердин ишиңер да, кылык-жоругуңар да элге үлгү болгондой болсун. (А.Т.) Согуш чыккандан бери тетиги тоо да, мына бул талаа да, апасы да, Анаркүл да түгөнгүс сырга толо берди. (С.Ө.) Көгү деле, кызылы деле бар. (Т.С.)\n" +
                        "Ар дайым бир өңчөй ээлер баяндооч мүчөлөр менен байланышта болушат: Туйлап аккан тунук булактар, жайкалган көк шибер, миң түркүн гүлдөр, кубулжуп сайраган куштар акындын канча жылкы кайгы-черин жазды. (Ж.Б.) Тетири сөздөр, жаман жоруктар уйгак сыяктуу жабышчаак келет. (Т.С.)\n" +
                        "Жалпысынан, сүйлөмдүн бир өңчөй ээлеринин милдетин зат атооч, ат атооч, зат ордуна колдонулган сөздөр аткара берет.\n" +
                        "\n\n\nБир өңчөй баяндоочтор.\n\n" +
                        "Бир өңчөй мүчөлөрдү өтүүнүн алдында сүйлөмдүн баш жана айкындооч мүчөлөрүн кайталоо керек. Мына ушул себептүү, адегенде баяндооч жөнүндө сөз кылсак. Сүйлөм тутумунда грамматикалык жактан ээге багынып, анын ким, эмне экендигин, кыймыл-аракетин, мүнөзүн, абалын, сынын, сыпатын, сан-иретин билдирген баш мүчө баяндооч деп аталат. Мисалы: Анын максаты – жапайы айбандарды коргоо жана көбөйтүү. Адам өлөт, искусство эч качан өлбөйт.\n" +
                        "Негизинен баяндоочтун милдетин толук маанилүү сөз түркүмдөрү, ажырагыс сөз тизмектери жана фразеологизмдер аткарат. Ал эми бир өңчөй баяндоочтор синтаксистик бирдей милдет аткарышат, сүйлөмдүн ээси менен бирдей даражада катышта турушат. Мисалы: А сен Каныке, эстүү, токтоо, чыдамкай бол. (К.Ж.) Өзү эле жарыла жаздап араң турган жүрөгүм Ажарды эстеген сайын ыйлап да, сыздап да жатты. (К.Б.)\n" +
                        "Сүйлөм тизмегинде бир гана ээге же тизмектекен бир өңчөй ээлердин тобуна тиешелүү болуу менен байламта жана интонациялар аркылуу тутумдашкан мүчө бир өңчөй баяндооч болуп саналат.\n" +
                        "Жөнөкөй баяндоочтор сыяктуу эле бир өңчөй баяндоочтор да экиге бөлүнөт. Алар этиштик жана атоочтук баяндоочтор. Мисалы: Керексиз отун алынбайт да, жагылбайт. Мындагы баяндоочтор этиштик баяндоочтор. Бөлмөнүн ичи таза, ары жарык, ары кең. Каныбек бардыгын жек көрөт, айрыкча жек көргөндөрү – Айымбача менен Кылыч. (К.Ж.) Көч арасы да – ызы-чуу, күрү-күү. Келтирилген мисалдардагы бир өңчөй баяндоочтордун милдетин (таза, кен, жарык) – сын атоочтор, (Айымбача, Кылыч) – зат атоочтор, (ызы-чуу, күрү- күү) – тууранды сөздөр аткарып тургандыгына күбө болдук.\n" +
                        "Бир өңчөй этиштик жана атоочтук баяндоочтор сүйлөм ээсинин кыймыл-аракеттик, сапаттык, заттык, сандык жана башка белгилеринин бир мезгилдүүлүгүн, ырааттуулугун билдирет: Командир аябай ачуулуу жана каардуу. Шамбет менен Сергей ар качан бирге болушкан жана бирге эмгектенишкен. (Т.С.) Ал чалгынчынын өлтүрө албай жүргөндөрү – сен жана мен. (К.Ж.)\n" +
                        "Кээ бир учурда бир өңчөй этиштик баяндоочтор сүйлөм ээсинин биринен кийин бири болуучу ар башка мезгилдеги кыймыл- аракетин да билдирип калат:\n" +
                        "Орусбек баракты ачты да, окуй баштады. Анын үнү бирде басаңдап, бирде катуу чыгып жатты. (Т.С.)\n" +
                        "Бир өңчөй баяндоочторго кызматчы сөздөр, көмөкчү этиштер тизмектелип айтылып, алар бир өңчөй баяндоочтун ар биринен кийин кайталанып айтылса, анда ал бардык баяндоочко тиешелүү болуп, жалпылоо максатында колдонулат: Базар кайнаган учур экен: элдер кажы-кужу, күрү-күү болуп жатыптыр. (К.Ж.) Энеси баласынын жакы окугандыгына аябай толкунданып да жатты, сүйүнүп да жатты.\n" +
                        "Айрым учурларда кайра-кайра кайталоону четтетүү үчүн, же стилдик максатта сүйлөмдү кыска айтуу үчүн баяндоочту бир гана жолу колдонсо болот: Жаман кабар менен чабарман кемпир-чалга бир, чай куюп жаткан келинге бир карады. (С.Б.) Анын карааны бирде ойдон, бирде кырдан көрүнүп, алыстап кетти. \n" +
                        "\n\n\n2. Бир өңчөй аныктоочтор.\n\n" +
                        "Өз ара тең байланышта туруп, бирдей суроого жооп берип, аныктоочтук милдетти аткарат. Бир өңчөй аныктоочтордун ар бири аныкталып турган зат атооч менен түздөн-түз байланышат да, мааниси боюнча, бир эле заттын ар түрдүү белгисин, же бир топко кирген бирдей заттарды бири-биринен айырмалоочу белгилерин санап көрсөтөт. Мисалы: Ал - бир мүнөздүү, өжөр, эр көкүрөк, кайраттуу адам (Т.С.).Мында курсив менен терилген бир өңчөй аныктоочтордун ар бири «адам» деген аныкталгыч менен түздөн-түз байланышып турат жана алар ошол «адам» деген бир эле заттын ар түрдүү сапаттык белгисин билдирип турушат.\n" +
                        "Азыр жайлоодо көк, сары, кызыл, ак гцлдөр жайнап турган учур экен.\n" +
                        "Бул сүйлөмдөгү бир өңчөй аныктоочтор бир топко кирген бирдей заттарды (көп гүлдү) бири-биринен айырмалап турган ар түрдүү белгилерди санап көрсөтүп турат. Жөнөкөй аныктоочтордун милдетин кайсы сөз түркүмдөрү аткарса, бир өңчөй аныктоочтордун милдетин да ошолор аткарат.\n" +
                        "Бир өңчөй аныктоочтор да өз ара:\n" +
                        "1) санак интонация жана байланыштыруучу тыным аркылуу\n" +
                        "байланышат: Сабитахун Ажардын сулуу, сүйкүмдүү, ак жуумал өңүн көрдү (К.Б.). Арабыздан күн сайын жүздөгөн, миңдеген кишилерди бөлүп айдап кетишчү (Ж.Мав.). Иванды билген, тааныган кыргыздар ага бирдемесин бергенче шашчу (К.Б.). Менин сенден башка барар, таянар жерим калбай калды («КМ»).\n" +
                        "Илик жөндөмөдөгү сөздөр да бир өңчөй аныктооч болуп келет: Бул көз караш ак ниеттиктин, сүйүүнүн, күчтүн, ишеничтин учкундарына толгон (Н.Б.). Анын ысмы да ар качан баатырдыктын, каармандыктын жана эрдиктин өрнөгү болуп калмакчы. Бул булгаарыдан өтүктүн, маасынын кончу жасалат (С.С.).\n" +
                        "Кээде, мындай тузүлүштөгү бир өңчөй аныктоочторду ыкчамдатып айтуу максатында, илик жөндөмөнүн мүчөсү акыркы аныктоочко гана уланып айтылат: Ал жер-де топтолгон ата, эне, ак сакал, көк сакалдардын ортолорунда бир топ жаштар да отурушат (Т.С.). Ошентип, Жапар, Жамийланын бош убактылары өтө аз эле (К.Б.).\n" +
                        "2) Бир өңчөй аныктоочторду бири-бири менен жана, менен, да, ары, же, бирок байламталары да байланыштырат:\n" +
                        "а) Жетим менен жесирдин, бечара менен момундун намысы үчүн күрөшүү сендей жигиттердин гана колунан келет (К.Ж.). Өкмөт менен партиянын чечимдерин турмушка ашыруу үчүн кызуу эмгектенишүүдө.\n" +
                        "б) Жумушчулардын жана инженер-техник кызматкерлердин маданий турмушу жакшыртылды. Биринчи жана экинчи класстар экскурсияда.\n" +
                        "в) Жаштардын деги түрдүү уюмдардын да кандайча иштеп жатканын билип турушуңуз зарыл (Ш.Б.).\n" +
                        "г) Ал билимдүү, ары өткүр, ары уюштургуч жигит эле.\n" +
                        "д) Дүкөнгө барып, же кызыл, же көк материалдан 10 метр алып кел.е) Өткөн сегиз жыл Неля Николаевнанын турмушундагы түйшүктүү, татаал, бирок кубанычтуу жылдар болду.\n" +
                        "\n\n\nБир өңчөй толуктоочтор.\n\n" +
                        "Булар да өз ара тең байланышта туруп, сүйлөмдүн бир мүчөсү менен бирдей грамматикалык формада байланышат, бирдей суроого жооп берип, бир өңчөй толуктоочтук милдетти аткарат: Мисалы: Спектакль жаштардын ак ниеттүүлүгүн, тазалыгын, терең сезимин, ойлоосун кеңири берген («СК»).\n" +
                        "Бул сүйлөмдөгү бир өңчөй толуктоочтор өз ара тең укукта, тең байланышта; алар-дын ар бири «берген» деген баяндооч менен түздөн- түз байланышып, баары тең эмнени? деген бир суроого жооп берип, бир өңчөй толуктоочтук милдетти аткарып турат.\n" +
                        "Бир өңчөй толуктоочторго да башка айкындоочтор таандык болуп айтылып, жайылма түрдө да кездешет: Даңктуу Родинабызды, эмгекчи элибизди сүйгөндүгүм мени шыктандырды. Зууракан Москвага баргандыгын, Кремлге киргенин, Калининдин колунан Ленин орденин алганын элестетти (К.Б.). Айрым учурларда сүйлөм ичиндеги бир өңчөй толуктоочторду ыкчамдатып айтуу максатында аларга жалганган жөндөмө мүчө түшүрүлүп да айтылат, же эң аяккысында гана сакталат. Бирок алардын кайсы жөндөмөдө экендиги эң кийинки толуктоочтун аягындагы мүчөдөн же сүйлөмдүн жалпы маанисинен белгилүү болуп турат: Алма, өрук, алчасын көрсөк, көзүбүз кызарчу (Т.С.). Ырас, майданга жылуу кийим, акча, заем, эт, сут, дан, май берип жатабыз (Т.С.). Бул акыркы сүйлөмдөгү бир өңчөй сөздөрдүн табыш жөндөмөдө экендиги сүйлөмдүн маанисинен белгилүү болуп турат. Бир өңчөй толуктоочтор да бири-бири менен:\n" +
                        "1) Санак интонация жана байланыштыргыч тыным аркылуу байланышат. Жөнөкөй толуктоочтун милдетин кайсы сөз түрүкүмү аткарса, бир өңчөй толуктоочтун милдетин да ошолор аткарат. Мисалы: Ал кызмат өтөгөн полк Донбассты, Херсонду, Одессаны, Варшаваны бошотууга катышты («СК»). Мен көргөнүмдү, укканымды айтам (К.К.). Арыгына-торугуна, чыгымына, жогуна жооптуу мен беремин (Т.С.).\n" +
                        "2)Байламталар байланыштырат (көбүнчө да, менен, жана, кээде байламталары):\n" +
                        "а) Кишилерге да, унаага да, сокого да, шайманга да сен жооп бересиң (Ч.А.). Мен ишкананы да, андагы эмгекти да өзүмдүкү деп эсептеймин (С.С.). Өзүнө да, чарбага да кас күтцп алган жокпу? (Ч.А.). б) Ошол түнү алар Ажар менен Каныбекти качырышты (К.Ж.). Экөө тең Айчүрөк менен Акунканга ийилип салам беришти (Ж.Т.). Мергенчинин бул кейпи Ажар менен Каныбек үчүн табышмак эле (К.Ж.).\n" +
                        "в) Зыядахан бир топ кыз-келин жана улгайган кемпирлерди ээрчитип келет (К.Ж.). Алардын демилгеси мактоого жана колдоого татыктуу болду. Мурда Россияда зыгырдын буласынан жана кендирден токулган кийимдерди кийишчү.\n" +
                        "г) Ал азыр же малга, же жанга зыянын тийгизген жери жок (Т.С.). Ажарга өлүүдөн, же болбосо бир жакка тентип кетүүдөн башка эч бир айла калбай калды (К.Б.).\n" +
                        "\n\n\nБир өңчөй бышыктоочтор.\n\n" +
                        "Булар өз ара тең байланышта келип, сүйлөмдүн бир мүчөсү менен байланышат; баары бирдей маанини (орундук, мезгилдик, же сыпаттык ж.б.) туюндуруп, бирдей суроого жооп берип, бир өңчөй бышыктоочтук милдет аткарат. Мисалы: Музейлерге, театрларга бардык, метрону көрдүк. Эгин жакшы, тегиз суугарылыптыр. Мында биринчи сүйлөмдөгү «музейлерге, театрларга» дегендер өз ара тең байланышта, экөө тең сүйлөмдүн «бардык» деген баяндоочу менен бирдей грамматикалык формада түздө-түз байланышкан: экөө тең «кайда»? деген бир суроого жооп берип, бир өңчөй орун бышыктоочтук милдетти аткарып турат. Экинчи сүйлөмдөгү «жакшы», «тегиз» деген сөздөр «кандай»? деген бир суроого жооп берип, бир өңчөй сыпат бышыктоочтук милдетти аткарып турат.\n" +
                        "Жөнөкөй бышыктоочтун милдетин кайсы сөз түркүмдөрү аткарса, бир өңчөй бышыктоочтордун милдетин да ошол эле сөз түркүмдөрү аткарат. Айрым учурларда бир өңчөй бышыктоочторго да тиешелүү айкындоочтор таандык болуп айтылып, жайылма формада колдонулат: Экспедиция менен биз Анархайдын түпкүрүнө, Балхаштын жээгине кеттик (Ч.А.). Ошонусу менен ал азыр баштагыдан сулуураак, баштагыдан жакыныраак көрүндү (Ч.А.).\n" +
                        "Бир өңчөй бышыктоочтор бири-бири менен:\n" +
                        "1) санак интонация, байланыштыргыч тыным аркылуу\n" +
                        "байланышат: Метрейдин Караколдо, Түптө бирден үйлөрү болчу (М.Э.). Шарше дөгүрсүнүп, менменсинип кербезденет (Т .С.). Коллектив актуалдуу коомдук теманы өз күчү, өз мүмкүндүгү менен сахнада чечип көрсөтүүгө аракет кылган («СК»).\n" +
                        "2) Байламталардын жардамы менен байланышат: а) Компас менен токойдун ичинде да, деңиздин үстүндө да адашууга болбойт («КП»). Азыр мал чарбада да, тоодо да көп. Үй ээси күн да, түн да сыртта (Т.С.).\n" +
                        "б) Сапарбайдын сөзү чечкиндүү, ары маңыздуу болду. Жайкы каникулду окуучулар ары көңүлдүү, ары максаттуу өткөрүштү («ЛЖ»).\n" +
                        "в) Бүгүн же эртең Саадаттар айылга кол салат деген имиш бар (Т.С.). Үч кой артына же алдына кете албай, жардын боорундагы тектирчеде араң эле турушкан экен (К.Ж.).\n" +
                        "г) Термечиков кыска, бирок маңыздуу сүйлөдү.\n" +
                        "Бир өңчөй орун бышыктоочтор: Областтын бардык кыштактарында, шаарларында, дыйкан чарбаларында өкмөттүн чечимдери окулуп жатат («КТ»). Жакында, алыста, кайда, кайсы жерде жүрсөк да, Родинаны эстейбиз (К.Б.).\n" +
                        "Бир өңчөй мезгил бышыктоочтор: Айрым жаштар күндүз да, кечкисин да иштешет («ЛЖ»). Бул китептериң окуганда да, иштегенде да керек болот (С.С.). Бир өңчөй сыпат бышыктоочтор: Ошол замат буттарын тыбырчылатып, бакырып ыйлачу (Т .С.). Мына эми жөтөлүп, тердеп, онтоп, керсары тартып төшөктө жатат (К.Б.).\n" +
                        "Бир өңчөй максат бышыктооч: Далай ак ниет адамдар биз үчүн, биздин таалайыбыз үчүн курман болушту (К.Б.).\n" +
                        "Бир өңчөй себеп, сан-өлчөм бышыктоочтор тилибизде сейрек учурайт: Керим жокчулуктан, ачкачылыктан байларга малай да болду.\n" +
                        "\n\n\n3. Бир өңчөй мүчөлүү сүйлөмдөгү жалпылагыч сөз жана анын тыныш белгиси. Бир өңчөй мүчөлөрдүн маанисин жалпылап жана аларды бириктирип, чечмелеген сөздөрдү жалпылагыч сөздөр дейбиз.\n" +
                        "Жалпылагыч сөздөр сүйлөмдүн бир өңчөй мүчөсүнө тиешелүү болуп айтылат да, аларды топтоштуруп, жалпылап, бириктирип турат. Сүйлөмдө бирөңчөй мүчөлөр кандай синтаксистик милдет аткарса, ага тиешелүү болуп келген жалпылагыч сөз да ошондой эле милдет аткарат. Жалпылагыч сөз бир өңчөй мүчөлөрдөн мурда да, кийин да келет. Мисалы: Абдыбек менен Узак – экөө Жапардыкынан кайтканда, саат он бирден өткөн. Кечээ, бүгүн, эртеңдер – бардыгы бири-биринен айырмалуу.\n" +
                        "Жалпылагыч сөздөр чыгыш теги жагынан окшош болгон түшүнүктөрдү жалпылап көрсөтөт. Ошондуктан алардын милдетин, негизинен, булар, алар, бүт, бүткүл, баары деген ат атоочтор, жандама сан атоочтор жана айрым жалпылама маанидеги сөздөр аткарат. Жалпылагыч сөздөргө тыныш белгиси төмөнкүдөй коюлат:\n" +
                        "Жалпылагыч сөз бир өңчөй мүчөлөрдөн мурда келсе, өзүнөн кийин кош чекит коюлат. Мисалы: Баарын: азапты, кыйноону, теңсиздикти, кордукту, жокчулукту баыбыздан кечирсек да, пенде бойдон кала берет экенбиз го!\n" +
                        "Жалпылагыч сөз бир өңчөй мүчөлөрдөн кийин келсе, өзүнөн мурда сызыкча коюлат. Мисалы: Кийимибиз жука болгондуктан, Беккул, Сапарбек жана мен – үчөөбүз калчылдай баштадык. (М.Э.)");
                break;
            case 6:
                openSecond("Тема: Чакчыл жана атоочтук түрмөктөр","\n" +
                        "Маселелер:\n" +
                        "www.bizdin.kg\n" +
                        "1. Чакчыл түрмөктөр.\n" +
                        "2. Атоочтук түрмөктөр.\n" +
                        "\n1. Көпчүлүк учурда тилибизде заттардын, көрүнүштүн, кубулуштун жана кыймыл-аракеттин ар түрдүү татаал белгилерин жөнөкөй бир сөз менен толук чагылдырып берүү мүмкүн болбой калат. Мына ушундай учурларда андай белгилерди бир канча сөздүн айкашы аркылуу берүүгө туура келет. Ушундай кырдаалга байланыштуу, башка түрк тилдериндегидей эле, кыргыз тилиндеги сүйлөмдөрдүн тутумунда да бир нече сөздүн тизмегинен уюшулуп, сүйлөмдүн тутумдаш бир мүчөсүнүн милдетин акаруучу тутумдуу сөз айкаштары да кеңири учурайт. Андай сөз айкаштары (сөз тизмеги) дайыма чакчыл жана атоочтук этиштердин негизинде уюшулат.\n" +
                        "Тактап айтканда, чакчыл же атоочтук алды жагында өзү менен башкаруу, ыкташуу жолу аркылуу байланышып айтылган бир нече сөздүн турушун талап кылат; экинчи жагынан, ал чакчыл же атоочтук менен түздөн-түз байланышып турган сөздөрдүн өзү менен байланышып келген башка сөздөрдүн да болушу мүмкүн. Натыйжада, ошол чакчыл же атоочтуктун алкагында бири-бири менен тыгыз байланышкан бир канча сөздүн бирдиктүү бир түрмөгү (тизмеги) уюшулат: Мисалы: Кез-кез арабаларга карап коюп, Темир да өз жолу менен келе жаткан эле (Т.С;). Энесинин сырын жакшы билген Абыл сөздү узартып отурган жок (С.Ө.).\n" +
                        "Мында биринчи сүйлөмдөгү «Кез-кез арабаларга карап коюп» деген сөз тизме- гинин уюшулушуна негиз болгон сөз - «карап коюп» деген чакчыл. Бул чакчылга «арабаларга» деген сөз башкарылып, «кез- кез» деген сөз ыкташып байланышып келип, бирдиктүү сөз тизмеги уюшулду. Экинчи сүйлөмдөгү «энесинин сырын жакшы билген» деген сөз тизмегинин түзүлүшүнө негиз болгон сөз - «билген» деген атоочтук, ага «жакшы» деген сөз ыкт-шып, «сырын» деген сөз башкарылып жана бул сөзгө «энесинин» деген сөз таандык жолу аркылуу байланышып келип, сөз тизмеги түзүлгөн.\n" +
                        "Бул сүйлөмдөргө синтаксистик талдоо жүргүзгөндө жогорку сөз тизмектери ту-тумундагы сөздөрү менен бирдиктүү түрдө туруп, кантип? кайсы? деген сыякуу бир суроого жооп берип, тутумдаш бышыктоочтун жана тутумдаш аныктоочтун милдетин аткарышат:\n" +
                        "- Темир кантип келе жаткан?\n" +
                        "- «Кез-кез арабаларга карап коюп» (тутумдаш сыпат бышыктооч).\n" +
                        "- Кайсы Абыл?\n" +
                        "- «Энесинин сырын жакшы билген» (тутумдаш аныктооч).\n" +
                        "Бул өңдүү сөз тизмектеринин уюшулушуна негиз болгон сөз чакчыл этиш жана атоочтук болгондуктан, алар чакчыл түрмөктөр жана атоочтук түрмөктөр деген термин менен аталат.\n" +
                        "\n\n\nЧакчыл түрмөктөр.\n\n" +
                        "Чакчыл түрмөктөр, жогоруда белгиленгендей, бир нече сөздүн синтаксистик байланышынан уюшулат. Андай сөз тизмектеринин түзүлүшүндө негизги ролду дайыма чакчыл этиш аткарат, анткени ал айрым учурларда айтылуучу ойдун мазмунуна ылайык өзү менен башкаруу, ыкташуу жолдору менен байланышып келген бир нече сөздүн бирге айтылышын талап кылат, экинчи жагынан, ал сөздөрдүн өзү менен байланышып айтылган башка сөздөрдүн да болушу мүмкүн, ушунун натыйжасында түзүлүшү жагынан бирдиктүү келген сөз түрмөгү уюшулат.\n" +
                        "Мисалы: Ичигин желбегей жамынып, Султан эшикке чыкты. Акматтын шашып келе жатканын көрө коюп, Зуура эмнегедир сестее түштү. Мында биринчи сүйлөмдө «жамынып» деген чакчылга «желбегей» деген сөз ыкташып, «ичигин» деген сөз башкарылып байланышып келип, бирдиктүү сөз тизмеги, чакчыл түрмөк, уюшулган. Экинчи сүйлөмдө болсо, «көрө коюп» деген чакчыл «келе жатканын» деген сөздү башкарып байланышып, ал эми «Акматтын» деген сөз «келе жатканын» деген сөз менен таандык байланыш аркылуу, «шашып» деген сөз ошол эле «келе жатканын» деген сөз менен ыкташуу жолу аркылуу байланышып келип, чакчыл түрмөк уюшулган.\n" +
                        "Чакчыл түрмөктөр дайыма тутумундагы сөздөрү менен бирдикте туруп, сүйлөмдүн этиштик баяндоочу менен же этиш сөздөн турган башка мүчөлөрү менен байланышта айтылат да, алар билдирген кыймыл-аракеттин мүнөзүн, абалын, кандайча аткарылгандыгын билдирет; бир суроого жооп берип, тутумдаш бышыктоочтун милдетин аткарат: Коштоо атын жетелеп, Курман илгери жөнөдү (А.У.). Бул сүйлөмдөгү «Коштоо атын жетелеп» деген чакчыл түрмөк маани жагынан сүйлөмдүн «жөнөдү» деген этиштик баяндоочу менен байланыштуу айтылып, ал кыймыл-аракеттин кандай абалда аткарылгандыгын билдирип турат; бирдиктүү түрдө туруп, кантип? деген суроого жооп берип, тутумдаш сыпат бышыктоочтук милдетти аткарып турат.\n" +
                        "Шейшебин тээп таштап, борбоюп ук-тап жаткан балага Амантур суктана карады (А.У.).Бул сүйлөмдө болсо, курсив менен терилген чакчыл түрмөк «уктап жаткан» деген атоочтук этиштен турган аныктооч менен байланышта айтылып, ал билдирген абалдын белгисин, мүнөзүн аныктап көрсөтүп турат - тутумдаш сыпат бышыктооч.\n" +
                        "\n\nБир канча сөздүн чакчыл менен байланышып айтылышы аркылуу түзүлгөн бирдиктүү тизмеги чакчыл түрмөк деп аталат.\n" +
                        "Кыргыз тилинде чакчыл түрмөктөр көбүнчө сүйлөмдүн башында, кээде аягында, ортосунда келет. Оозеки кепте сүйлөмдүн башка бөлүгүнөн кыска тыным аркылуу бөлүнүп айтылат; сүйлөмдүн башында жана аягында келген учурларда алардын чакчыл түрмөк экендиги ачык даана билинип турат.\n" +
                        "Чакчыл түрмөктөр сүйлөмдүн башында келсе,өзүнөн кийин, аягында келсе, өзүнөн мурда үтүр коюлат: Болотбектин келе жатканын көрүп, трактористтердин бригадири Чумаков бери басты (К.Ж.). -Жоокер болгум келет! - деди Жапар, көз жашын кылгыртып (К.Б.).\n" +
                        "Чакчыл түрмөк сүйлөм ортосунда келген убактарда, анын башталышы кыска тыным (па уза) менен ачык бөлүнүп чектелип турбайт, ошол себептүү алардын чакчыл түрмөк экендиги сүйлөмдүн башына же аягына келген түрмөктөр сыяктуу даана билинип турбайт. Чакчыл түрмөктөр сүйлөм ортосунда келгенде, эки жагынан үтүр ажыратылып жазылат: Андан кийин ал, анда-санда гана аялына карап коюп, унчукпай отура берди (С.Б.). Жапар, аштыгын алга карай сундуруп, жоокерлердин эң алдында кетип бара жатат (К.Б.).\n" +
                        "Кыргыз тилинде чакчыл түрмөктөрдүн уюшулушуна көбүнчө -ып формасындагы, аз да болсо -а, -й-ганча/ /-гыча, -ганы, -май-ынча формасындагы чакчылдар катышат. Чакчыл түрмөктөр мааниси жагынан көбүнчө сүйлөмдүн этиштик баяндоочу менен, кээде этиш сөздөн турган башка мүчөсү менен байланыштуу айтылып, аны ар түрдүү жагынан мүнөздөп турат: сүйлөм ичинде берген маанисине карай сыпат чакчыл түрмөк, мезгил чакчыл түрмөк, себеп чакчыл түрмөк, максат чакчыл түрмөк, салыштырма чакчыл түрмөк, карама-каршы чакчыл түрмөк болуп 6 түргө бөлүнөт.\n" +
                        "Сыпат чакчыл түрмөк.\n" +
                        "Мында чакчыл түрмөктү уюштурган сөз тизмеги бирдиктүү түрдө туруп, сүй-лөмдүн этиштик баяндоочу билдирген кый-мыл- аракеттин белгисин, мүнөзүн, кандай-ча аткарылгандыгын көрсөтөт: кантип? деген суроого жооп берип, сүйлөм ичинде тутумдаш сыпат бышыктоочтун милдетин аткарат.\n" +
                        "1) Сыпат чакчыл түрмөктү көбүнчө -ып формасындагы чакчылдар уюштурат: Кабагын карыш түйүп, ал да заар тилин агытты (К.К.).\n" +
                        "2) Сыпат чакчыл түрмөк -ган+сы+ып формасында уюшулат: Эки көзүнөн ажалдын отун жандыргансып, кара буура жакын келип калды (К.Ж.).\n" +
                        "3) ... деген +си-ип түрүндө да кездешет: Мырзабек, мунун байкагычын көрчү деген-сип, кызды таңдана тиктеди (Т.Аб.)\n" +
                        "4) Кээде -а формасындагы чакчылдар уюштурат: - Эчтеке эмес, - деди Батый да, Сурмакандын жанын күйгүзө (Т.С.). 5) Айрым учурларда -бай (ба+й) формасындагы чакчылдар уюштурат: Баягыдай сумсайбай, кишилер үй-үйлөрүнө көңүлдүү тарашты (Т.С.).\n" +
                        "Кээде сүйлөмдүн этиштик баяндоочу менен бир эмес, бир нече чакчыл түрмөк байланышып келиши мүмкүн: Анархандын тизесине башын коюп, маңдайынан канын агызып, Каныбек жатат (К.Ж.). Айна, башын жаздыктан көтөрүп, демин ичине алып, тынып калды (Т.С.).\n" +
                        "\n\n\nСебеп чакчыл түрмөк.\n\n" +
                        "Чакчыл түрмөктүн бул түрү баяндооч билдирген кыймыл- аракеттин аткарылыш же аткарылбай калыш себебин билдирет; эмне үчүн? эмне себептүү? эмнеликтен? деген суроолорго жооп берип, тутумдаш себеп бышыктоочтун милдетин аткарат.\n" +
                        "1) Себеп чакчыл түрмөктү да көбүнчө -ып формасындагы чакчылдар уюштурат: Анын кызыксынбагандыгын байкап, Акбар өзү ачылып эчтеме айткан жок (А.У.). Анын иреңинин бузулушунан чочуп, ордуман учуп турдум (К.К.).\n" +
                        "2) Чакчыл формадагы «деп» жардамчы этишинин катышуусу менен уюшулат: Сени келбей калабы деп, биз абдан тынчсыздандык эле.\n" +
                        "3) Терс маанидеги -бай формасындагы чакчылдар түзөт: Туу жондун сууктугуна чыдап тура албай, ал кайта бастырып жылкы четине келди (А.У.).\n" +
                        "\n\n\nМезгил чакчыл түрмөк.\n\n" +
                        "Мындай маанидеги чакчыл түрмөктөр бирдиктүү түрдө туруп, баяндооч билдирген кыймыл-аракеттин аткарылыш мезгилин көрсөтөт; сүйлөм ичинде качан? качантан бери? качанга чейин? деген суроолорго жооп берип, тутумдаш мезгил бышыктоочтук милдетти аткарат. Мезгил чакчыл түрмөктөр да, негизинен:\n" +
                        "1) -ып формасындагы чакчылдардын негизинде уюшулат. Мындай учурда чакчыл түрмөк туюндурган кыймыл сүйлөмдүн баяндоочу билдирген кыймыл-аракеттен алдын, мурунураак болгондугун билдирет: Ошентип кыштакты кызуу кыймылга келтирип коюп, Амантур менен Акбар жайытта жаткан малдарга барышты (А.У.). Эгерде чакчыл учур чактык мааниде болсо, анда чакчыл түрмөк билдирген кыймыл-аракет менен сүйлөмдүн баяндоочу билдирген кыймыл-аракет бир мезгилде болгондугун көрсөтөт: Курулуштан келе жатып, Жапар кызыл үйгө кирди (К.Б.).\n" +
                        "2) Кээде -а формасындагы чакчылдар уюштурат. Мында чакчыл түрмөктөгү кыймыл-аракет аяктары менен эле, сүйлөмдүн баяндоочу билдирген кыймыл-аракет удаалаш башталгандыгын билдирет: Үйгө жете, мен Султанга кат жаза баштадым.\n" +
                        "3) -ганча//гыча формасындагы чакчылдар уюштурат. Мындай чакчыл түрмөктөр:\n" +
                        "а) Эгерде сүйлөмдүн баяндоочу созулуңку эмес, кыска мөөнөттүү кыймыл-аракетти бидирсе, чакчыл түрмөктөгү кыймыл аякталганча, сүйлөмдүн баяндоочу билдирген кыймыл-аракет аткарылып калгандыгын көрсөтөт: Бул сөздөрүн айтып болгончо, Чаргын өзү да узай берди (Т.С.). Ата-энелерине жеткенче, алар тойпоңдошуп күрөшүп да жиберишет (А.Т.).\n" +
                        "б) Эгерде сүйлөмдүн баяндоочу созулуңку кыймыл-аракетти билдирсе, анда баяндооч бидирген кыймыл-аракет чакчыл түрмөк билдирген иш-аракет башталууга чейин созуларын туюндурат: Капчыгайдын түзөңчөсүнө жеткиче, Акбардын эки көзү чакчайды (А.У .).\n" +
                        "4) Мезгил чакчыл түрмөктү -ганы формасындагы чакчылдар уюштурат. Мындай түзүлүштөгү түрмөктөр сүйлөмдүн баяндоочу билдирген кыймыл-аракеттин кайсы убактан бери болуп жатканын же болгонун билдирет: Армиядан келгени, ал «Кеңеш» чарбасында агроном болуп иштейт.\n" +
                        "5) -майынча мүчөсү уланган чакчылдар уюштурат. Мындай учурда чакчыл түрмөк-төгү кыймыл-аракет аткарылмайынча, баяндооч билдирген кыймыл-аракет да аткарылбай тургандыгын көрсөтөт, сүйлөмдүн баяндоочу дайыма терс формада, келер чакта айтылат: Менин сөзүмдү угуп болмоюнча, силер эч жакка жылбайсыңар («Чайка»). Бул жумушту бүтүрмөйүнчө, мен ал тапшырмаңды аткара албайм. Мындай формадагы чакчыл түрмөктөрдө мезгилдик маани менен бир аз шарттуулук мааниси да бар экендиги сезилип турат.\n" +
                        "6) Айрым учурларда мезгил чакчыл түрмөктү терс (-бай) формадагы чакчыл да уюштурат. Булар да, -майынча формасын-дагы түрмөк сыяктанып, чакчыл түрмөктөгү кыймыл аткарылмайынча, баяндооч билдирген кыймыл да аткарылбай тургандыгын баяндайт, бир аз шарттык маани да бар экендиги байкалып турат: Бул ишти бүтүрбөй, мен үйгө кете албайм. Эмгек тартибин чыңдабай, биз анын өндүрүмдүүлүгүн жогорулата албайбыз.\n" +
                        "\n\n\nМаксат чакчыл түрмөк.\n\n" +
                        "Максат чакчыл түрмөктү түзгөн сөз тизмеги бирдиктүү түрдө туруп, сүйлөмдүн баяндоочу туюндурган кыймыл-аракеттин максатын билдирет; эмнеге? эмне үчүн? деген сыяктуу суроолорго жооп берип, тутумдаш максат бышыктоочтук милдетти аткарат.\n" +
                        "1) Максат чакчыл түрмөктөр -мак, -мак-чы мүчөсү уланган этиштерден кийин «болуп» деген чакчылдын кошулуп айтылышы менен түзүлөт: Шаарга жөнөмөкчү болуп, Айдар күндөгүсүнөн эрте турду.\n" +
                        "2) Каалоо ыңгайдын I жагынын -айын, -алы мүчөлөрү уланган этиштен кийин -ып формасындагы «де» (деп) жардамчы этишинин кошулуп айтылышы менен да түзүлөт: Зайыбым менен акылдашайын деп, Керимахун короосуна кирип кетти (К.Ж.). Малчылардын ал-абалын билели деп, башкарма менен зоотехник таң заардан жайлоого жөнөштү.\n" +
                        "3) Келер чактагы этиштен кийин «деп» айкашып айтылып да, максат чакчыл түрмөктүн уюшулушуна негиз болот: Баланы өзүнө көндүрөм деп, Нуржан аны далай ирет койнуна да алып жатты (Т.С.).\n" +
                        "4) Аягына I жактын мүчөсү уланган -са формасындагы шарттуу ыңгайдан кийин «деп» айкашып айтылып да, максат чакчыл түрмөк түзүлөт: Планды ашыра орундатсак деп, биз жумушчулардын арасында мелдешти кеңири жайылттык. 6) Максат чакчыл түрүмөктү кээде -ып формасындагы чакчылдар да уюштуруп калат: Султанды издеп, мен көчөгө чыккан элем.\n" +
                        "Жогоруда көрсөтүлгөн максат чакчыл түрмөктөрдө бир аз себептик маани да бар экендиги байкалып турат, бирок, канткен менен да, максаттык маани басымдуураак кылат.\n" +
                        "\n\n\nКайчылаш чакчыл түрмөк.\n\n" +
                        "Кайчылаш чакчыл түрмөк маани жагынан сүйлөмдүн баяндоочу билдирген кыймыл-аракетке карама-каршы келет жана баяндооч туюндурган иш-аракет чакчыл түрмөктөгү аракетке кайчы, карама-каршы аяктайт.\n" +
                        "Кайчылаш маанидеги түрмөктөр тилибизде өтө аз кездешет; негизинен төмөнкү-дөй жолдор менен уюшулат:\n" +
                        "1) -ып+ -ып формасындагы татаал чакчылдар уюштурат: Белеттеги суроолорго түшүнүп туруп, мен жакшы жооп бере албадым.\n" +
                        "2) -а+ -ып формасындагы татаал чакчылдар түзөт: Уулунун бүгүн келбесин биле туруп, энеси түн ортосуна чейин күтүп олтура берди.\n" +
                        "3) -ып мүчөсү уланган жөнөкөй чакчылдар да уюштура берет, кээде андан кийин «да» бөлүкчөсү кошулуп айтылат: Отту ушунчалык көп жагып да, Зуура үйүн ысыта албады.\n" +
                        "\n\n\nСалыштырма чакчыл түрмөк.\n\n" +
                        "Мында чакчыл түрмөк туюндурган иш-аракет сүйлөмдүн баяндоочу билдирген кыймыл-аракет менен салыштырылып берилип, алардын бири-биринен айырмасы, артыкчылыгы көрсөтүлөт. Чакчыл түрмөктүн бул түрү да тилибизде өтө сейрек учурайт.\n" +
                        "Салыштырма чакчыл түрмөктү, негизинен, -ганча, -гыча формасындагы чакчылдар уюштурат: Сенин жамандыгыңды көргүчө, мен жердин жети катарына кирип кетейин (А.Т.). Салыштырма чакчыл түрмөк катышкан жөнөкөй сүйлөмдөрдү салыштырма багыныңкылуу татаал сүйлөмдөрдөн ажырата билүү керек.\n" +
                        "\n\n\n2. Атоочтук түрмөктөр.\n\n" +
                        "Бир нече сөздүн атоочтук менен грамматикалык жана маанилик жактан бекем тутумдашып байланышы аркылуу түзүлгөн бирдиктүү тизмеги атоочтук түрмөктөр деп аталат.\n" +
                        "Атоочтук түрмөктөр тутумундагы сөздөрү менен тыгыз бирдикте туруп, дайыма өзүнөн кийинки аныкталгычы менен ыкташып, ага кошумча мүнөздөмө берип турат жана ошол аныкталып турган сөзү менен бирдикте бир ритмико-интонациялык бирдикти (синтагматикалык бирдикти)түзөт. Мисалы: Кулагына чейин кызара түшкөн Сапарбай үчүн бир аз көтөрүңкү чыгарды (Т.С.).\n" +
                        "Бул сүйлөмдөгү «Кулагына чейин кызара түшкөн» деген атоочтук түрмөк тутумундагы сөздөрү менен бирдикте туруп, өзүнөн кийинки «Сапарбай» деген заттын (аныкталгычтын) ошол учурдагы абалын аныктап турат жана ошол аныкталгычы менен бирге синтагматикалык бирдикти (ритмико-интонациялык бирдикти) түзөт (кулагына чейин кызара түшкөн Сапарбай).\n" +
                        "Атоочтук түрмөктөр, негизинен, эки түрдүү түзүлүштө кездешет:\n" +
                        "1) Сыртынан караганда, айрым атоочтук түрмөктөрдүн тутумундагы бир сөзү грамматикалык ээ сыяктанып, экинчиси баяндооч сыяктуу болуп көрүнөт. Мисалы: Баштагы сулуулугу өчпөгөн Найман эне алыс сапар жолго даяр (Ч.А.).\n" +
                        "Бул сүйлөмдүн тутумундагы «Баштагы сулуулугу өчпөгөн» деген атоочтук түрмөктү сыртынан алып караганда, анын тутумундагы «сулуулугу» деген сөз ээ сыяктанып, «өчпөгөн» дегени баяндооч сыяктанып көрүнөт. Бирок ошондой болсо да бу тизмек сүйлөмдүк сапатка жетише албайт. Себеби:\n" +
                        "а) сүйлөм үчүн мүнөздүү болгон баяндоо интонациясына ээ эмес;\n" +
                        "б) ээ, баяндооч сыяктуу болуп көрүнгөн тигил эки сөз предикативдик катышты түзө албайт;\n" +
                        "в) ошол себептүү бул сөз тизмеги кандайдыр бир бүткөн ойду түшүндүрө албайт. Ал өзү тутумунда турган жөнөкөй сүйлөмдүн тутумдук бир бөлүгү гана болуп эсептелет. Бул түрмөк тутумундагы сөздөрү менен тыгыз бирдикте туруп кайсы? деген суроого жооп берип, «Найман эненин» сапаттык белгисин билдирип, тутумдаш аныктоочтун милдетин аткарып турат.\n" +
                        "2) Кээ бир атоочтук түрмөктөрдүн тутумунда жогоркудай грамматикалык ээ сыяктанып көрүнгөн мүчөсү болбойт. Анын акыркы компоненти атоочтуктардан болот да, ал өзүнүн алдындагы грамматикалык жактан жана мааниси боюнча өзү менен тыгыз байланышып турган сөздөрү менен бирдикте туруп, бир суроого жооп берип, сүйлөмдүн тутумдаш мүчөсүнүн милдетин аткарат: «Баятан көпчүлүктөн кеп-сөз күткөн Сапарбай экинчи ирет отургандарга кайрылды» (Т.С.). Бул жөнөкөй сүйлөмдүн тутумундагы «Баятан көпчүлүктөн кеп-сөз күткөн» деген атоочтук түрүмөктө грамматикалык ээ сыяктанып көрүнгөн мүчөсү жок. Мында «күткөн» деген атоочтук менен алдындагы сөздөр башкарылып, ыкташып байланышып келип, синтаксистик жактан бирдиктүү сөз түрмөгү уюшулган. Бул түрмөк ошол тутумундагы сөздөрү менен биримдикте туруп өзүнөн кийинки «Сапарбай» деген аныкталгычы менен ыкташып байланышып, кайсы? деген суроого жооп берип, тутумдаш аныктоочтун милдетин аткарып турат.\n" +
                        "Кыргыз тилиндеги атоочтук түрмөктөр:\n" +
                        "а) -ган формасындагы атоочтуктардын негизинде уюшулат: Ал жал-куйругу төгүлгөн чоң күрөң атты минип келди (К.Б.). Ошолордун баарын тарткан поезд нөшөргө жуунуп кете берди (Ч.А.).\n" +
                        "б) -ар(-бас) формасындагы атоочтуктардын негизинде уюшулат: Бүркүт уялаар аска бийик болот. Алар бири-биринен эчтеке жашырбас курбулар эле.\n" +
                        "в) -оочу, -уучу формасындагы атоочтуктардын негизинде уюшулат: Ат жалын тартып аттануучу мезгил жетти. Ж-йынга баруучу адамдар бүт келишти.\n" +
                        "г) -а(-й)+элек формасындагы атоочтуктардын негизинде уюшулат: Жини таркай элек Каранар айрылып калган үйүрүнө кайра барууга жулкунду (Ч.А.). Чөбү чабыла элек тайпак бетке койду жайып жибердик.\n" +
                        "Атоочтук түрмөктөр синтаксистик функциясына карай сүйлөм ичинде тутумдаш ээлик, тутумдаш баяндоочтук, тутумдаш аныктоочтук, тутумдаш толуктоочтук жана тутумдаш бышыктоочтук милдетти аткарат.\n" +
                        "Атоочтук түрмөктөрдөн турган тутумдаш аныктоочтор да сүйлөмдүн ар түрдүү мүчөсүн аныктап келе берет:\n" +
                        "1) Сүйлөмдүн ээсин аныктайт: Күрөө тамыры көбө түшкөн Калпакбай уулу үнүн барынча чыгарды (Т.С.). Атка бергис кунан бар, кызга бергис жубан бар (макал).\n" +
                        "2) Толуктоочту аныктайт: Ар бир жоо-кер Ата мекенге берер антын ойлошту. Алар бут кийим сатуучу дүкөндөн өтүп, оңго бурулушту.\n" +
                        "3) Бышыктоочту аныктайт: Эркингүл Мариялар жүргөн ашканага кирип барды. Жусуп Тверъ шаарын коргогон фронтто болуптур.\n" +
                        "4) Атоочтук баяндоочту аныктап келет: Алар - өткөн өмүрлөрү табылбай калган карыялар. Улууга урмат, кичүүгө ызаат көрсөтүү - кыргыз элинин кылымдан келаткан салты.\n" +
                        "5) Аныктоочту аныктап келиши мүмкүн: Машинага түшүп көрбөгөн энемдин башы айланып, жүрөгү айныды.");
                break;
            case 7:
                openSecond("Тема: Грамматикалык жактан сүйлөмгө мүчө боло албаган сөздөр.","Маселелер:\n" +
                        "1. Каратма сөздөр.\n" +
                        "2. Киринди сөздөр.\n" +
                        "3. Сырдык сөздөр.\n" +
                        "\n1.Сүйлөм тутумундагы сөздөр синтаксистик байланыштын тигил же бул түрү (ээрчишүү, башкаруу, ыкташуу, таандык байланыш) аркылуу өз ара бири-бири менен байланышып келип, сүйлөм уюшулары белгилүү. Анын тутумундагы сөздөр синтаксистик жагынан дайыма сүйлөмдүн баш жана айкындооч мүчөлөрүнүн милдетин аткарышат. Ушуну менен бирге, сүйлөм ичинде айрым бир сөздөр, сөз айкаштары да болот. Алар башка сөздөр сыяктанып, грамматикалык жактан сүйлөм мүчөлөрү менен байланышпайт (б.а. алар менен ээрчишүү, башкаруу, ыкташуу, таандык байланыш аркылуу байланыша албайт), сүйлөм мүчөлөрүнүн милдетин аткара албайт. Бул өңдүү сөздөр, адатта, бүткүл сүйлөмгө таандык болуп айтылат да, мааниси боюнча, айрымдары айтылган ой кимге, эмнеге багытталгандыгын көрсөтөт, кээ бир айтылып жаткан ойго карата болгон сүйлөөчүнүн ар кандай мамилесин(айтылган ойду бекемдөө, ырастоо же күмөндүүлүк, болжолдоо, ыраазы болуу ж.б. маанилерди) билдирсе, айрымдары айтуучунун ага карата болгон ар түрдүү ички сезимин туюндурат. Бул топко кирүүчү сөздөр ушул өңдүү маанилик өзгөчөлүктөрүнө карай каратма сөздөр, киринди сөздөр, сырдык сөздөр болуп бөлүнөт.\n" +
                        "\n\n\nКаратма сөздөр.\n\n" +
                        "Айтылган ой багытталган адамды же нерсени атап көрсөтүүчү сөз же сөз айкашы каратма сөз деп аталат.\n" +
                        "Каратма сөздөр грамматикалык жактан сүйлөм мүчөлөрү менен байланышпайт, кандайдыр бир суроого жооп берип, сүйлөм мүчөсүнүн милдетин да аткара албайт. Каратма сөздүн милдетин угуучунун көңүлүн айтылып жаткан ойго буруу, анын кимге, эмнеге багытталгандыгын билдирүү жана угуучуну пикир алышууна тартуу бо-луп эсептелет. Каратма сөздүн милдетин бир сөз же бир нече сөздөн турган сөз айкаштары да аткара берет. Мисалы: Султан, бул кыштак орношкон жер мурун шыбак өскөн таштак талаа болучу. Мекеним Ала-Тоо, экинчи кайрылып жүзүңдү көрөр бекенмин («КМ»).\n" +
                        "- Керим, «Сынган кылыч» романын окудуң беле?\n" +
                        "- Окугам.\n" +
                        "Каратма сөздөр көбүнчө көркөм адабий чыгармаларда, диалог иретинде айтылган сүйлөмдөрдө, оозеки кепте, чакырыктарда көбүрөөк колдонулат. Каратма сөздөр синтаксистик жактан айрым сүйлөм мүчөлөрү менен байланышпаганы менен, ал бүткүл сүйлөмгө таандык болуп айтылып, мааниси боюнча, сүйлөмдүн жалпы ички семантикасы менен тыгыз байланыштуу болот, анын структуралык жана маани жактан толук, так уюшулушуна көмөктөш болуп турат. Эгерде аны сүйлөмдүн тутумунан чыгарып таштасак, анын жалпы мааниси солгундай түшөт. Мисалы: - Рахмат сага, Толгонай, ушундай катаал күндөрдө эл-журтка баш болуп жүрөсүң (Ч.А.). - Оо, кулпунган жашыл көлцм, мен сени унутмак турсун, сагындым! («КМ») Бул сүйлөмдөрдүн тутумундагы каратма сөздөрдү алып таштасак, айтылып жаткан ойдун кимге, эмнеге багытталгандыгы белгисиз болуп калат.\n" +
                        "Кыргыз тилинде каратма сөздүн милдетин атооч жөндөмөдөгү төмөнкүдөй сөздөр аткарат:\n" +
                        "1) Адамдын энчилүү аттары жана жалпы аттар: Саадат, коногуңду ойгот (Т.С.). Андай болсо, Зууракан, белиңди бекем буу (К.Б.)- Сүйлө, Керимбек. Бул да болсо сенин бактың экен (К.Б.).- Жок, жолдоштор, биз иштин көзүн таппай жатабыз (Ч.А.). - Мен сөзгө чечен эмесмин, туугандар! (Т.С.). Малдын башын урдум, дүйнөнү сууга салдым, журт! (Т.С.).\n" +
                        "2) Туугандык, жолдоштук, ар кандай жакындык мамилелерди билдирүүчү зат атоочтор: Ата, орустуку бизге нан береби? (С.Ө.). - Балам, ал интернат дегениң эмне? (К.Б.). Күт, энеке, күз барамын (Т.С.). -Ии, кандай экенсиң, жеңе, уялдыңбы? (Ч.А.). - Кечир, досум, мен мында (К.Б.). Жарайсың, жолдошум, ыраазымын (Ч.А.).\n" +
                        "3) Адамдын жаш өзгөчөлүгүнө карата айтылуучу сөздөр: - Аксакал, күлүгүңүз кандай? (Т.С.). Билем, байбиче, жайында барган экенсиң (С.ө.). Мейли, чал, карышкыр сени жесин (жомок). Жигит, жаш экенсиң, сенин деле үй-бүлөң бардыр (К.М.).\n" +
                        "4) Кесипчиликти, адистикти, кызмат абалды билдирүүчү сөздөр: Бул үйрөтүлбөгөн азоо көрүнөт, туурабы, председателъ? (М.Ормонбеков) Сенин ишиң жеңил, бухгалтер (М.Ормонбеков). Ак иштеген аялдарыбыздын эмгегин так жазбай жүрөсүңөр, бригаттар (Т.С). - Макул, жолдош секретарь, ошолор барып келишсин («Ала- Тоо»). - Ой, почточу, бизге качан кат алып келесиң?\n" +
                        "5) Бирөөнү жакшы көрүүдө, жалынууда, эркелетүүдө колдонулуучу кагылайын, секет, садага, алтыным, күмүшүм, кубатым, жаным, күнүм, айым, кулунум, жылдызым, жалгызым деген сыяктуу сөздөр да каратма сөз катары келип, сүйлөмгө экспрессивдик маани берет: Кагылайын, сен да көзүбүзгө жылуу учурап жатасың (К.Б.). - Жакшы тандагыла, секеттер! -дейт Зарыл пионерлерге (Т.С). - Алтыным, курсагың ачкандыр (К.Б.). - Энең сенин маңдайыңда турат, жылдызым! (Т .С). Ал өзү чын, айланайындар! (А.Т.). - Кечирип кой, чырагым (К.Бек.).\n" +
                        "6) Каратма сөздүн милдетин кээде субстантивдешкен сын атоочтор, атоочтуктар да аткарып калат: Урматтуум, сизди канча болсо да күтөбүз (К.Ж.). Ай, жаман, башыңды көтөр, мынча неге салбырайсың?(«КМ»). Эстүүлөр, акыл тегеретип көргүлө, заман бузулуп турган чак (Т.Кас). Шаардан келгендер, силер тигил боз үйгө киргиле.\n" +
                        "7) Жан-жаныбарлардын аттары, жансыз заттарды бидирүүчү сөздөр да каратма сөз катары келе берет: Кой, Гүлсары, жаман көзүң менен караба! (Ч.А.). Кана, Балтек, менин ботинкамды алып келчи (М.Ормонбеков). Жаан, төгө бер берекеңди, төөгө бер! (Ч.А.). Энем сенсиң, Ала-Тоо, эмчегин соруп эмизген (Ж.Б.). Жадырап эркин жашаган элимди макта, комузум (А.Ү.).\n" +
                        "8) Жандама мүчө өзүнүн алдындагы аныкталгычы менен бирдикте туруп, каратма сөздүк милдетти аткарат. Мындай учурда аныкталгычы адам аттарынан болот да, аны аныктай турган жандама мүчө карыя, байбиче, жеңе, эже, аке, ата, апа, эне, агай, баатыр, уста ж.б. деген сыяктуу сөздөрдөн болот: Кечирип коюңуз, Калбүбү эже, менин олдоксон мамилемди («КМ»). Эми жок нерсеге кыйнаба, Кемелбек баатыр (М.Ормонбеков). Өкмөткө даттансам, Болотбек балам, кайра сенде м-нин акым көп (Т.С).\n" +
                        "9) Кээде бир нече каратма сөз катар келип да айтылат: Касым, Майсалбек, Жайнак, силердиби! Түшкүлө! (Ч.А.). Студенттер жана мектеп окуучулары! Ата Мекенибизди жан дилиңер менен сүйгүлө! («КМ»). Эми куда, кудагый, эп келсе кызымды чакыртсаңар («СК»).\n" +
                        "10) Айрым учурларда бир нече каратма сөз бир эле нерсени (адамды же башка затты) түрдүүчө атап көрсөтөт. Мындай учурда каратма сөздөр көбүнчө жалынуу, эркелетүү маанисиндеги сөздөрдөн болот да сүйлөмгө эмоционалдык түс берип турат: Көрсө, менин сүйгөнүм туулган жеримде тура! Көрсө, ал сен экенсиң, Жамилам, кызыл гүлүм! (Ч.А.). - Садагаларым, алтындарым! Эми ырысыңар болуп, мен аман тургай элем (К.Б.). - Оо, Керим, айланайын кубатым, эмне болду сага?! («ЛЖ»).\n" +
                        "11) Кээде каратма сөз сырдык сөз менен бирге айтылып калат. Мындай учурда ал (каратма сөз) же бүткүл сүйлөм кандайдыр бир эмоцияны (сүйүүнү, өкүнүү, чочуу, кайгыруу, кубануу, жактырбоо, бирөөнүн көңүлүн буруу үчүн чакыруу мааниси ж.б.) коштоосу менен айтылат: - Ботом, Акия, берегинин көйнөгү башкача да... (Т.С.). О, кайран Турумбек теңтушум! Кайран теңтушум болсо, өзүм менен майданга чыкпайт беле (Т.С.). - О, саякатчы эргулдар, келип калдыңарбы?(Т.С). - Ой, Элебес, сен шашпа! (Т.С.). - Ой, балам, токтой турчу (Т.С.).\n" +
                        "Каратма сөздөр түзүлүшү жагынан жайылма түрдө да көп жолугат. Мындай учу-да каратма сөздү ар түрдүү белгилери боюнча аныктап турган айкындооч мүчөлөрү болот. Аны көбүнчө сын атоочтор, атооч же илик жөнөдөмөдөгү зат атооч, ат атоочтор, атоочтуктар, туруктуу сөз айкаштары аныктап келет; ушуну менен бирге каратма сөздү түздөн-түз аныктап турган сөздүн өзүнүн да аныктооч мүчөлөрү болушу мүмкүн. Мисалы: Жолдош Кыдыров, сизди автобазага иштөөгө алып калалы деп жатабыз («Ала-Тоо»). Кымбаттуу меймандар, кечинде оюн коюучу жериңер даяр («Ала- Тоо»). Дүйнө жүзүнүн элдери, атом куралдарын кыскартуу үчүн күрөшкүлө! Айланамды карасам, жан боордош дыйкан талаам, сен дагы караан да жок, үн да жок томсоруп жаткан экенсиң (Ч.А.). - Келгиле, бышкан алчадай болгон садагалар. Кимди издеп жүрөсүңөр? (Т.С.).\n" +
                        "\n\n\nКаратма сөздөрдө тыныш белгилеринин коюлушу.\n\n" +
                        "Каратма сөздүн сүйлөмдө алган орду туруктуу эмес: ал сүйлөмдүн башынан, ортосунан жана аягынан орун ала берет. Каратма сөз бирөөгө атайын кайрылуу иретинде айтылганда жана ураандарда көбүнчө сүйлөмдүн башынан орун алат. Сүйлөм башында келген каратма сөз, адатта, чакырык интонациясы менен жабдылып, көтөрүңкү үн менен айтылат да, сүйлөмдүн өзүнөн кийинки бөлүгүнөн пауза (тыным) аркылуу ачык бөлүнүп турат.\n" +
                        "1) Жазууда, каратма сөз сүйлөм башында келгенде, андан кийин көбүнчө үтүр коюлат: - Элебес, баягы ишиң бүттүбү? (М.Э.). Бурулча, эми сен коногуңду жак-шылап күтүп ал (К.Б.).\n" +
                        "2) Сүйлөм башында келген каратма сөз күчтүү сезим менен айтылса, андан кийин илеп белгиси коюлат: Майсалбек! Алда айланып, согулуп кетейин балам, ай! (Ч.А.). - Иничектерим! Биз үчүн, элиңер, жериңер үчүн душмандан өч ала көргүлө! (К.Б.). Жолдош солдаттар жана матростор, сержанттар жана старшиналар! Сиздерди даңктуу майрамыңар менен куттуктаймын!\n" +
                        "Сырдык сөз айкашып айтылган каратма сөздөр жана катар келген бир нече каратма сөз да көпчүлүк учурда сүйлөмдүн башында айтылат: Апиий, кызым, ала качкан бойдон жолдон бир жакка чыгып кетпейби бул? (Т.С.). - Ой, Зарылжан! Эмне эле ойлонуп калгансың, ыя? (Т.С.). Ваня, Мария Исаевна, биз силерди жүдөттүк көрүнөт, кечирип койгула! (К.Бек.).\n" +
                        "3)Каратма сөз сүйлөмдүн ортосунда келсе, сүйлөм башында айтылгандагыга караганда, басыраак үн менен айтылып, өзүнөн кийин кыскача тыным болот, мурун болбойт. Жазууда эки жагынан үтүр менен ажыратылат: Баса, балам, сага кат бар (К.Б.). Кана, Султанмурат, класстагы балдардын ирдүүсү эле сен окшойсуң... (Ч.А.). Рахмат, туугандар, тамашалап болгонуңарга! (Ч.А.).\n" +
                        "4) Каратма сөз сүйлөмдүн аягына келсе, өзүнөн мурун үтүр коюлат. Мындай учурда оозеки кепте каратма сөз пауза менен ачык бөлүнбөйт: Жардамыңызга ыракмат, аке (А.Т.). Ишенем, жолдош Акматов (Н.Б.). Бул айткандарың туура, Толгонай (Ч.А.). Жакшы айттың, уулум (К.Б.).\n" +
                        "\n\n\n2.Киринди сөз жана киринди сөз айкаштары.\n\n" +
                        "Грамматикалык жагынан сүйлөм мүчөлөрү менен байланышпай, айтылып жаткан ойго карата сүйлөөчүнүн ар кандай мамилесин билдирүүчү сөздөр жана сөз айкаштары киринди сөз деп аталат. Андай мамиле түрдүүчө болушу мүмкүн: ишенимдүүлүк, бекемдөө, болжолдоо, күмөн саноо, шектенүү, ыраазы болуу, өкүнүү, бирөөнцн пикирине таянуу ж.б.\n" +
                        "Киринди сөздөр сүйлөмдүн тиги же бул мүчөсү менен байланышпагандан кийин, ал өзү да кандайдыр бир суроого жооп берип, сүйлөм мүчөсүнүн милдетин аткара албайт. Бирок ал мааниси боюнча бүткүл cүйлөмгө же айрым сүйлөм мүчөсүнө тиешелүү болуп айтылып, ага модалдык ар түрдүү| кошумча маанилерди (болжолдоо,кубаттоо, кумөндуулук, ыраазы болуу ж.б.) берип турат. Оозеки кепте киринди сөздү айтканда үн акырындап чыгып, өзү ылдамдатылган темп менен жана басымсыз айтылат; сүйлөмдөгү башка сөздөрдөн кыска тыным аркылуу жиктелип турат. Мисалы: Балким, окуучулар бүгүн келишер. Мамлекетке пахта тапшыруунун планы, мүмкүн,бүгүн бүтүп калар.Мында «балким» деген киринди сөз бүткүл сүйлөмгө тиешелүү болуп, анда айтылган ойго болжолдоо маанисин берип турат. Экинчи сүйлөмдөгү киринди сөз («мүмкүн») сүйлөмдүн «бүгүн бүтүп калар» деген бөлүкчөсүнө гана тиешелүү болуп айтылып, пландын бүгүн бүтөрүн болжолдуу баяндап турат.\n" +
                        "Кыргыз тилинде киринди сөздөргө жана киринди сөз айкаштарына, негизинен, төмөнкүлөр кирет: албетте, арийне, ырас, сөзсүз, шексиз, балким, чамасы, болжолу, сыягы, кыязы, чагымда, мүмкүн, чындыгында, чынында, калыбы деген модал сөздөр дайыма киринди сөздүк милдетти аткарат; булардан тышкары: айтмакчы, баса, мисал, айтор, кантейин, унутпасам, жаңылбасам, менимче, тескерисинче, биринчиден, экинчиден, үчүнчүдөн..., демек, чынын айтканда, ырасын айтканда, жыйынтыктап айтканда, корутундулап айтканда, жалпысынан айтканда, кыскача айтканда, бир сөз менен айтканда, бизче айтканда, башкача айтканда, мындайча айтканда, жогоруда айтылгандай, жогоруда көрсөтулгөндөй, ал айткандай, жалпысынан алганда, анын айтканына караганда, өзуңөргө маалым, өзуңөргө белгилүү, бир жагынан, экинчи жагынан, бактыга жараша, анын айтуусу боюнча, анын ою боюнча, тилекке каршы, амал канча ж.б.\n" +
                        "Киринди сөздөрдүн түзүлүшү.\n" +
                        "Киринди сөздөр түзүлүшү жагынан бир кылка эмес, ар түрдүү. Айрымдары атооч сөздөрдүн базасында (атоочтук киринди сөздөр), экинчи бирөөлөрү этиш сөздөрдүн негизинде (этиштик киринди сөздөр), үчүнчү бир тобу эки-үч сөздүн өз ара айкашынан түзүлгөн. Киринди сөз айкаштары да негизги компоненттеринин кайсы сөз түркүмүнөн экендигине карай атоочтук киринди сөз айкаштары жана этиштик киринди сөз айкаштары болуп бөлүнөт. Мисалы: өзүңөргө белгилүү, тилекке каршы; жыйынтыктап айтканда, болжол менен алганда.\n" +
                        "Жогоруда көрсөтүлгөн киринди сөздөрдүн ичиндеги атоочтук киринди сөздөрдүн айрымдары:\n" +
                        "1) Уңгу түрүндө туруп, киринди сөздүк функцияда келишет: албетте, арийне, ырас, балким, мүмкүн, баса, айтор. Калгандары төмөнкүдөй грамматикалык түзүлүштө жолугат:\n" +
                        "2) Зат атоочко үчүнчү жакка таандык -ы мүчөсү жалганган: чамасы, болжолу, сыягы, калыбы, кыязы, мисалы;\n" +
                        "3) аягына I, III жакка таандык мүчө уланган чак, чын, чындык, негиз деген зат атоочторго жатыш жана чыгыш жөндөмөнүн мүчөсү уланган: чагымда, чынында, чындыгында, негизинен;\n" +
                        "4) I, II жактын таандык формасындагы мен, тескери деген атоочторго -ча мүчөсү жалганган: менимче, тескерисинче;\n" +
                        "5) айрым иреттик сан атоочторго чыгыш жөндөмөнүн мүчөсү уланган: биринчиден, экинчиден, үчүнчүдөн ж.б.\n" +
                        "Этиштик киринди сөздөр да түзүлүшү жагынан ар түрдүү:\n" +
                        "1) Терс формадагы айрым этиштерге шарттуу ыңгайдын -са мүчөсү жана I жак-тын -м мүчөсү уланган: унутпасам, жаңылбасам;\n" +
                        "2) айт, де деген уңгу этиштерге ниет ыңгайдын -мак, -макчы мүчөсү жалганган: айтмакчы, демек;\n" +
                        "3) кант (кандай+эт), айт деген этиштерге каалоо ыңгайдын I жак жекелик жана көптүк түрүнүн -айын, -алык формалары уланган: кантейин, айталык;\n" +
                        "4) Кээде киринди сөздүк функцияда келүүчү «ушинтип»: ушундай+эт+ип деген бөлүкчөлөрдөн турат.\n" +
                        "\n\n\nКиринди сөз айкаштары, негизинен, төмөнкүдөй жолдор менен уюшулат:\n\n" +
                        "1) Жатыш жөндөмөдөгү «айтканда» деген атоочтукка:\n" +
                        "а) III жакка таандык формадагы зат атоочтун табыш жөндөмөдө туруп айкашуусу аркылуу: чынын айтканда, ырасын айтканда, туурасын айтканда;\n" +
                        "б) III жакка таандык формадагы сөздүн чыгыш жөндөмөдө туруп айкашы аркылуу: жалпысынан айтканда;\n" +
                        "в) «бир сөз менен», «эки сөз менен» деген тизмектин айкашуусу аркылуу: бир сөз менен айтканда;\n" +
                        "г) -ча формасындагы тактоочтордун айкашуусу менен: кыскача айтканда, бизче айтканда, башкача айтканда, мындайча айтканда;\n" +
                        "д) -ып формасындагы чакчылдардын айкашып айтылышы менен: жыйынтыктап айтканда, корутундулап айтканда, тактап айтканда;\n" +
                        "2) салыштыруу маанисиндеги -дай мүчөсү жалганган -ган формасындагы атоочтукка жатыш жөнөдөмөдөгү «жогору» деген тактоочтун жана атооч жөндөмөдөгү айрым зат атоочтун же жактама ат атоочтун айкашып айтылышы менен: жогоруда айтылгандай; жогоруда көрсөтүлгөндөй, ал айткандай, Зарыл айткандай;\n" +
                        "3) жатыш жөндөмөдөгү -ган формасындагы «алганда» деген атоочтукка чыгыш жөндөмөдөгү «жалпысынан» деген сөздүн жана «менен» жандоочуна башка бир сөздүн байланышып айтылышы менен уюшулган жандоочтук тизмектин айкашып келүүсү менен: жалпысынан алганда, болжол менен алганда, орто эсеп менен алганда;\n" +
                        "4) жатыш жөндөмөнүн мүчөсү уланган -ган формасындагы «караганда» деген атоочтукка барыш жөндөмөдөгү атоочтуктун (мунун өзүнүн илик жөндөмөдөгү аныктоочу болушу мүмкүн) айкашып айтылышы аркылуу: анын айтканына караганда, кечиккенине караганда, шашканына караганда;\n" +
                        "5) маалым, белгилүү, жараша, каршы деген атооч сөздөргө барыш жөндөмөдөгү атоочтордун айкашып келиши менен: өзүңөргө маалым, өзүңөргө белгилүү, бактыга жараша, тилекке каршы ж.б.;\n" +
                        "6) «амал» деген зат атоочтун «канча» деген ат атооч менен айкашуусу аркылуу: амал канча;\n" +
                        "7) чыгыш жөндөмөдөгү «жагынан» деген сөз менен «бир», «экинчи» деген сан атоочтордун айкашып айтылышы менен: бир жагынан, экинчи жагынан;\n" +
                        "8) илик жөндөмөдөгү атооч сөз менен аягына таандык мүчө уланган айтуу, байкоо, ой, пикир, көз караш деген сөздөр байланышып келип, «боюнча» жандоочу менен айкашып айтылып да, киринди сөз айкашы уюшулат: анын айтуусу боюнча, Салыйдын ою боюнча, сенин көз карашың боюнча, менин байкоом боюнча.\n" +
                        "\n\n\n3. Сырдык сөздөр.\n\n" +
                        "Сөз түркүмүнүн бул өзгөчө түрү адамдын нчки сезимин жана айбанатка болгон эрктик мамилени билдирери белгилүү. Мисалы: Бах, жайлоонун толукшуп турган кези экен го! (К.Ж.). - Өш, талаада калгыр, өш! (Т .С.). Сырдык сөздөр өзүнчө жеке айтылганда же кийинки сүйлөм менен тыгыз байланышпай, андан бөлүнүп өз алдынча колдонулганда, сүйлөмдүк мааниге да ээ болуп калат. Мындай учурда алар айтылган ойго карата болгон сүйлөөчүнүн ар түрдүү ички сезимин билдирген өзүнчө бир сүйлөмдү туюндурат. Мисалы: - О, жеңе, иш илгери! Тегирмен чуркап жаткан тура. Баракелде!..\n" +
                        "Бирок сырдык сөздөр көпчүлүк учурда сүйлөмдүн тизмегинде келип, аны менен тыгыз байланышта айтылат. Мындай учурда алар өз бетинче турган бирдиктүү ойду түшүндүрө албайт, тескерисинче, маани жагынан бүткүл сүйлөмгө таандык болуп турат. Мындай кырдаалда сырдык сөздөр сүйлөм ичиндеги башка сөздөр менен байланышпайт, сүйлөм мүчөлөрүнүн милдетин да аткара албайт; тек гана бүткүл сүйлөмгө ар түрдүү эмоционалдык маани берип турат. Сырдык сөздөр ушундай жагдайда, ушундай функцияда колдонулганда, «грамматикалык жагынан сүйлөм мүчөлөрү менен байланышпаган сөздөрдүн» тобуна кошулат. Сөздөрдүн бул тобу өзгөчө оозеки кепте, көркөм адабий чыгармаларда кеңири колдонулат.\n" +
                        "Сырдык сөздөрдүн туюндурган маанилери өтө бай. Алар сүйлөмдүн тутумунда келип, айтылып жаткан ойго карата болгон сүйлөөчүнүн ар түрдүү ички сезимин билдирет.\n" +
                        "1) Адамдын сүйүнүү, кубануу сезимин билдирет: Охо, көп нерсени билип калыпсыз («КМ»). Ай-иий, эрте келгениңер жакшы болбодубу!\n" +
                        "2) Таң калуу сезимин билдирет: Ой тобо, шумдук экен го бул! - деп, өзү таң калууда (Т.С.). Адам, биздин өлкө кең тура (Т.С)\n" +
                        "3) К убаттоо, мактоо сезимин: Ой бали, баарынан да кийинкиси жоон (Т.С). Баракелде, Шамбетим! Көп ишти бүтүрүп салдыңар (Т.С).\n" +
                        "4) Суктануу, ыраазы болуу сезимин: Бах... жер эмес бекен! Пай-пай, эмгек сүйгөн кишилер алп экен го! (Т.С).\n" +
                        "5) Кайгыруу, өкүнүү, кейүү сезимин: Каап, ушу сары бучук келиндин азабы... (Т.С). Аттиң ай, менин чолоктугумду карачы! (К.Б.).\n" +
                        "6) Чочуу, коркуу сезимин: Ушундай аркырайт экен ээ, ботом (Т.С). - О кокуй, бул эмне кылганыңар! (К.Б.).\n" +
                        "7) Жактырбоо, нааразылык сезимин билдирет: Койчу, ушундай да иш кыласыңбы?!\n" +
                        "Сырдык сөздөр синтаксистик жагынан сүйлөмдүн башка мүчөлөрү менен байланышпагандыктан, алар да үтүр белгиси менен ажыратылып жазылат.\n" +
                        "1) Эгерде сырдык сөз сүйлөмдүн башында келсе, өзүнөн кийин үтүр коюлат: Апей, ботом, салтың кана? (Т.С.).\n" +
                        "2) Сүйлөмдүн ортосунда келсе, сырдык сөздүн эки жагына тең үтүр коюлат: Биздин Султан, ой бо-оой, кадимки эле чоң жигит болуп калган турбайбы!\n" +
                        "3) Сүйлөмдүн аягында келсе, үтүр белгиси сырдык сөздөн мурун коюлат: Ат эмей эле дулдул го, чиркин! (Ч.А.).\n" +
                        "4) Сырдык сөздөр сүйлөм башында келип, күчтүү сезим менен айтылса, андан кийин илеп белгиси коюлат: - Бали! Макиш мына ушундай Макиш! Оо, дүйнө, ай! Ал күнкү окуялар кайсы кыргыздын баласынын оюнан кете коёр экен? (К.Б.).\n" +
                        "5) Эгерде сүйлөм ичинде сырдык сөз, каратма сөз, киринди сөз да катышып ай-тылса, ар биринде тиешелүү тыныш белгилери сакталат: Эх, Керим, албетте, иштин баары биз ойлогондой бүтсө, жакшы болбойт беле!");
                break;
            case 8:
                openSecond("Тема: Татаал сүйлөмдүн синтаксиси.","\n" +
                        "Маселелер:\n" +
                        "1. Татаал сүйлөмдөр жөнүндө жалпы түшүнүк.\n" +
                        "2. Татаал сүйлөмдүн түзүлүшү.\n" +
                        "3. Татаал сүйлөмдүн уюшулуу жолдору.\n" +
                        "\n1.Адамдын башка бирөөгө билдире турган ою структурасы жагынан жөнөкөй түрдө же татаал түрдө болушу мүмкүн. Ушуга байланыштуу андай ой-пикирди тилдик материалдардын негизинде туюндуруучу сүйлөмдөр да түзүлүшү жагынан жөнөкөй же татаал формада уюшулат. Башкача айтканда, жөнөкөй түзүлүштөгү ой бир гана предикативдик бирдиктен турган жөнөкөй сүйлөм аркылуу берилсе, татаал ой эки же андан ашык жөнөкөй сүйлөмдүн өз ара байланышынан түзүлгөн татаал сүйлөм аркылуу берилет.\n" +
                        "Мисалы: 1) Жапар газетаны колуна алды. Анда район боюнча мамлекетке эгин тапшыруунун жыйынтыгында биринчи болуп «өрнөк» чарбасы жазылган болучу (К.Б.). 2) Ушул маселе жетиштүү чечилмейинче, эт комбинаттары толук баалуу эт азыгын чыгара албайт экен (А.Уб.).\n" +
                        "Мында акыркы сүйлөмдө «эт комбинаты сапаттуу продукцияны чыгарууга жетишүү үчүн, алды менен ага байланыштуу башка бир маселелердин чечилиши зарыл» экендиги жөнүндөгү эки (татаал) ой эки жөнөкөй сүйлөмдүн бири-бири менен айкашып айтылышы аркылуу берилген.\n" +
                        "Ушинтип, жөнөкөй сүйлөмдөр бир нече сөздүн же сөз айкаштарынын өз ара байланышынан түзүлсө, татаал сүйлөмдөр эки же андан көп жөнөкөй сүйлөмдүн айкашынан уюшулуп, бирдиктүү бир татаал ойду билдирет. Башкача айтканда, жөнөкөй сүйлөмдүн курулуш материалы сөз жана сөз айкаштары болсо, татаал сүйлөмдөрдүн курулуш материалы жөнөкөй сүйлөмдөр болуп эсептелет.\n" +
                        "Татаал сүйлөмдүн тутумунда келген жөнөкөй сүйлөмдөр өз алдынча турган жөнөкөй сүйлөмөгө айрым белгилери боюнча окшош келсе, кээ бир белгилери боюнча алардан айырмаланып да турат.\n" +
                        "Экөөнүн ортосундагы окшоштук, негизинен, алардын грамматикалык структурасы (түзүлүшү) менен байланыштуу: татаал сүйлөмгө компонент болуп келген жөнөкөй сүйлөмдө да, өз алдынча турган жөнөкөй сүйлөмдө да (эгерде алар эки тутумдуу сүйлөм болсо) өзүнө тиешелүү баш мүчөлөрү - ээ менен баяндоочу, болот; эгерде бир тутумдуу сүйлөмдөрдөн болсо, алардын уюшулушуна негиз болгон баяндоочу болот; мындан тышкары экөөнүн тең тутумунда баш мүчөлөрү менен байланышта айтылган ар түрдүү айкындооч мүчөлөр да катышышы мүмкүн. Мисалы: Биздин райондун мектептери чарбанын өндүрүш иштерине чоң жардам берип жатат. Мен согуш жөнүндө экинчи Даниярдан эчтеке сураган жокмун, балдар дагы ага тийишип тең аталашканын коюшту (Ч.А.).\n" +
                        "Ушуну менен бирге бул жөнөкөй сүйлөмдөрдүн ортосунда кээ бир айрымачылыктар да бар. өз алдынча турган жөнөкөй сүйлөмдөр «аякталган интонация» менен айтылып (сүйлөм аягында үн акырындык менен бир кыйла төмөндөп айтылат), толук бүткөн ойду билдирет. Татаал сүйлөмдүн тутумундагы жөнөкөй сүйлөмдөр болсо (акыркы сүйлөмүнөн башкалары), «аякталбаган интонация» менен айтылат (сүйлөм аягында үн бир аз төмөндөп айтылат), ошол себептүү алар толук аяктаган ойду билдире албайт, демек, өз алдынча турган сүйлөм боло албайт; тескерисинче, ал бүтүндүн бир бөлүкчөсү гана экендиги, өзүнөн кийин маани жагынан аны менен байланышкан дагы бир сүйлөмдүн бар экендиги сезилип турат. Качан гана ал ошол өзүнөн кийинки башка сүйлөм менен (же сүйлөмдөр менен) тыгыз байланышып, аны менен бирдикте айтылганда гана толук аяктаган бир бүтүн татаал ойду туюндура алат.\n" +
                        "Татаал сүйлөмдөрдө «аякталган интонация» бүткүл сүйлөмгө тиешелүү болот, ошол себептүү ал (интонация) дайыма татаал сүйлөмдүн акыркы компонентинин эң аяккы сөзүнө түшүрүлүп айтылат. Бирок татаал сүйлөмдөр туш келген эле жөнөкөй сүйлөмдөрдөн уюшула бербейт. Алар маани жагынан бири-бири менен байланыштуу келген, өз ара шартташып турган бир нече жөнөкөй сүйлөмдүн грамматикалык жактан байланышып айтылышы аркылуу түзүлөт: Мисалы: Курман үйдө чай ичип отурганда, эшиктен аттын дүбүртү чыгып калды (К.Б.). Караңгылык тарады, жер бети абдан жарык болду.\n" +
                        "Булар, биринчиден, маани жагынан өз ара байланыштуу эки жөнөкөй сүйлөмдүн айкалышынан уюшулган. Алдыңкы татаал сүйлөмдүн биринчи компоненти экинчи компонентинде айтылган ойдун мезгилин билдирип турат. Кийинки татаал сүйлөмдө болсо, тутумундагы биринчи жөнөкөй сүйлөм себепти, экинчиси андан чыккан натыйжаны билдирип, өз ара шартташып турат. Экинчиден, жогорку татаал сүйлөмдөрдүн тутумундагы жөнөкөй сүйлөмдөрдүн биринчисин жалпы контексттен бөлүп алсак, анда ал «аякталбаган интонация» менен айтылат да, натыйжада, кандайдыр бир толук аяктаган ойду туюндура албай калат, өзүнөн кийин маани жагынан аны менен байланышкан дагы бир сүйлөмдүн бар экендиги байкалып турат («Курман үйдө чай ичип отурганда...», «Караңгылык тарады...»). Качан гана бул татаал сүйлөмдөрдүн тутумундагы жөнөкөй сүйлөмдөрдү өз ара байланыштуу алганда гана, толук аяктаган бирдиктүү татаал ойду билдирет; ал эми сүйлөм үчүн мүнөздүү болгон «аякталган интонация» бүткүл татаал сүйлөмгө тиешелүү болуп, анын акыркы сүйлөмүнүн аяккы сөзүнө түшүрүлүп айтылат. Мисалы: Сапырылган эгиндин чаңы асманга учуп, топону созулуп куйрук болуп, кызылы Илибай карыянын бет алдына себилип түшүп жатат (К.Б.).\n" +
                        "Бул татаал сүйлөмдүн тутумундагы ар бир жөнөкөй сүйлөм (эң акыркысынан башкалары) «аякталбаган интонация» менен айтылат да, ал эми бүткүл татаал сүйлөм бирдиктүү түрдө туруп, «аякталган интонация» менен айтылат, б.а. үн сүйлөм аягында акырындык менен бир кыйла төмөндөп айтылат.\n" +
                        "\n\n\nМаани жагынан өз ара байланыштуу келген эки же андан ашык жөнөкөй сүйлөмдүн айкалышынан уюшулуп, түзүлүшү, мааниси жана интонациялык жактан бир бүтүн бирдикти түзүп турган сүйлөмдөр татаал сүйлөм деп аталат.\n" +
                        "\nАйтыла турган ойдун структурасына ылайык татаал сүйлөмдөр эки же андан көп жөнөкөй сүйлөмдүн өз ара байланышынан уюшулат. Адатта, анын тутумундагы ар бир жөнөкөй сүйлөмдүн өзүнө тиешелүү грамматикалык ээси жана баяндоочу болот:\n" +
                        "1) Макар алга карай жүгүрдү, анын артынан агылып башка жоокерлер да чур-кашты (К.Б.).\n" +
                        "2) Бир кездерде ошол дөбөнүн үстүндө мектеп болгон имиш, биз анын ордун да көрбөй калдык, бирок кош теректүү дөбөнүн аталышы эл оозунда мурунку бойдон келе жатат (Ч.А.).\n" +
                        "3) Мен түгөнбөйм, мен тарыбаймын, мен кенчмин, мен баарыңарга тегиз же-тем (Ч.А.).\n" +
                        "Бул татаал сүйлөмдөрдүн биринчиси эки, кийинкиси үч, акыркысы төрт жөнөкөй сүйлөмдөн уюшулган жана ар биринин өзүнө таандык баш мүчөлөрү бар. Бирок бардык учурда эле татаал сүйлөмдөрдүн ар бир компонентинде грамматикалык ээ катыша бербейт.\n" +
                        "1) Кээде, өзгөчө баяндоочтун милдетин жак мүчө уланган этиш аткарып турган учурда, стилдин талабына ылайык, татаал сүйлөмдүн тутумундагы жөнөкөй сүйлөмдөрдүн ээси айтылбай да калат. Мындай учурда ээнин кайсы сөз экендиги баяндоочунун аягындагы жак уландыдан ачык билинип турат: Күнү-түнү туутта жүрдүм, аны жакшы билесиң (Н.Б.). Алтынайды жер каратпайм, Курманды болсо, бала кылып багып алам (К.Б.).\n" +
                        "2) Эгерде татаал сүйлөмдүн тутумундагы жөнөкөй сүйлөмдөр билдирген иш-аракет, ой-пикир бир гана субьектиге тиешелүү болсо, анда анын тутумундагы жөнөкөй сүйлөмдөрдүн баяндоочтору да бир гана ээге баш ийет. Мындай учурда татаал сүйлөмдө жалгыз гана ээ болот: Чабалекей балдарына жем берип болуп, эс алууга уясында жаткан (К.Б,). Ал азыр эс тартып калды, үчүнчү класста окуйт, айрыкча, апасынын көзүнчө токтоо жцрөт (Ч.А.).\n" +
                        "3) Айрым учурда татаал сүйлөмдүн ээси айтылбай калган компонентинин грамматикалык субъектиси кайсы сөз экендиги өзүнөн мурунку сүйлөмдүн маанисинен белгилүү болуп турат: Жарым аралдын чаңы бат эле асманга чыгып, дүмүрөйүп капкара боло тцштц (Т.С.). Биз тентек суу-ну жөнгө салсак, жыргал үчүн кызмат кылат (Т.С.).\n" +
                        "Бул татаал сүйлөмдөрдүн экинчи компонентинин грамматикалык ээси жок, бирок биринчи татаал сүйлөмдө «жарым арал», экинчисинде «суу» деген сөздүн ээлик функцияда келе тургандыгы өзүнөн мурунку сүйлөмдүн мазмунунан белгилүү болуп турат.\n" +
                        "\n\n\n2.Татаал сүйлөмдүн компоненттеринин түзүлүшү (структурасы) жөнүндө. Татаал сүйлөмдөр жөнөкөй сүйлөмдүн бардык түрлөрүнөн уюшула берет.\n" +
                        "1)Татаал сүйлөмдөрдүн компоненттери жалаң жана жайылма сүйлөмдөрдөн болот: Тамак келди, дасторкон жыйналды, колдор жуулду (К.Б.). Тойчубекке кошулуп Бурулча да келди, бирок ал эмне жумуштап келгенин айткан жок (К.Б.).\n" +
                        "2) Кээ бир татаал сүйлөмдөрдүн компоненттеринин бири же экөө тең татаал сүйлөмдөрдөн болушу мүмкүн: Ат дүбүртү жол үстүн дүңгүрөтүп, тозоң көтөрүлдү: жол үстүнө боз түштү (Т.С.). Ак падыша тагынан кулап, улуктары, чиновниктери жоюлду: генералдары погондорун тытып, армиясы чилдей тарады (Т.С.). Мында алдыңкы татаал сүйлөмдүн биринчи компоненти татаал сүйлөмдөн, экинчи компоненти жөнөкөй сүйлөмдөн турат. Кийинки сүйлөмдө болсо эки компоненти тең татаал сүйлөмдөн уюшулган.\n" +
                        "Эгерде татаал сүйлөмдүн компоненттеринин өзү бул сыяктанып татаал сүйлөмдөрдөн болсо, анын тутумундагы жөнөкөй сүйлөмдөрдүн арасына эреже боюнча үтүр коюлат да, ал татаал сүйлөмгө компонент болуп келген татаал сүйлөмдөрдүн чеги (же татаал сүйлөм менен жөнөкөй сүйлөмдүн чеги) үтүрлүү чекит менен ажыратылат; эгерде бул жерге да үтүр белгисин койсок, анда ал татаал конструкциянын тутумундагы сүйлөмдөрдүн баары бир-биринен жалаң үтүр менен бөлүнүп, анын ичиндеги жөнөкөй сүйлөмдөр менен татаал сүйлөмдөрдүн чегин ажыратуу ишине бир топ кыйынчылык туудурмак.\n" +
                        "3) Кээ бир татаал сүйлөмдөрдүн компоненттеринин бири же экөө тең жалаң гана баяндоочтон (кээде баяндоочко тиешелүү айкындоочу болушу мүмкүн) болуп, өтө кыска структурада жолугат. Алардын айтылбай калган ээси баяндоочтун аягындагы жак мүчөдөн же сүйлөмдүн жалпы маанисинен белгилүү болуп турат: Бербейм, кеткин! (Ч.А.). Келсем-жоксуң. Көрсөтүңүз, ошол замат эле аны үйүңүзгө келтирем (К.Б.). Эп көрсөң, ошого үйлөн (К.Б.).\n" +
                        "Мында «Келсем - жоксуң» деген татаал сүйлөмдүн тутумундагы экинчи сүйлөм\n" +
                        "биринчи сүйлөмгө кескин түрдө карама-каршы мааниде айтылгандыктан, арасына сызыкча коюлган.\n" +
                        "4) Айрым татаал сүйлөмдөрдүн бир компоненти же экөө тең бир тутумдуу сүйлөмдөрдөн болушу мүмкүн:\n" +
                        "а) Биринчи компоненти жаксыз сүйлөм, экинчиси эки тутумдуу сүйлөмдөн болот: Тегирменге кала бериш керек, андан көрө өзүм жаргылчакка тартып алам (Ч.А.).\n" +
                        "б) Биринчи компоненти жалпылама жактуу сүйлөмдөн болот: Калпты чындай айтса, чын чычая качат (макал).\n" +
                        "в) Биринчи компоненти туюк жактуу сүйлөм, экинчиси жаксыз сүйлөмдөн болот: Кечээги тигилген көчөттөрдү сындырып кетишиптир, эми анын ордуна жаңы көчөттөрдү отургузууга туура келет.\n" +
                        "г) Эки компоненти тең жаксыз сүйлөмдөрдөн болот: Азыр айтууга болбойт, ыңгайлуу учурду күтүү керек.\n" +
                        "д) Эки компоненти тең жалпылама жактуу сүйлөмдөрдөн болот: Ачыкканга казан астырба, үшүгөнгө от жактырба. Эрди күчүнөн тааныйт, узду ишинен тааныйт (макалдар).\n" +
                        "5)Татаал сүйлөмдөрдүн бир же эки компоненти тең атама сүйлөмдөрдөн болушу мүмкүн: Күн алда качан уясына отуруп, айлана теребелди коюу караңгылык чулгаган кез. Текчеде көп китептер, столдо жазылып бүтө элек барак-барак кагаздар. Шкафта Нурбекке тааныш эмес түрдүү приборлор. Дубалда мыкты алпинисттик кара көз айнек илинип турат (Ч.А.).\n" +
                        "6) Бир же эки компоненти тең кыскартылган баяндоочтуу сүйлөмдөрдөн болушу ыктымал: Асты таш, үстү таш, ортосунда - бышкан аш. Эт-этке, сорпо-бетке. Күч - билекте, кайрат - жүрөктө (макалдар).\n" +
                        "\n\n\n3. Татаал сүйлөмдүн уюшулуу жолдору.\n\n" +
                        "Мааниси жагынан өз ара байланыштуу келген жөнөкөй сүйлөмдөр ар түрдүү грамматикалык каражаттар аркылуу бири-бири менен тутумдашып айтылып, бирдиктүү маанидеги татаал сүйлөмдөр уюшулат. Ал грамматикалык каражаттар кайсылар экендиги жана алардын татаал сүйлөмдөрдү түзүүдөгү кызматы кийинчерээк багынычсыз жана багынычтуу татаал сүйлөмдөрдүн түзүлүшү жөнүндө сөз болгондо кеңири каралат. Азыр бул маселе боюнча окуучуларга жалпы түшүнүк бере кетүү максатында гана ал грамматикалык каражаттарга кыскача мүнөздөмө бере кетмекчибиз.\n" +
                        "Татаал сүйлөмдөрдүн тутумундагы жөнөкөй сүйлөмдөрдү бири-бири менен байланыштыруучу негизги грамматикалык каражаттарга төмөнкүлөр кирет:\n" +
                        "1) Интонация. Татаал сүйлөмдө эки түрдүү интонация бар: аякталган интонация жана аякталбаган интонация.\n" +
                        "Аякталган интонация татаал сүйлөмдүн эң аягына түшүрүлүп айтылып, анын толук аяктаганын билдирет, экинчи жагынан, тутумундагы жөнөкөй сүйлөмдөрдү бир бүтүнгө бириктирип турат.\n" +
                        "\n\n\nАякталбаган интонация болсо, татаал сүйлөмдүн тутумундагы ар бир жөнөкөй сүйдөмдүн (эң аяккысынан башкасынын) эң аяккы сөзүнө түшөт да, өзүнүн тыянаксыз айтылышы жана андан кийинки кыскача тыным ал сүйлөмдүн толук аякталбагандыгын, айтылып жаткан ой андан кийин да улантыларын билдирип турат. Ушуну менен бирге мындагы саноо иретинде айтылган интонация татаал сүйлөмдүн компоненттерин бири-бири менен байланыштыруу кызматын да аткарат. Мисалы: Аттар чаалыкты, жолоочулар талыкшыды (К.Ж.). Күн ысып турат, кой-эчкилер таш-таштын көлөкөсүндө жуушап жатат (К.Ж.).\n" +
                        "Бул татаал сүйлөмдөрдүн компоненттери бири-бири менен санак интонация (же байланыштыруучу тыным (пауза) аркылуу байланышып турат. Санак интонация менен көбүнүчө тутумундагы сүйлөмдөрдүн баяндоочу тыянактуу формада (үч жактын биринде) айтылган тең байланыштагы татаал сүйөмдөр уюшулат.\n" +
                        "2) Татаал сүйлөмдөрдүн компоненттерин ар түрдүү маанидеги байламталар байланыштырат: Жаркыраган ай ааламга нурун төктү жана электр шамы да балбылдап жанды (К.Ж.). Төрттө келейин, бирок сиз убакытты так сактаңыз (Ш.Б.). Азыр ашуу бек, же алар машина аркылуу тиги жол менен барабы? (Н.Б.). Каныбек абдан такып сурады, себеби анын көзүнө Эркин сүйгүнчүктүү учурады (К.Ж.).\n" +
                        "3) Татаал сүйлөмдүн тутумундагы багыныңкы сүйлөм өзүнөн кийинки баш сүйлөм менен дайыма баяндоочтук формасы аркылуу байланышат. Тактап айтканда, багыныңкы сүйлөмдүн этиштик баяндоо-чуна чакчыл -ып, -а(-й), -бай, -ганча(-гыча ) ,-ганы(-галы ) ,- майынча(-майын ) мүчөлөрүнүн уланып айтылышы аркылуу: Кандайдыр укмуштуу ой кылт этип, Нур-бек селдее калды (Ч.А.). Күндүн нуру жайыла, комбайнчылар эгин чабууга кири-шишти. Биз Тогуз-Булакка келгени, бир айдан ашуун убакыт өттү (М.Э.).\n" +
                        "4) Багыныңкы сүйлөмдүн атоочтук этиштен турган баяндоочуна жатыш жөндөмөнүн -да мүчөсүнүн уланышы менен: Таятам жомок айтып отурганда, мен ушунун баарын ойлоп отурам (Ч.А.).\n" +
                        "5) Атоочтук этиштин кийин, соң, сайын, менен, үчүн, чейин, дейре, бери, тартып деген өңдүү жандоочтор менен айкашып келип, баяндоочтук милдетти аткарышы аркылуу: Карымшак аттанары менен, Бозгунчу дагы сөзгө аралашты (Т.С.). Бала там-туң басканга дейре, келин үйдө болду (Н.Б.). Кой сарайлар жылуу болгон үчүн, козулар чыгашага учураган жок.\n" +
                        "6) Атоочтуктун жатыш жөндөмөдөгү убакта, мезгилде, кезде, учурда, чакта, ченде деген сыяктуу мезгилдик маанидеги сөздөр менен айкашып келип, баяндооч-тук милдетти аткарышы менен: Гүлсары алдына ат салбай чыгып турган чагында, атагы жер жарып турат (Ч.А.). Биз жарым аралга жакындай берген учурда, душман бизди замбирек менен ата баштады (К.Б.).\n" +
                        "7) Негизги сөзгө эле, экен, эмес деген жардамчы сөздөрдүн айкашып айтылып, баяндоочтук функцияда келиши менен: Ай нуру өзөн ичине чачырай элек, эрте жаздын коңур нуру бир аз нымдуу (Т.С.). Жемге тойгон арабанын аттары дыңылдап карылуу экен, бат эле алда кайда узап кеттик (С.Ө.). Жамийла жалгыз эмес, ал Айша деген кыз менен бирге келген (К.Б.)\n" +
                        "8) -ган формасындагы атоочтукка -дык-тан мүчөсү жалганып, баяндоочтук милдетти аткарышы аркылуу: Малчылар Чүйгө көчүп кеткендиктен, жайлоонун чөбү кайрадан кулпура баштады.\n" +
                        "9) Багыныңкы сүйлөмдүн баяндоочунун милдетин шарттуу ыңгайдын аткарышы аркылуу баш сүйлөм менен байланышат: Акча чөнтөгүмдө болсо, губернаторлорду да бийлетүүгө чамам жетет (К.Бек.). Командири шер болсо, жоокерлери эр болот (К.Ж.).\n" +
                        "10) Бир катар багынычсыз татаал сүйлөмдөрдүн ичиндеги жөнөкөй сүйлөмдөрдү өз ара тыгыз байланыштырууда, алардын ортосунда белгилүү бир маанилик катыштын түзүлүшүндө ал сүйлөмдөрдүн орун тартиби да орчундуу ролду ойнойт. Өзгөчө татаал сүйлөмдүн компоненттери биринен кийин экинчиси болгон кыймыл- аракетти, себептен чыккан натыйжаны билдирсе, же экинчиси биринчи сүйлөмдүн мазмунун чечмелеп, тактап турса, же болбосо эки компонентине тең орток болгон жалпы бир мүчөсү болсо ж.б., алардын орун алуу тартиби туруктуу болот. Эгерде андай сүйлөмдөрдүн ордун алмаштырсак, ортолорундагы синтаксистик байланыш, маанилик катыш бузулат. Мисалы: Чай ичилип бүттү, дасторконго башка тамактар коюлду (Н.Б.). Бирок башка буюмдар да жоголуп баратат, ошого ич ачышат (Ч.А.). Азыр сенден өтүнүчүм бар: төрүмдөгү меймандар жөнүндө кымындай шек чыгара көрбө (К.О.). Бир кезде иттин үнү да токтолду, карааны да жоголду (А.Т.). Мында акыркы мисалдагы «иттин» деген аныктооч биринчи сүйлөмдөгү «үнү», экинчи сүйлөмдөгү «карааны» деген аныкталып турган сөздөр үчүн орток, жалпы мүчө.\n" +
                        "Эгерде жогорку татаал сүйлөмдөрдүн тутумундагы жөнөкөй сүйлөмдөрдүн ордун алмаштырсак, анда алардын ортосундагы структуралык жана маанилик байланыш бузулат, же начарлайт, же болбосо, башкача маани берип калат. Мисалы: «Ошого ич ачышат, бирок башка буюмдар да жоголуп жатат. Карааны да жоголду, бир кезде иттин үнү да токтолду» деп орун тартибин алмаштырсак, алардын алгачкы мааниси бузулат.\n" +
                        "Бирок айрым багынычсыз татаал сүйлөмдөрдө өзгөчө тең байланыштагы татаал сүйлөмдөрдө, компоненттеринин орун алуу тартиби эркинирээк болот, ошол себептүү аларды орун алмаштырып айта берүүгө мүмкүн; андан алардын ортосундагы синтаксистик катыш бузулбайт, айрым стилистикалык өзгөрүүлөр гана болушу ыктымал. Мисалы: Аба ысык, күн ачык (К.Ж.). -Күн ачык - аба ысык. Менде ык бар, сизде күч бар («КМ»). - Сизде күч бар, менде ык бар. Атама кат жаз, жеңеме салам айт (А.Т.). - Жеңеме салам айт, атама кат жаз. Билеги күчтүү бирди жыгат, билими күчтүү миңди жыгат (макал). - Билими күчтүү миңди жыгат, билеги күчтүү бирди жыгат.");
                break;
            case 9:
                openSecond("Тема: Татаал сүйлөмдүн түрлөрү.","Маселелер:\n" +
                        "Тең байланыштагы татаал сүйлөмдөр.\n" +
                        "Багыныңкы байланыштагы татаал сүйлөмдөр.\n" +
                        "Көп багыныңкылуу жана аралаш татаал сүйлөмдөр.\n" +
                        "\n1.Татаал сүйлөм – эки, же андан көп жөнөкөй сүйлөмдөрдөн туруп, алар грамматикалык түзүлүшү, маани жана интонациясы жактан бир бүтүндүккө биригип, бирдиктүү татаал ойду билдирет.\n" +
                        "Татаал сүйлөмдүн составындагы жөнөкөй сүйлөмдөр өз ара тең укукта жана маани жактан да, грамматикалык жактан да көз каранды абалда болот. Ошого жараша татаал сүйлөмдөр төмөндөгүдөй болуп бөлүнөт:\n" +
                        "1. Тең байланыштагы татаал сүйлөм.\n" +
                        "2. Багыныңкы байланыштагы татаал сүйлөм.\n" +
                        "3. Аралаш татаал сүйлөм.\n" +
                        "\n1.Татаал сүйлөмдөрдүн тутумундагы жөнөкөй сүйлөмдөрдүн өз ара байланышуу жолдору, алардын ортосундагы маанилик катыш жана баяндоочторунун түзүлүш формасы ар түрдүү. Мисалы: Жапардын ичи бук болду, жүрөгү сокту, деми кысталды (К.Б.). Ал кишинин билгенин биз билбейбиз, ал - доордун булбулу (А.Т .). Көкүрөктө чердүү кайгылары көп болгондуктан, Анархан менен Дарыяхан да муңдууларга мукамдуу үндөрүн кошушту (К.Ж.). Тишиң адамдыкындай болгону менен, акылың айбанча жок экен (А.Т.). Мында 1-2-татаал сүйлөмдүн тутумундагы жөнөкөй сүйлөмдөрдүн баяндоочтору тыянактуу формада уюшулган, ошондуктан алардын ар бири белгилүү бир даражада толук аяктаган ойду билдире алат, семантикасы жагынан ал сүйлөмдөр бири-бири менен байланыштуу, бири-бири менен шартташып турат, бирок бири экинчисине багыныңкы мааниде эмес, ар бири белгилүү бир өлчөмдө өз бетинчелигин сактап турат; өз ара интонация аркылуу байланышкан.\n" +
                        "Ал эми 3-4-татаал сүйлөмдөрдүн тутумундагы биринчи сүйлөмдүн баяндоочу тыянаксыз формада уюшулган, ошондуктан аны өзүнчө бөлүп алсак, кандайдыр бир толук аяктаган ойду билдире албайт; мааниси боюнча өзүнөн кийинки сүйлөмгө багынып, биринде ал сүйлөмдө айтылган ойдун себебин көрсөтсө, экинчисинде ага карама-каршы маани берип турат. Тутумундагы биринчи сүйлөм өзүнөн кийинки сүйлөм менен баяндоочтук формасы аркы-луу, б.а., - ган формасындагы баяндоочко -дыктан мүчөсүнүн жана менен жандоочунун айкашып айтылышы аркылуу байланышкан.\n" +
                        "Татаал сүйлөмдөр тутумундагы жөнөкөй сүйлөмдөрдүн өз ара байланышуу өзгөчөлүктөрүнө жана ал сүйлөмдөрдүн ортосундагы маанилик катыштын мүнөзүнө карай тең байланыштагы татаал сүйлөм жана багыныңкы байланыштагы татаал сүйлөм деп эки чоң топко бөлүнөт.\n" +
                        "Татаал сүйлөмдүн бул эки түрүнүн ортосунда айрым окшоштуктар бар:\n" +
                        "1) Тең байланыштагы татаал сүйлөм да, багыныңкы байланыштагы татаал сүйлөм да маани жагынан өз ара байланыштуу келген эки же андан артык жөнөкөй сүйлөмдөрдүн айкашынан уюшулат.\n" +
                        "2)Экөөнүн тең тутумундагы жөнөкөй сүйлөмдөр мааниси жана интонациялык жактан белгилүү бир бирдикти түзүп, бир бүтүн ойду туюндурат: Караңгылык тарады, жер бети абдан жарык болду (Т.С.). Сен башкарма болгону, бригад болуп иштеп келатам (Т.С.).\n" +
                        "Мында биринчиси - тең байланыштагы, экинчиси - багыныңкы байланыштагы татаал сүйлөм. Экөөнүн тең тутумундагы жөнөкөй сүйлөмдөр маани жагынан өз ара байланыштуу, өз ара шартташып турат. Ушуну менен бирге, бул татаал сүйлөмдөрдүн тутумундагы биринчи сүйлөмү аякталбаган интонация менен айтылып, ал баяндалып жаткан ой өзүнөн кийин дагы улантыларын билдирип турат; экинчи жагынан, бул татаал сүйлөмдөрдүн ар бири, жалпы жонунан, аяктаган интонация менен айтылып, ойдун толук бүткөндүгүн көрсөтүп турат. Ушинтип, бул татаал сүйлөмдөрдү уюштурган жөнөкөй сүйлөмдөр маанилик жана интонациялык жактан бир бүтүндүктү түзүп, бирдиктүү бир татаал ойду түшүндүрүп турат. Эгерде булардын тутумундагы жөнөкөй сүйлөмдөрдү айрым-айрым бөлүп алсак, анда алардын татаал сүйлөмдүн тутумунда туруп туюндурган бирдиктүү мааниси бузулат, ал маанини бере албай калат.\n" +
                        "3) Тең байланыштагы татаал сүйлөмдүн да, багыныңкы байланыштагы татаал сүйлөмдүн да компоненттеринин ортосунда белгилүү бир маанилик (синтаксистик) катыш болот жана алардын туюндурган мааниси көпчүлүк учурда экөөндө тең бири-бирине окшош келет: экөө тең мезгилдик, салыштыруу, себеп-натыйжалаш ж.б. маанилерди туюндура берет. Мисалы: Таң сүрдү, Бектемир устаканага жөнөдү. Эркеайым үйүнө кетери менен, чоң койчу Эсенди ээрчитип келди (К.Ж.).\n" +
                        "Мында эки татаал сүйлөм тең мезгилдик маанини билдирип турат. Ушуну менен бирге тең байланыштагы татаал сүйлөм менен багыныңкы байланыштагы татаал сүйлөмдөрдүн ортосунда орчундуу айырмачылыктар да бар.\n" +
                        "1) Экөөнүн ортосундагы негизги айырмачылык тутумундагы жөнөкөй сүйлөмдөрдүн (эң акыркы сүйлөмдөн башкаларынын) баяндоочторунун уюшулуу формасы менен байланыштуу.\n" +
                        "Тең байланыштагы татаал сүйлөмдүн тутумундагы жөнөкөй сүйлөмдөрдүн баяндоочтору дайыма тыянактуу формада (үч жактын биринде) айтылат. Багыныңкы байланыштагы татаал сүйлөмдөгү жөнөкөй сүйлөмдөрдүн баяндоочу болсо, көбүнчө тыянаксыз формада айтылат. Мисалы:\n" +
                        "а) Сен эртең аларды конторго жыйнагын, мен алар менен кеңешме өткөрөйүн (К.Б.).\n" +
                        "б) Алгачкы он гектар жер айдалып, үрөнү себилди (Н.Б.).\n" +
                        "2) Татаал сүйлөмдүн бул эки түрү тутумундагы жөнөкөй сүйлөмдөрдү бири-бири менен байланыштыруучу грамматикалык каражаттары боюнча да айырмаланышат.\n" +
                        "Тең байланыштагы татаал сүйлөмдөрдүн компоненттери:\n" +
                        "а) санак интонациянын жардамы менен байланышат;\n" +
                        "б) багындырбас байламталар (бириктирүүчү, каршылагыч,\n" +
                        "ажыраткыч) аркылуу тутумдашат.\n" +
                        "Мисалы: \n" +
                        "а) Эртеси кун бүркөлдү, туман түштү, борошолоп кар\n" +
                        "жаады (К.Б.)(санак интонация). Үйдө чечен - жоодо жок (макал) (каршы коюу интонациясы). Анын мойнуна тагылган кызматы бар: ал айылаткомунун катчысы (Т.С.) (түшүндүрүү интонациясы).\n" +
                        "б) Полу сырдалган жана дубалдары акталган (К.Б.). Түн ортосу небак ооп кеткен, бирок уйку келбейт (Ч.А.). Мен быйыл отпуска убагында жайлоого чыгам, же курортко барып эс алам. \n" +
                        "Багыныңкы байланыштагы татаал сүйлөмдөрдүн компоненттери:\n" +
                        "а) тутумундагы биринчи сүйлөмдүн (же сүйлөмдөрдүн) баяндоочтун формасы аркылуу байланышат. Башкача айтканда, алардын этиштик баяндоочуна чакчыл мүчөлөрдүн, айрым жөндөмө мүчөлөрдүн (ган-+дык+тан, ган+да), шарттуу ыңгайдын -са мүчөсүнүн, жандоочтордун, ошондой эле кээ бир башка кызматчы сөздөрдүн айкашып айтылышы менен байланышат. Мисалы: Почточу чал антип-минтип жооп кайтарганча, тигилер жетип келишти (Ч.А.). Ошол жылы күздө малдуулар кыштоого көчүп өткөндөн кийин, биздин айылга аскер шинель кийген бир чоочун жигит келди (Ч.А.). Данияр бутун сүйрөп траптан түшкөндө, күтүп турган эл тең жарыла беришти (Ч.А.). Айдын жарыгы тоо-тоонун чокуларына чачырай баштаган учурда, короодогу койлор эмнегедир дыр үрктү (Н.Б.).\n" +
                        "б) Багындыргыч байламталар аркылуу байланышат: Мал кыш бою колго карап калды, себеби быйыл суук катуу болду. Анархан экөөбүзгө ишенбей келе жатышы да мүмкүн, ошондуктан маселени өзү менен кеңешели (К.Ж.).\n" +
                        "в) Кээде эскертүү маанисиндеги интонация аркылуу байланышат. Мындай учурда татаал сүйлөмдүн биринчи компонентинин баяндоочу, адатта, тыянактуу формада айтылат, бирок экинчи компоненти маани жагынан өзүнүн алдындагы биринчи сүйлөмгө баш ийип, анда айтылган ойдун себебин көрсөтүп тургандыктан, сүйлөм багыныңкы байланыштагы татаал сүйлөм болот. Мисалы: Биздин коркок билишибиз жок: планды өз мөөнөтүндө аткарып койгонбуз.\n" +
                        "3) Тең байланыштагы татаал сүйлөмдүн тутумундагы жөнөкөй сүйлөмдөр маани жагынан белгилүү бир өлчөмдө өз бетинчелигин сактайт жана синтаксистик жагынан өз ара тең укуктуу келет: Анын үнү каргылданып чыкты, көзү жашка толо түштү (А.Т.). Сенсиз менин күнүм жок, сен киши болор бекенсиң? (А.Т.).\n" +
                        "Багыныңкы байланыштагы татаал сүйлөмдөрдө болсо, тутумундагы биринчи сүйлөм экинчисине семантикасы жагынан да, синтаксистик жагынан да баш ийип, анын маанисин ачып, ар түрдүү белгисин (сыпатын, мезгилин, шартын ж.б.) билдирип турат. Ушул өзгөчөлүктөрүнө карай багыныңкы байланыштагы татаал сүйлөмдөрдүн тутумундагы биринчи жөнөкөй сүйлөм (сүйлөмдөр) өз бетинчелигин, тең укуктуулугун толук сактай албайт: Жакшылап үрөн себилсе, бул жерге ак буудай жер көтөргүс болор (Т.С.). Каныш үйдөн чыккыча, Камка кобурап тигиндей узай берди (Т.С.).\n" +
                        "\n\n\nТең байланыштагы татаал сүйлөм.\n\n"+
                        "Тутумундагы жөнөкөй сүйлөмдөрдүн бири экинчисине грамматикалык жактан багынбай, өз ара тең даражада байланышып, синтаксистик түзүлүшү, мааниси жана интонациялык жактан бир бүтүн бирдикти түзүп турган сүйлөмдөр тең байланыштагы татаал сүйлөм деп аталат.\n" +
                        "\nБагынычсыз татаал сүйлөмдүн тутумундагы жөнөкөй сүйлөмдөрдүн баяндоочтору дайыма тыянактуу формада (үч жактын биринде) айтылат, ошол себептүү алар синтаксистик жагынан белгилүү бир өлчөмдө өз бетинчелигин сактап, грамматикалык жактан өз ара тең укуктуу келет. Ушундан улам багынычсыз татаал сүйлөмдүн компоненттери баш жана багыныңкы сүйлөм болуп бөлүнбөйт. Мисалы: Ай аркан бою көтөрүлдү, жылкылар көк жашыл бетке жайылып чыкты. Ай нуру өзөн ичине чачырай элек, эрте жаздагы аба бир аз нымдуу (Т.С.).\n" +
                        "Тең байланыштагы татаал сүйлөмдөр тутумундагы жөнөкөй сүйлөмдөрдүн тең укукта, бири экинчисине көз каранды болбой туруп айкалышы менен мүнөздөлөт. Мисалы: Кылыч жарасы бүтөт, тил жарасы бүтпөйт. Баш кесмек бар, тил кесмек жок. Жакшы сөз жан эргитет, жаман сөз жан кейитет. (макал) Бул мисалдардагы жөнөкөй сүйлөмдөр мааниси жагынан байланышып, интонациялык жактан ажырагыс биримдикте болушту да, ар бир жөнөкөй сүйлөмдүн баяндоочу тыянакталган ойду берип турат.\n" +
                        "Бирок мындан тең байланыштагы татаал сүйлөмдүн тутумундагы жөнөкөй сүйлөмдөр өз-өзүнчө турган жөнөкөй сүйлөмдөрдүн жыйындысы экен деп белгилөөгө болбойт, анткени алар өз ара биригип, бир бүтүн ойду берип турушат. Мисалы: Адамды сөзүнөн тааныбайт, ишинен тааныйт. Жакшыны сөз өлтүрөт, жаманды таяк өлтүрөт.\n" +
                        "Тең байланыштагы татаал сүйлөмдүн тутумундагы жөнөкөй сүйлөмдөр төмөнкүдөй жолдор менен байланышат: 1)интонация, 2) байламталар. Мисалы: Адамдын бийлигин жоготуп койгону аянычтуу эмес, абийрин жоготуп койгону аянычтуу. Ар дайым акыл издеген адамды акылдуу десе болот, ал эми акылдуусунганды – акмак десе болот. Ак ниет адамдарга асылгандар көп болот, бирок абийрин эч ким ача албайт. Дүйшөн бул сүрөттү көптөн бери алып жүргөн болуу керек, себеби кагазы эскирип калган болчу. (Ч.А.)\n" +
                        "Мына ушундай өзгөчөлүгүнө карай тең байланыштагы татаал сүйлөмдөр байламталуу тең байланыштагы татаал сүйлөм жана байламтасыз тең байланыштагы татаал сүйлөм болуп экиге бөлүнөт. Тең байланыштагы татаал сүйлөмдөрдүн тутумундагы жөнөкөй сүйлөмдөрдүн ортосунда төмөнкүдөй маанилик катыштар болот: мезгилдеш, карама-каршы, себеп-натыйжалаш, чечмелөө, толуктоо.\n" +
                        "\n\n\nБайламтасыз тең байланыштагы татаал сүйлөм.\n\n" +
                        "Тең байланыштагы татаал сүйлөмдөрдүн тутумундагы жөнөкөй сүйлөмдөрдүн өз ара интонация аркылуу байланышын байламтасыз тең байланыштагы татаал сүйлөм дейбиз.\n Мисалы: Көп жашаган билбейт, көптү көргөн билет.\n" +
                        "Байламтасыз багынычсыз татаал сүйлөмдүн тутумундагы жөнөкөй сүйлөмдөр өз ара төмөнкүдөй маанилик катышта колдонулат:\n" +
                        "а) Мезгилдеш. Мындай сүйлөмдөрдө окуя бир же ар мезгилде\n" +
                        "болгону көрсөтүлөт: Сессия башталды, студенттер экзамен, зачетторун тапшыра башташты. Мен уктайм, сен сабагыңа даярдан.\n" +
                        "б) Карама-каршы. Бул сүйлөмдөрдө ой бири экинчисине каршы мааниде болот: Тилден уу да тамат, бал да тамат. Ооору кетет – адат кетпейт. (Макал)\n" +
                        "в) Себеп-натыйжалаш. Мында татаал сүйлөмдүн тутумундагы жөнөкөй сүйлөмдөрдүн экинчиси биринчи сүйлөмдөгү айтылган ойдун натыйжасын билдирет: Чагылган жарк этти – Айша жерде сулап жатты.\n" +
                        "г) Чечмелөө. Мындай сүйлөмдөрдүн тутумундагы жөнөкөй сүйлөмдөрдүн бири жалпыланган ойду билдирет, калгандары анын маанисин тактап, чечмелеп көрсөтөт: Бектурган бүгүн бактылуу: эки уулу бала-чакасы менен кашында; алар ойноп-күлүп конокто отурушат.\n" +
                        "д) Байламтасыз тең байланыштагы татаал сүйлөмдүн тутумундагы жөнөкөй сүйлөмдөрдүн экинчи сүйлөмү биринчи сүйлөмдүн маанисин толуктап, ага кошумча түшүндүрмө берет: Студенттердин парламенти түзүлдү – анын башчысы 3-курстун старостасы болду.\n" +
                        "ж) Мында экинчи сүйлөм биринчи сүйлөмдө айтылган ойдун себебин билдирет: Биз абдан чарчадык: жумуш абдан көп экен.\n" +
                        "\n\n\nБайламталуу тең байланыштагы татаал сүйлөм.\n\n" +
                        "Байламталуу тең байланыштагы татаал сүйлөм деп – татаал сүйлөмдүн тутумундагы жөнөкөй сүйлөмдөр байламталар аркылуу байланышып, бирок өз ара тең укук абалда болуп түзүлөөрүн айтабыз.\n" +
                        "\nБайламталуу багынычсыз татаал сүйлөмдү төмөнкүдөй байламталар байланыштырат:\n" +
                        "\n\n\nБайланыштыргыч байламталар: жана, жана да, да, дагы, жана дагы ж.б. Бул байламталар аркылуу байланышкан жөнөкөй сүйлөмдөр окуянын бир мезгилде болгондугун билдирет. Мисалы: Ошондо бала кийимин чечип ыргытты да (кадим жайкысындай, ичиркене бүрүшүп) жээктеги талдын бутагын кармай сууга кирди. Жаңы жылды жыл сайын чогуу тособуз жана майрамды көңүлдүү өткөрүүгө аракеттенебиз.\n" +
                        "\n\n\nКаршылагыч байламталар: а, ал эми, бирок, антсе да, ошентсе да, ошондо да, ошондой болсо да, анткени менен ж.б. Мындай байламталар менен байланышкан жөнөкөй сүйлөмдөр карама-каршы мааниде айтылган ойду билдирет. Мисалы: Анатай экөө а күнү жарашкан жок, бирок катар жүрүп иштей беришти. (Ч.А.) Окуу деген эң сонун, а сиз эмне окубайсыз?\n" +
                        "\n\n\nАжыраткыч байламталар: же, же болбосо. Мисалы: Элдин көзүнчө жыгылып каламбы, же кусуп жиберемби деп коркуп баратты. Бул байламталардын ичинен да байламтасы ар дайым өзүнөн мурунку сүйлөмгө таандык болот да, ошондуктан үтүр байламтадан кийин коюлат. Мисалы: Жээктен алыс болсок да, алачыкты алып кетеби деген коркунуч эрксизден туулат.\n" +
                        "Калган байламталардын баары (ары, жана, а, ал эми, бирок, же, же болбосо, ошондой болсо да, бирок ж.б.) өзүнөн кийинки сүйлөмгө таандык болуп айтылат, ошол себептүү үтүр байламтанын астына коюлат. Мисалы: Бул баягы өлөсөлүү көтөрүмдөрбү, же жаңы туулганбы деп калышат балдарды мактап.\n" +
                        "\n\n\n2. Багыныңкы байланыштагы татаал сүйлөм\n\n" +
                        "Багыныңкы байланыштагы татаал сүйлөмдөрдүн составындагы жөнөкөй сүйлөмдөрдүн бири экинчисине көз каранды же болбосо багыныңкы абалда болот. Багынып турган сүйлөм ойдун тыянагын билдирүүгө мүмкүнчүлүгү болбой, экинчи сүйлөмгө мааниси жагынан көз каранды болуп, экөө бириккенде гана белгилүү тыянакка келет. Мисалы: Жүз сом күткөнчө, бир жолдош күт. (Макал) Мүнүшкөрү келишсе, шумкардын көркү алганда. (Токтогул) Жогорудагы сүйлөмдөрдөгү жүз сом күткөнчө, мүнүшкөрү келишсе деген сүйлөмдөр өз алдынча туруп бүткөн, тыянакталган ойду билдире албайт. Алар кийинки сүйлөмдөр менен катышта болгондо гана ырааттуу бир маанини билдирген касиетке ээ болот.\n" +
                        "\n\n\nОшентип, татаал сүйлөмдүн тутумундагы жөнөкөй сүйлөмдөрдүн бири экинчисине маани жана грамматикалык жактан баш ийип, анын ар түрдүү кырдаалын билгизип түзүлгөн сүйлөмдөрдү багыныңкы байланыштагы татаал сүйлөм дейбиз.\n\n Мисалы: Асмандан жамгыр төгүлсө, жердин көркү ачылат. Алды жакта капчыгайдын чыга бериши агарып, өрөөндөн келген жел бетке сокту. Жогорудагы сүйлөмдөрдүн: асмандан жамгыр төгүлсө, алды жакта капчыгайдын чыга бериши агарып деген сүйлөмдөрдүн баяндоочтору бүткөн ойду билдирбестен, багыныңкы абалда болуп, кийинки сүйлөмдөн жыйынтык чыгарылышын талап кылып турат.\n" +
                        "Багыныңкы байланыштагы татаал сүйлөмдүн тутумундагы жөнөкөй сүйлөмдөр баш жана багыныңкы сүйлөм болуп бөлүнөт.\n" +
                        "Татаал сүйлөмдүн тутумунда туруп, ойдун натыйжасын билдирген сүйлөм баш сүйлөм деп аталат.\n" +
                        "Баш сүйлөмдөгү ойдун ар түрдүү кырдаалын билдирген сүйлөм багыныңкы сүйлөм деп аталат.\n" +
                        "Мисалы: Ал күнү биз адаттагыдай станциядан чыгып, айылга кайтып бара жаткан элек. Асманда жылдыздар төгүлүп, түн кирип калган кез. Мында: Айылга кайтып бара жаткан элек – баш сүйлөм. Кантип? – Ал күнү биз адаттагыдай станциядан чыгып – багыныңкы сүйлөм, ал баш сүйлөмдө айтылган ойдун сыпатын көрсөттү.\n" +
                        "\n\n\nБаш сүйлөм менен багыныңкы сүйлөмдүн байланышуу жолдору.\n\n" +
                        "Багыныңкы байланыштагы татаал сүйлөмдүн тутумундагы багыныңкы сүйлөм менен баш сүйлөм төмөндөгүдөй жолдор аркылуу байланышат.\n" +
                        "Багыныңкы сүйлөмдүн баяндоочуна чакчылдын –а, -бай, - ганы, -ганча, -гыча, -майынча, -майын мүчөлөрүнүн улануусу аркылуу. Мисалы: Күн басылганча, теректин түбүнө далдааландык.\n" +
                        "Багыныңкы сүйлөмдүн баяндоочуна этиштин –са мүчөсү жалгануу аркылуу байланышат. Мисалы: Мээнетиң катуу болсо, татканың таттуу болот.\n" +
                        "Багыныңкы сүйлөмдүн баяндоочу атоочтуктардан болуп, ага жөндөмө мүчөлөр, жандоочтор кошулуп айтылуу менен байланышат. Мисалы: Динар ырдын ар бир сабын айткан сайын, үнү чыйрала берди.\n" +
                        "Багыныңкы байланыштагы татаал сүйлөмдүн тутумундагы баш сүйлөмдүн ар түрдүү кырдаалын, ар түрдүү мүнөздөгү катыштарын билгизгенине карай төмөндөгүдөй түрлөргө бөлүнөт:\n" +
                        "Мезгил багыныңкы сүйлөм.\n" +
                        "\n\n\nСебеп багыныңкы сүйлөм.\n\n" +
                        "Максат багыныңкы сүйлөм. Шарттуу багыныңкы сүйлөм. Салыштырма багыныңкы сүйлөм. Карама-каршы багыныңкы сүйлөм. Сыпат багыныңкы сүйлөм.\n" +
                        "Сан-өлчөм багыныңкы сүйлөм.\n" +
                        "\n\n\nМезгил багыныңкы сүйлөм\n\n" +
                        "Баш сүйлөмдөгү ойдун мезгилин билгизген сүйлөм мезгил багыныңкы сүйлөм деп аталат.\n" +
                        "\nМезгил багыныңкы сүйлөмгө качан? качантан бери? качанга чейин? кайсы убакта? деген суроолор берилет.\n" +
                        "Мезгил багыныңкы сүйлөмдүн баяндоочу баш сүйлөм менен төмөндөгүдөй жолдор аркылуу байланышат:\n" +
                        "1) Багыныңкы сүйлөмдүн баяндоочуна чакчылдын –а, -ганы, - ганча, -майын, -майынча, -гыча, -бай мүчөлөрүнүн жалганышы менен байланышат. Мисалы: Эшикте кар жаап жатканда, үйдө тынч отура албайсың. Тоо боорунда өсүп турган долононун жанына басып барганда, анын мөмөсү жерге ийилип берет.\n" +
                        "2) Мезгил багыныңкы сүйлөмдүн баяндоочуна шарттуу ыңгайдын –са мүчөсү улануу менен. Мисалы: Жаңы жылдык майрамдар аяктаса, студенттердин кышкы сессиясы башталат.\n" +
                        "3) Багыныңкы сүйлөмдүн баяндоочу атоочтуктардан болуп, ага жөндөмө мүчөлөр же чейин, соң, дейре, убакта, учурда сыяктуу жандоочтордун катышы менен байланышат. Мисалы: Байтемир жол кыдырып бүткөн соң, биз дасторконго отурдук.\n" +
                        "Мезгил багыныңкы сүйлөмдүн тутумундагы баш сүйлөм менен багыныңкы сүйлөмдүн ортосуна үтүр белгиси коюлат.\n" +
                        "\n\n\nСебеп багыныңкы сүйлөм.\n\n" +
                        "Баш сүйлөмдөгү ойдун себебин билдирген сүйлөм себеп багыныңкы сүйлөм деп аталат.\n" +
                        "Себеп багыныңкы сүйлөмгө эмне себептен? эмне үчүн? эмне себептүү? эмнеликтен? деген суроолор берилет.\n" +
                        "\nБаш сүйлөм менен себеп багыныңкы сүйлөм төмөнкүдөй жолдор аркылуу байланышат:\n" +
                        "1) Себеп багыныңкы сүйлөмдүн баяндоочуна –гандыктан уландысынын жалганышы менен байланышат. Мисалы: Кыргызстанда ар түрдүү улуттардын өкүлдөрү жашагандыктан, жыйындар көбүнчө орус тилинде өткөрүлүүдө.\n" +
                        "2) Себеп багыныңкы сүйлөмдүн баяндоочуна атоочтуктун – ган мүчөсү уланып, ага соң, үчүн, себептүү, кийин деген жандооч сөздөрдүн жалганышы менен тутумдашат. Мисалы: Поезддер өтүп кеткен соң, бир топко чейин станцияда эч жан жоктой ээн тынчтык өкүм сүрөт.\n" +
                        "3) Себеп багыныңкы сүйлөмдүн баяндоочуна кыймыл атоочтордун –уу, -ыш уландылары уланып, ага үчүн деген жандооч сөздүн кошулушу менен байланышат. Мисалы: Транспорттордун тынымсыз каттап турушу үчүн, шаарыбыздын жолдору мыкты болуш керек.\n" +
                        "4) Багыныңкы сүйлөмдүн баяндоочу чыгыш жөндөмөсүндөгү жана –ган уландысындагы атоочтуктан болуп, андан соң кийин деген жандоочтун кошулушу менен жасалат. Мисалы: Студенттер кышкы каникулдан келгенден кийин, алар практикага барууга даярданышат.\n" +
                        "5) Себеп багыныңкы сүйлөмдүн баяндоочуна чакчылдын –ып, -бай мүчөлөрүнүн жалганышы менен байланышат. Мисалы: Аба ырайы бир калыпта болбой, кан басымы көтөрүлгөндөрдүн саны көбөйдү.\n" +
                        "\n\n\nМаксат багыныңкы сүйлөм\n\n" +
                        "Баш сүйлөмдөгү ойдун аткарылыш максатын билгизген сүйлөмдү максат багыныңкы сүйлөм дейбиз.\n" +
                        "\nМаксат багыныңкы сүйлөм эмнеге? эмне үчүн? эмне максат менен? деген суроолорго жооп берет.\n" +
                        "Кыргыз тилинде максат багыныңкы сүйлөмдүн колдонулушу анчалык көп өнүккөн эмес, ошого байланыштуу баш сүйлөм менен багыныңкы сүйлөмдүн өз ара байланышуу жолдору да саналуу гана:\n" +
                        "1) Максат багыныңкы сүйлөмдөгү баш жана багыныңкы сүйлөмдүн байланышынын эң активдүү жолу болуп багыныңкы сүйлөмдүн баяндоочу буйрук ыңгайдын –сын, атоочтуктун –ар, этиштин –айын мүчөсүнөн болуп, ага деп деген жардамчы этиш кошулуп айтылышы болуп саналат. Мисалы: Учуп кетпесин деп, анын эки канатын тең кесип салдык. (К.Ж.) Бүгүн экзамен эрте башталар деп, студенттер институтка толук келишти. Зинага кол кабыш кылайын деп, Сатар анын артынан жүгүрүп чыкты.\n" +
                        "2) Багыныңкы сүйлөмдүн –оо, -өө, -уу, -үү, -ыш, -бас мүчөлөрү менен аяктаган баяндоочуна үчүн деген жандооч кошулуп айтылуу аркылуу байланышат. Мисалы: Окуучулардын сүймөнчүлүктүү мугалими болуу үчүн, азыртан методикалык адабияттарды көп окуу керек.\n" +
                        "3) Багыныңкы сүйлөмдүн баяндоочу деген менен, деген көрүнөт сыяктуу сөз тизмектери кошулуп айтылуу аркылуу түзүлөт. Мисалы: Апамдын тынчсызданганы басылсын деген ой менен, оюма келген байланышсыз нерселерди сүйлөй бердим.(Ө.Д.)\n" +
                        "4) Максат багыныңкы сүйлөмдүн этиштик баяндоочуна болуп деген жардамчы милдет аткарган сөз кошулуп, ал эми негизги баяндоочуна кыймыл атоочтун –макчы мүчөсүнүн улануусу менен байланышат. Мисалы: Конкурстук комиссиянын чечимин укмакчы болуп, университеттин алдына жетип бардым. (Ө.Д.)\n" +
                        "\n\n\nОрун багыныңкы сүйлөм\n\n" +
                        "Баш сүйлөмдөгү ойдун ордун билгизген сүйлөм орун багыныңкы сүйлөм деп аталат.\n" +
                        "\nОрун багыныңкы сүйлөмгө кайда? кайдан? кайсы жерден? кайсы жерде? каерде? деген суроолор берилет.\n" +
                        "Орун багыныңкы сүйлөм баш сүйлөм менен төмөндөгүдөй жолдор менен байланышат:\n" +
                        "1) Багыныңкы сүйлөмдүн тутумундагы кайда? кайдан? кайсы жерде? каерде? деген сөздөр болуп, баяндоочко –са мүчөсү уланып байланышат. Мисалы: Саанчы кайда болсо, сабаа ошол жерде. Оору кайда болсо, жан ошол жерде.\n" +
                        "2) Кээ бир учурларда орун багыныңкы сүйлөмдүн тутумунда кайда, кайдан деген сөздөр колдонулса, баш сүйлөмдө ошондо, ошол жерде, ошол жакта ж.б. орундук, мейкиндик мааниси бар сөздөр колдонулбай деле айтыла берет. Мисалы: Кыз кайда басса, жигит артынан басат.\n" +
                        "\n\n\nСыпат багыныңкы сүйлөм\n\n"+
                        "Баш сүйлөмдөгү кыймыл-аракеттин кандайча, кантип аткарылыш мүнөзүн билдирген багыныңкы сүйлөмдөрдү сыпат багыныңкы сүйлөм дейбиз.\n" +
                        "\nСыпат багыныңкы сүйлөмгө кантип? кандайча? кандай? деген суроолор берилет.\n" +
                        "Сыпат багыныңкы сүйлөмдүн тутумундагы баш сүйлөм менен багыныңкы сүйлөмдүн байланышуу жолдору төмөндөгүчө:\n" +
                        "1) Сыпат багыныңкы сүйлөмдүн баяндоочуна чакчылдын –ып, -а мүчөлөрү жалгануу менен байланышат. Мисалы: Инкамал апай кыйык сала бир карап коюп, сабагын уланта берди. (Ч.А.)\n" +
                        "2) Багыныңкы сүйлөмдүн баяндоочуна –бай мүчөсү уланат да. Мисалы: Зоолору күмүш кымкап кийгендей, Алай тоолору асман мелжийт. (К.Ж.)\n" +
                        "3) Багыныңкы сүйлөмдүн баяндоочуна –гансып мүчөсү уланып, сыпат багыныңкы сүйлөм жасалат. Мисалы: Ортобузда эч нерсе болбогонсуп, Данияр мурдагыдай эле бир сырдуу, токтоо. (Ч.А.)\n" +
                        "\n\n\nШарттуу багыныңкы сүйлөм\n\n" +
                        "Баш сүйлөмдөгү айтылган окуянын, кыймыл-аракеттин, ойдун ишке ашуу же ашпоо шартын билгизип турган сүйлөмдү шарттуу багыныңкы сүйлөм дейбиз.\n" +
                        "\nШарттуу багыныңкы сүйлөмгө кантсе? эмне болсо? деген суроолор берилет. Мисалы: Улугуң ит болсо, (эмне болсо?) сени мышык кылат.\n" +
                        "Шарттуу багыныңкы сүйлөмдөр кыргыз тилинде кеңири колдонулат.\n" +
                        "Шарттуу багыныңкы сүйлөмдүн ичиндеги баш сүйлөм менен багыныңкы сүйлөмдөр төмөндөгүдөй жолдор аркылуу байланышат:\n" +
                        "1) Багыныңкы сүйлөмдүн баяндоочуна шарттуу ыңгайдын –са мүчөсүнүн уланышы менен байланышат. Мисалы: Жазында жамгыр көп жааса, эгиндин түшүмү мол болот. Сөксөөлдөй атаңыздын алдында алсыз экениңизди билсем, сокур сезимге алданбайт элем. (Э.Т.)\n" +
                        "2) Багыныңкы сүйлөмдүн баяндоочуна чакчылдын –бай мүчөсү улануу менен түзүлөт. Мисалы: Шагылы болбой зоо болбойт, шайыры болбой эл болбойт. (Мак.) Көп уйкуга кызыкпай, сергек болгон жакшы экен. (Б.А.)\n" +
                        "3) Шарттуу багыныңкы сүйлөмдөрдүн баяндоочуна –да мүчөсүнүн кошулушу менен уюшулат. Мисалы: Алдаркөсө байды чекеге бир черткенде, анын чекесине бармагы батып кетмек. (Кырг.эл жомогу)\n" +
                        "Шарттуу багыныңкы сүйлөмдөгү баш сүйлөм менен багыныңкы сүйлөмдөрдүн ортосу үтүр белгиси менен ажыратылат.\n" +
                        "Эгерде шарттуу багыныңкы сүйлөмдүн составын бир тутумдуу жөнөкөй сүйлөмдөр түзүп, алар өз ара тең салмак катышында болсо, анда алардын ортосуна сызыкча белгиси коюлат. Мисалы: Таш койсоң – таш аласың, кыш койсоң – кыш аласың. Бакса – бала, бакпаса – балээ.\n" +
                        "\n\n\nСалыштырма багыныңкы сүйлөм.\n\n" +
                        "Баш сүйлөмдөгү ойдун багыныңкы сүйлөмдөгү окшоштугу же, айырмачылыгы салыштыруу иретинде айтылган сүйлөмдөрдү салыштырма багыныңкы сүйлөм дейбиз.\n" +
                        "\nСалыштырма багыныңкы сүйлөмгө кандай? эмне болгон сыяктуу? эмне болгондон бетер? эмне болгончо? деген суроолорго жооп берет.\n" +
                        "Салыштырма багыныңкы сүйлөмдөгү баш сүйлөм менен багыныңкы сүйлөм төмөндөгүдөй жолдор аркылуу байланышат:\n" +
                        "1) Багыныңкы сүйлөм менен баш сүйлөмдүн байланышынын эң активдүү жолу болгон чакчылдын –ып мүчөсү менен жасалат. Мисалы: Кайдагы бир итирейген улутчулдун керооздугуна татыктуу жооп берип, чокуга чокмор менен чапкандай кылган тергөөчүгө шатырата кол чабышты.\n" +
                        "2) Багыныңкы сүйлөмдүн баяндоочуна шарттуу ынгайдын –са мүчөсү уланып, ага кандай, ал эми баш сүйлөмгө ошондой деген сөздөр кошулуп уюшулат. Мисалы: Энеден кандай тарбия көрсө, бала да ошондой болот.\n" +
                        "3) Багыныңкы сүйлөмдүн баяндоочуна чакчылдын –ганча, - гыча мүчөлөрү улануу менен түзүлөт. Мисалы: Тил албасты жумшаганча, тына-тына өзүң бар.\n" +
                        "4) Багыныңкы сүйлөмдүн атоочтуктан болгон баяндоочуна – дай мүчөсү жалганып жасалат. Мисалы: Жакшы аялдын белгиси – Аста сүйлөп күлүшү.\n" +
                        "Жыла баскан жоргодой,\n" +
                        "Маин болот жүрүшү.(Б.А.)\n" +
                        "\n\n\nКарама-каршы багыныңкы сүйлөм\n\n" +
                        "Баш сүйлөмдөгү ойго карама-каршы мааниде айтылган\n" +
                        "багыныңкы байланыштагы татаал сүйлөмдүн түрүн карама- каршы багыныңкы татаал сүйлөм дейбиз.\n" +
                        "\nКарама-каршы багыныңкы сүйлөм эмне болсо да? эмне болгону менен? эмне болгону болбосо? кантсе да? деген суроолорго жооп берет.\n" +
                        "Кыргыз тилинде карама-каршы багыныңкы сүйлөмдөр мезгил, орун, шарттуу, себеп ж.б. багыныңкы сүйлөмдөр сыяктуу эле көбүрөөк өнүккөн жана анын баяндоочунун баш сүйлөм менен байланышуу жолдору да ар түрдүү:\n" +
                        "1) Карама-каршы багыныңкы сүйлөмдүн баяндоочу шарттуу ыңгайдын –са мүчөсү менен аяктап, андан кийин да (дагы, деле, дале) байламтасы кошулуп айтылат. Мисалы: Аял канчалык акылдуу болсо да, эркектен бийик боло албайт.\n" +
                        "2) Багыныңкы сүйлөмдүн баяндоочуна –бы мүчөсүнүн жалганышы менен карама-каршы багыныңкы сүйлөм жасалат. Мисалы: Даниярдын таарынчысы, ызасы жазылдыбы, биз аны биле алган жокпуз. (Ч.А.)\n" +
                        "3) Багыныңкы сүйлөмдүн баяндоочуна этиштин –са мүчөсүнүн тутумдашуусунан карама-каршы багыныңкы сүйлөм түзүлөт.Мисалы: Жамийла кээде анчейин тамаша үчүн тигиге үстөмдүк менен күлө сүйлөсө, кээде анын бары-жогун такыр эле унутуп коет.(Ч.А.)\n" +
                        "4) Багыныңкы сүйлөмдүн баяндоочу атоочтуктун –ган мүчөсүнүн уланышы менен жасалып, ага менен деген жардамчы сөздүн кошулушунан түзүлүп, уюшулат. Мисалы: Садык сыртынан байкатпаган менен, ичинен абдан эле намыстанып жүрдү. (Ч.А.)\n" +
                        "5) Багыныңкы сүйлөмдүн баяндоочуна –дейм деген жардамчы этиш кошулуп айтылып, карама-каршы багыныңкы сүйлөм түзүлөт. Мисалы: Каргайын дейм, жалгызсың, каргабайын дейм, жан койбогон байкушсуң. («Кожожаш»)\n" +
                        "\n\n\n3. Көп багыныңкылуу татаал сүйлөм.\n\n" +
                        "Эки же андан көп багыныңкы татаал сүйлөмдөр баш сүйлөмгө багынып, аны менен маанилик катышта болсо, ал көп багыныңкылуу татаал сүйлөм деп аталат.\n" +
                        "\nМындай көп багыныңкы сүйлөмдүн тутумунда ар түрдүү синтаксистик милдет аткарган бир нече багыныңкы сүйлөмдөр бир гана баш сүйлөмгө багынып турат. Мисалы: Жамийла күлгөндө, анын чымкый кара сүйрүрөөк көзүндө ден соолуктун, жаштыктын ашып- ташкан күчү ойт берип, ал эмнегедир өзүнчө бой карангансып, секетбайдын туздуусунан ырдап жиберет.\n" +
                        "Көп багыныңкылуу сүйлөмдөрдөгү баш сүйлөм менен байланышкан багыныңкы сүйлөмдөр бир өңчөй жана бир өңчөй эмес мааниде колдонулушу мүмкүн. Мисалы: Күн өйдөлөп, талаа ысыган сайын, аттардын сооруларынан жылга-жылга тер кетип, үйүр алып, кошо эрчишкен чымындар чаң аралаш учуп конот.(Ч.А.) Даниярдын артынан келе жаткандар анын ыгына көнүп, ал токтогондо туруп калышып, ал басканда басышып, Даниярга карайлап, алар да кубаттан тайып, кара терге түшкөн.(Ч.А.)\n" +
                        "\n\n\nАралаш татаал сүйлөм\n\n" +
                        "Тең байланыштагы жана багыныңкы байланыштагы\n" +
                        "татаал сүйлөмдөрдүн белгилери болгон сүйлөмдөрдү аралаш татаал сүйлөм дейбиз.\n\n Мисалы: Душман жеңилип, согуш аяктаганда, алдастаган дүйнө тымтырс боло түштү да, мурда согуштун кызуусу менен анча байкалбаган нерселер ошондо элдин жүрөгүн бир сыйра кансыратып алды, анткени жеңиш олжо алып келген жок, ал майрам болуп да келген жок, ал бизге согуштан аман калган жоокерлердин сүрдүү кейпинде нечен жолдор басып, таманы жешилген солдат өтүгүн кийип келди (Ч.А.).\n" +
                        "Бул аралаш татаал сүйлөм бир нече жөнөкөй сүйлөмдөн түзүлгөн. Мында «Душман жеңилип, согуш аяктаганда» деген - татаал мезгил багыныңкы сүйлөм, андан кийинки баш сүйлөмү да тең байланыштагы татаал сүйлөм («анткени» байламтасына чейин), ал баш сүйлөм менен 3 жөнөкөй сүйлөм анткени деген байламта аркылуу тутумдашып, анын себебин билдирип турат.\n" +
                        "Тумагы башынан учуп, күрткүгө башы киргенде, бирде муздак кар уруп, ичи-койнуна кар кирип, карысына да шапалагы оролуп, же турар чактысы жок, же белбоосунан бууранын боосун бошотор чамасы жок, оңкобаш атып кете берди /Эдигей/ (Ч.А.).");
                break;
            case 10:
                openSecond("Тема: Төл жана бөтөн сөз.","Маселелер:\n" +
                        "1. Бөтөн сөз. Тике жана кыйыр сөздөр.\n" +
                        "2. Төл сөз менен тике сөздөр катышкан сүйлөмдөр.\n" +
                        "3. Төл жана бөтөн сөздөргө коюлуучу тыныш белгилери.\n" +
                        "\n1.Бир нерсе тууралуу жазуу түрүндө же оозеки түрүндө баяндама берип жатканда, өз сөзүбүзгө башка бирөөнүн сөзүн да катыштырып айтууга туура келет. Мындай конструкциянын ичиндеги өзүбүздөн сөзүбүз төл сөз деп, башка бирөөнүн сөзү бөтөн сөз деп аталат.\n" +
                        "Адатта, көркөм адабий чыгармалардагы каармандардын сөзү жана илимий, публицистикалык чыгармалардагы башка авторлордун эмгектеринен келтирилген цитаталар бөтөн сөзгө кирет. Мисалы: Анан Чернов айтты: «Жолукканың ырас болбодубу, жүр менин кабинетиме, силерге кат бар, разъездге ала кет», - деди (Ч.А.). Мындагы автордун сөзү - Анан Чернов айтты; деди. Бул - төл сөз. Башка бирөөнүн (кейипкердин) сөзү - «Жолукканың ырас болбодубу, жүр менин кабинетиме, силерге кат бар, разъездге ала кет». Бул -бөтөн сөз.\n" +
                        "Төл сөздүн ичинде бөтөн сөздү колдонууда кээде анын мазмуну гана эмес, сырткы формасы: лексикалык тутуму, грамматикалык курулушу да өзгөрүлбөй, так берилсе, кээде жалпы мазмуну гана сакталып, лексикалык тутуму, грамматикалык түзүлүшү айрым өзгөрүүлөргө дуушар болот. Бөтөн сөз колдонулушундагы ушул өзгөчөлүктөрүнө карай тике сөз жана кыйыр сөз болуп бөлүнөт.\n" +
                        "Башка бирөөнүн сөзүнүн мазмуну да, лексикалык, грамматикалык өзгөчөлүгү да өзгөрүлбөй, так сакталып берилсе, тике сөз деп аталат.\n" +
                        "Тике сөз дайыма кейипкердин (персонаждын) өзү тарабынан айтылат. Мисалы: «Таза балээ эми болот турбайбы» - деп, Эдигейдин эси эңгирей баштады (Ч.А.). - Балдарга деңиз тууралуу айта жүр! -деп шыбырады Абуталип (Ч.А.).Мындагы «Таза балээ эми болот турбайбы», «Балдарга деңиз тууралуу айта жүр» дегендер - тике сөз. Булардын мааниси, лексикалык тутуму, грамматикалык түзүлүшү өзгөртүлбөй, алар кейипкерлер (Эдигей, Абуталип) тарабынан кандай мааниде, кандай формада айтылса, ошол боюнча толук сакталып берилген.\n" +
                        "\n\n\nБашка бирөөнүн сөзү лексикалык тутуму, синтаксистик түзүлүшү жагынан айрым өзгөртүүлөргө учурап, жалпы мазмуну гана берилсе, кыйыр сөз деп аталат.\n" +
                        "\nКыйыр сөздө кейипкердин ою анын өзү тарабынан айтылбастан, автордун атынан берилет. Мисалы: Садык акем анда госпиталда. Куда кааласа күз ортосуна карай бошоп келермин дептир (Ч.А.). Мында Садыктын катта жазган сөзүнүн мазмуну гана берилип, синтаксистик түзүлүшү так сакталган эмес. Экинчиден, бул кыйыр сөздөгү ой кейипкердин (Садыктын) өзү тарабынан айтылбастан, автордун атынан берилип жатат. Эгерде бул сүйлөмдүн синтаксистик курулушун так берүү керек болсо, төмөнкүдөй болмокчу: Садык акем катында: «Куда кааласа, күз ортосуна карай бошоп келермин», - дептир.\n" +
                        "Кыйыр сөздүн түзүлүш өзгөчөлүгүн өзгөртүүгө дуушар боло турган тике сөздүү сүйлөмдүн структурасы менен салыштырып караганда ачык байкоого болот. Тике сөздүү сүйлөмдү кыйыр сөзгө айланды-ганда анын синтаксистик түзүлүшү олуттуу өзгөрүүгө учурайт: тике сөздүн кимге тиешелүү экендигин билдирип турган автордун сөзү кыйыр сөздө ээ, баяндооч катары келет, ал эми тике сөздүн мазмунун берип турган сөз тизмеги кыйыр сөздүн ортосуна (анын ээ менен баяндоочунун арасына) жайгашып, объектилик маанини билдирет. Кыйыр сөздүн баяндоочу көбүнчө сүйлөө, ойлоо маанисин билдирүүчү деди, дешти, деген, деп айтты, сурады, ойлоду ж.б. деген сыяктуу этиштерден болот. Эгерде тике сөз менен төл сөз бири-биринен кыска тыным аркылуу ачык бөлүнүп айтылса, кыйыр сөздө тике сөзгө мүнөздүү болгон андай интонациялык өзгөчөлүк сакталбайт. Экинчи жагынан, жогоруда белгиленгендей, тике сөз кейипкердин өзү тарабынан айтылса, кыйыр сөздөгү ой автордун атынан берилет. Салыштыргыла:\n" +
                        "\n\n\nТике сөз\n\n" +
                        "- Ой, түш де аны аттан, үйгө кирсин! -деди үй ээси (Ч.А.). Үрпөйгөн Эдигей баса айтты: - Мен эчтемеге кол койбойм! (Ч.А.).\n" +
                        "- Мага Чабдар көбүрөөк жагат, - деди Султанмурат атасына (Ч.А.).\n" +
                        "\n\n\nКыйыр сөз\n\n" +
                        "Үй ээси аны аттан түшүп, үйгө кирсин деди.\n" +
                        "Эдигей мен эчтемеге кол койбойм деп баса айтты. Султанмурат атасына мага чабдар ат көбүрөөк жагат деди. \n" +
                        "Мында биринчи тике сөздүү сүйлөмдөгү «деди үй ээси» деген автордун сөзү (төл сөз) кийинки кыйыр сөздө баш мүчө катары келди; ал эми «- Ой, түш де аны аттан, үйгө кирсин!» деген тике сөздүн маанисин берип турган кыйыр сөздөгү «аны аттан, үйгө кирсин» деген соз тизмеги кыйыр сөздүн «деди» деген баяндоочуна карай объектилик катышты билдирип турат. Ошондой эле, тике сөздүү сүйлөмдө тике сөз менен төл сөздүн чеги кыска тыным аркылуу ачык жиктелип турса, кийинки кыйыр сөздө андай интонациялык өзгөчөлүк жок. Тике сөз кейипкердин өзү тарабынан айтылса, кыйыр сөз автордун атынан айтылып жатат. Кийинки тике сөздүү сүйлөмдөрдү кыйыр сөзгө айландырууда да алар ушул сыяктуу өзгөрүүлөргө учураган.\n" +
                        "Тике сөздөн тутумунда каратма сөз болсо, андай тике сөздү кыйыр сөзгө айландырууда каратма сөздүн көбүнчө барыш жөндөмөдө айтылып, толуктоочтук милдетти аткарып калат. Салыштыргыла:\n" +
                        "Кезек күтүп тургандар: «Карыя, майдаңызды, тез бериңиз!» - дешти (Т.С.). Кезек күтүп тургандар карыяга майдаңызды тез бериңиз дешти.\n" +
                        "Тике сөз илептүү сүйлөм болсо, аны кыйыр сөзгө өзгөртүүдө, адатта, анын эмоционалдык мааниси жоюлуп, илептүү сүйлөм жай сүйлөм менен алмаштырылат. Эгерде мындай маанидеги тике сөздүн тутумунда сырдык сөз болсо, ал кыйыр сөздө түшүрүлүп айтылат. Мисалы:\n" +
                        "- Баракелде, тапкан тура акылды! - деп, Чаргын ачуулана кетти (Т.С.).\n" +
                        "- Чаргын тапкан тура акылды деп ачуулана кетти.\n" +
                        "- Бали! Тартынбай сүйлөп жатат! - деп, Ыманбай ага ыраазы болуп отурду (Т.С.).\n" +
                        "- Ыманбай ага тартынбай сүйлөп жатат деп ыраазы болуп отурду.\n" +
                        "Тике сөз суроолуу сүйлөм болсо, аны кыйыр сөз менен алмаштырууда суроо тикеден-тике коюлбастан, ал суроолуу сүйлөмдө суралган ой тууралуу жайынча гана баяндама берилет жана анда суроолуу сүйлөмгө мүнөздүү болгон суроо интонациясы болбойт. Салыштыргыла:\n" +
                        "Баягы доктур жигит көргөн сайын сурап калат: «- Кандай апа, уулуң чоноюп жатабы?» - дейт (Ч.А.).\n" +
                        "- Баягы доктур жигит көргөн сайын уулуң чоңоюп жатабы деп сурап калат.\n" +
                        "- Даниярдан сайдагы аттар сеникиби деп сурадым.\n" +
                        "- Данике, сайдагы аттар сеникиби? -дедим(Ч.А.).\n" +
                        "Айрым учурларда суроо маанисиндеги тике сөздү кыйыр сөзгө өзгөртүү суроолуу интонацияны баяндоо интонациясы менен алмаштырып айтуу аркылуу жүзөгө ашырылышы мүмкүн. Мисалы: Райондон келген киши: - Китепканааңарда канча китеп бар? - деди. Ал: - Окууңду качан бүтөсүң? деди.\n" +
                        "Бул келтирилген мисалдарда суроолуу тике сөз катышкан сүйлөм менен кыйыр сөз түзүлүшү жагынан, негизинен, бирдей экендиги көрүнүп турат. Бул жерде төл сөз менен тике сөздүн ортосундагы кыска тынымды жана тике сөздөгү суроо интонациясын өзгөртүп айтуу менен тике сөздүн негизинде кыйыр сөз уюшулган.\n" +
                        "Бардык эле тике сөздү кыйыр сөзгө эркин өзгөртүп айта берүүгө болбойт. өзгөчө тике сөз бир нече сүйлөмдүн өз ара тутумдашып айтылышынан уюшулуп, түзүлүшу жагынан татаал, берген мааниси ар түрдүу болсо, аны кыйыр сөзгө өзгөртүп айтуу ошончолук кыйын болот. Мисалы: Мен бул кишинин жөнөкөйлүгүнө, адамкерчилигине ичим жылып, бир күнү жашырбай эле өзүнө айттым: - Эже, айыл жакка барып, эл-журт менен жолугушуп келсеңиз кантер эле? Сизди сыртыңыздан баары эле билип сыймыктанышат экен, бирок көпчүлүгү тааныбай. Атактуу окумуштуу кызыбыз айылын теңине албай чанып жүрөбү деген сөздөр бар (Ч.А.). Бул тике сөздү кыйыр сөзгө өзгөртүп айтуу чыны менен эле кыйын, мүмкүн да эмес.\n" +
                        "Тике сөз оозеки кепте да, жазууда да учурайт, өзгөчө көркөм адабий чыгармаларда өтө активдүү колдонулат.\n" +
                        "\n\n\nТике сөздүн мүнөздүү белгилери:\n" +
                        "1) башка бирөөнүн сөзүнүн мазмуну, лексикалык, грамматикалык өзгөчөлүгү өзгөртүлбөй, так сакталып берилет;\n" +
                        "2) дайыма кейипкердин өзү тарабынан айтылат;\n" +
                        "3) тике сөз ар убакта төл сөздүн (автордун сөзүнүн) коштоосу менен айтылат.\n" +
                        "\n\n\n2. Төл сөз. Төл сөздүн (автордун сөзүнүн) милдети: айтылып жаткан сүйлөмдүн тутумунда башка бирөөнүн да сөзү бар экендигин белгилөө жана ал сөз ким тарабынан айтылгандыгын, кимге арналгандыгын көрсөтүү; ошондой эле тике сөздүн кандай кырдаалда, кандай мүнөздө айтылгандыгын билдирүү болуп эсептелет. Мисалы: Карып кызды кайра өзүнө тартып: - Качан келейин? -деди, көзүн күлүңдөтө сүйлөп (М.Аб.). Мындагы «Карып кызды кайра өзүнө тартып, деди», «көзүн күлүндөтө сүйлөп» деген төл сөз «Качан келейин» деген тике сөз ким тарабынан (Карып) айтылгандыгын, кимге (кызга) багытталгандыгын жана ал тике сөз кандай шартта, кандай мүнөздө айтылгандыгын көрсөтүп турат.\n" +
                        "Тике сөз менен төл сөз экөө биригип келип, тутумуна бөтөн сөз катышып айтылган татаал сүйлөмдүн өзгөчө бир түрүн уюштурат. Мындай түзүлүштөгү татаал сүйлөмдөр ар бири өз алдынча турган эки бөлүктүн (тике сөз менен төл сөздүн) бири-бири менен мааниси боюнча жана интонациялык жактан байланышып айтылышы аркылуу уюшулат. Мисалы: Алиман сөзүн алдын ала айтты: «Эртен чарбадан кол бошобойт, эне, жууп алгын, чачыңды тарап берейин» (Ч.А.).\n" +
                        "Мында төл сөз менен тике сөз, биринчиден, маани жагынан өз ара байланыштуу: тике сөз төл сөздүн мазмунун чечмелеп, Алиман эмне деп айтканын тактап көрсөтүп турат; экинчиден, ал экөө бири- бири менен интонациянын жардамы менен байланышып, татаал сүйлөм уюшулган. Ушуну менен бирге, тике сөз менен төл сөз бири- бири менен ар түрдүү формада (деп, дейт, деди, деген ж.б.) колдонулуучу де жардамчы этиши аркылуу да активдүү тутумдашып айтылат. Мисалы: - Тиги да күндүзү жумушта болот, - деди Эдигей (Ч.А.). В.И.Ленин: «Тил адам баласынын өз ара катнашынын маанилүү куралы болуп саналат», - деген. - Катыңардын каары катуу экен, кантип келбей коёсуң, - деп Эдигей күлдү (Ч.А.).\n" +
                        "Минтип тике сөз менен төл сөздүн тутумдашып айтылышы аркылуу уюшулган татаал сүйлөмдөрдө, адатта, төл сөз баш сүйлөмдүн функциясын, тике сөз багыныңкы сүйлөмдүн функциясын аткарат. Мисалы: «Ырдай турган кишин тапкан экенсиң!» - деп күлүп койдум мен (Ч.А.). Бул татаал сүйлөмдүн тутумундагы тике сөз менен төл сөздү маанилик катышына карай талдасак: «Күлүп койдум мен» деген - баш сүйлөм; ал эми «Ырдай турган кишини тапкан экенсиң!» - деп деген тике сөз «кантип? (күлүп койду)» деген суроого жооп берип, сыпат (амал) багыныңкы сүйлөмдүк функцияда турат.\n" +
                        "Эгерде баш сүйлөмдүк функцияда турган төл сөздүн баяндоочу айтуу, сүйлөө маанисиндеги өтмө этиштен (мисалы, «аткан») болсо, ал өзүнүн алды жагында эмне жөнүндө айтылгандыкты билдирүүчү объектинин турушун талап кылат. Мындай объектилик маани тике сөз аркылуу да берилиши мүмкүн. Мындай учурда тике сөз толуктоочтук маанидеги багыныңкы сүйлөмдүн маанисинде колдонулат. Мисалы: «Жаш жазуучулар адабият тарыхын жакшы билүү керек» - деп, Максим Горький айткан. Бул татаал сүйлөмдө «Максим Горький айткан» - баш сүйлөм, баяндоочунун милдетин «айткан» деген өтмө этиш аткарып турат. Ушуга байланыштуу «Жаш жазуучулар адабият тарыхын жакшы билүү керек» - деп» деген - тике сөз «Горький эмнени айткан?» деген суроого жооп берип, сүйлөө процесси багытталган объектини көрсөтүп, толуктооч маанисиндеги багыныңкы сүйлөмдүк маанини берип турат.\n" +
                        "Эгерде тике сөз төл сөз менен деген жардамчы этиши аркылуу тутумдашып айтылып, өзүнөн кийинки заттын кандайдыр бир белгисин көрсөтүп турса, тике сөз ошол «деген» жардамчы этиши менен бирдикте туруп, жайылма аныктоочтук функцияда келет. Мисалы: Аңгыча: - «Туугандар, үйгө киргиле! - деген» тике сөз «кандай өтүнүч угулду?» деген суроого жооп берип, жайылма аныктоочтук милдетти аткарып турат.\n" +
                        "Эгерде төл сөз менен тике сөз бири-бири менен интонация аркылуу тутумдашса, анда алар тең байланыштагы татаал сүйлөмдүк мааниде колдонулат: Ал убакта жардамчысына катуу буюрду: «Эмне карап турасың, жүргүз моторду!».\n" +
                        "Адатта: 1) башка бирөөнүн сөзү катары берилет; 2) бирөөнүн айтылбаган ою тике сөз катары берилет; 3) сүйлөөчүнүн өз сөзү да тике сөз формасында берилиши мүмкүн. Мисалы: а) Энелер Жамийланын мунусун кеп кылышып, анан кайра: «Бала да, бара-бара салмак тартар» - деп коюшчу (Ч.А.).\n" +
                        "б) «Үй ээси ушу болсо керек», - деп ойлоду Атай (К.К.). в) - Жүр, жеңе, үйгө кетели, - дедим мен (Ч.А.).\n" +
                        "Тике сөз түзүлүшү жагынан ар түрдүү формада жолугат: ал жөнөкөй сүйлөмдөн, татаал сүйлөмдөн түзүлөт, ал гана эмес бир сөздөн, жалаң сырдык сөздөн да болушу мүмкүн. Мисалы:\n" +
                        "а) Касым аттан түшөр замат: «Силер станцияга узатабыз деп убара болбогула, ата,» - деди (Ч.А.).\n" +
                        "б) «Ко-ош, ал жөнүндө талаш жок, бирок Каныбектин өзүн иштетип алууну ойлобо» - деди да, Түлкүбек жантая кетти (К.Ж.).\n" +
                        "в) - Ташта! Ташта капты! - деп кыйкырып жиберди Жамийла. - Ташта! - деп, Зуура тескери бурулуп кетти («КМ»).\n" +
                        "Тике сөз менен бирге айтылган төл сөз да түзүлүшү жагынан түрдүүчө болот: көбүнчө эки тутумдуу жөнөкөй сүйлөм түрүндө, кээде татаал сүйлөм түрүндө кездешет. Мисалы: Алиман андан: «Бул эмне болгон эшолон?» - деп шыбырап сурады (Ч.А.). Бригадир: - Сугатты качан бүтөсүңөр? - деди.\n" +
                        "Кээде төл сөздүн грамматикалык ээси, стилдик максатка ылайык, айтылбай да калат, бирок анын кайсы сөз экендиги баяндоочунун аягындагы жак мүчөдөн белгилүү болуп турат, же ээсинин кайсы сөз экендиги алдыңкы сүйлөмдөрдө айтылган болот. Мисалы: «Капырай ушунун баарын ал кайдан үйрөндү, кайдан укту экен?» -деп ойлоно берем (Ч.А.). - «Карыя, кайгырбаңыз, балаңыз үчүн биз душмандан өч алабыз», - деди (К.Б.). - Анчейин эле, иним, - деп, Алтынай Сулайманова эки-үч кайтара саатын карап коюп, башка эчтеке айткан жок (Ч.А.).\n" +
                        "Төл сөздүн тутумуна чакчыл түрмөктөр да көбүрөөк кошулуп айтылат: Анан ал бери кылчайды да, ойлоно калып: - Азыр үйүңдө болосуңбу? - деди (Ч.А.)\n" +
                        "\n\n\n3. Төл жана бөтөн сөздөргө коюлуучу тыныш белгилери.\n\n" +
                        "Жазуу кебинде тике сөзгө эки түрдүү тыныш белгиси коюлат: Эгерде тике сөз автордук баяндаманын ичинде берилсе, тырмакчага алынып жазылат. Мисалы: Апам: «Эл тарткан азапты эл менен кошо тарт» - деп, келинин кайраттандырмакчы болуп жемелеп коёт (Ч.А.).\n" +
                        "Эгерде тике сөз, өзгөчө көркөм адабий чыгармаларда, диалогдордун бир бөлүгү катары колдонулса, анда ал тырмакчага алынбай, жол башынан сызыкча коюлуп жазылат. Мисалы: Аскар бир тынымга унчукпай калып сурап койду: - Азыр сиздин оозуңуздан чарбанын бүгүнкү абалын уккум келет (Т.С.).\n" +
                        "Тике сөз өзү менен бирге айтылган төл сөздүн ар түрдүү жеринен орун алышы мүмкүн, ошого байланыштуу буларда тыныш белгиси да түрдүүчө коюлат:\n" +
                        "1).Төл сөз мурда, тике сөз андан кийин келсе, төл сөздөн кийин кош чекит (:) коюлат да, тике сөз баш тамга менен башталып жазылат. Мисалы: Алар мындай деп шыбырашкандай болду: «Азыр күмүш, алтын буюмдарды аябай көп жасап, пайдага тунуп калды...» Ал бир аздан кийин сөзүн улантты:\n" +
                        "- Илээлетип жетелеп отуруп араң апкелдим (М.Аб.).\n" +
                        "2). Тике сөз мурда, төл сөз андан кийин келсе, тике сөздүн аягына коюлуучу чекиттин ордуна үтүр коюлат, же болбосо тике сөздүн маанисине карай суроо, же илеп белгиси, же көп чекит коюлат. Анан бул белгилерден кийин сызыкча (-) коюлуп, төл сөз кичине тамга менен башталып жазылат. Мисалы: «Каранардын ушуякка келип калганы жакшы болгон экен», - дейт Эдигей ичинен (Ч.А.).- Тобо, түн ичинде эмне кылып жүргөн немелер? - деди шофёрдун жанында отурган тумакчан жигит (Ч.А.). - Кана, калайыктан бата алалы! - деп заңк этти маареке башы аксакал чабандестерге (Ч.А.). «Эл көрүп, каада көрүп калган экен... -деди Абдылдабек, ичи жылып (Т.К.).\n" +
                        "Минтип, тике сөз мурун келип, андан кийин төл сөз айтылганда, кээде ал экөө да жардамчы этиши менен байланышпай, интонация аркылуу, биринен кийин экинчиси удаалаш айтылып байланышат. Мындай учурда тике сөздүн аягына тиешелүү тыныш белгиси (чекит, суроо,илеп) коюлат да, андан кийин сызыкча коюлуп, төл сөз баш тамга менен башталып жазылат: - Куру кол эмесмин. Мына менин куралым. - Эдигей куржундан сабына оролгон шапалагын алып чыкты (Ч.А.). - Бая күнү эле ушинтпей! - Атай сууп калган чайын жутту (К.К.).\n" +
                        "3) Төл сөздүн ортосуна тике сөз келсе, төл сөздүн биринчи бөлүгүнөн кийин кош чекит коюлуп, тике сөз баш тамга менен башталып жазылат да, эгер аягына чекит коюла турган болсо, анын ордуна үтүр, же болбосо маанисине карай суроо, же илеп белгиси, же көп чекит коюлат. Анан бул белгилерден кийин сызыкча коюлуп, төл сөздүн экинчи бөлүгү кичине тамга менен башталып жазылат. Мисалы: Жаштар адегенде: «Азык алып жүрбөйбүз, жүгүбүз оор болуп, жолду арбытышка жолто кылат», -дешти.\n" +
                        "Дүйшөн анын бет алдына барып тура калып:\n" +
                        "- Сиз эмне жумуш менен келдиңиз? -деди (Ч.А.).\n" +
                        "Дүйшөн тигилерди качырып сала берди:\n" +
                        "- Качкыла, балдар! Кач, Алтынай! - деп кыйкырды (Ч.А.). Муну көргөн Бекназар баатыр: «Жакшы өңүт алган экен...» -\n" +
                        "деп, ичинен ойлонду (Т.К.).\n" +
                        "4). Тике сөздүн ортосуна төл сөз келгенде, тыныш белгилери төмөнкүдөй коюлат:\n" +
                        "а) Эгерде тике сөздү төл сөз эч кандай тыныш белгиси коюлбай турган жерден, же үтүр, үтүрлүү чекит, кош чекит коюла турган жерден бөлсө, тике сөздүн ортосуна келген төл сөз эки жагынан үтүр жана сызыкча менен ажыратылат. Тике сөздүн экинчи бөлүгү кичине тамга менен башталып жазылат:- Анан, - деди Үкүбала, - мындайда Зарипаны жалгыз жиберип болбойт (Ч.А.). - «Ким иштебесе, - деген В.И.Ленин, -ал тиштебейт». Азыр сенден өтүнүчүм бар, - деди ал, -төрүмдөгү меймандар жөнүндө кымындай шек чыгара көрбө (К.Осм.).\n" +
                        "б) Тике сөздү төл сөз чекит коюла турган биринчи бөлүмүнөн кийин бөлсө, ал чекиттин ордуна үтүр жана сызыкча коюлуп, андан кийин келген төл сөз кичине тамга менен башталып жазылат да, аягына чекит жана сызыкча коюлуп, тике сөздүн экинчи бөлүгү баш тамга менен башталып жазылат:«Бул сөздү кайдан айттым экен, - деди ичинен Чүкөбай. - Эми Зуура муну элге айтып, мени маскара кылат го» (К.Б.). - Мен макул эмесмин, - деди Эдигей. -Андай болушу мүмкүн эмес (Ч.А.).\n" +
                        "в) Төл сөз тике сөздү суроо, илеп белгиси же көп чекит коюла турган биринчи бөлүмүнөн кийин бөлсө, анда тике сөздөн кийин маанисине карай суроо, же илеп белгиси, же көп чекит жана сызыкча коюлуп, андан кийинки төл сөз кичине тамга менен жазылат да, аягына чекит жана сызыкча коюлуп, андан кийин келген тике сөздүн экинчи бөлүгү баш тамга менен башталып жазылат:\n" +
                        "«Сүйүнчү чыгар! - деди Аккыз. - Тилегибиз орундалды! (К.К.).- Балам, кайда жүрөсүң? - деп, бир маалда Карабаш келди. - Сени күтүп калдык (К.К.).\n" +
                        "- Эмне болсо да, кабар бер... - деди Саадат кетип бара жатып. - Мен күтөм (Т.С.).\n" +
                        "5) а) Тике сөз менен төл сөздү байланыштырган деп жардамчы этиши өзүнүн алдындагы тике сөзгө тиешелүү болуп, анын тутумуна кирсе, деп сөзүнөн кийин да үтүр коюлат: - Жетишем, али убакыт бар, - деп, Касымжан ширеңке чакты (Ч.А.).- Эмне болуп кетти буларга? - деп, Чүкөбай шашып тышка чыкты (К.Б.).\n" +
                        "б) Эгерде тике сөз менен төл сөздү байланыштырган деп төл сөздүн баяндоочуна тиешелүү болуп, анын тутумунда айтылса, деп сөзүнөн кийин үтүр коюлбайт: Ала келген түйүнчөгүмдү көрө коюп: «Эне, алма алып келдиңби?» - деп сүйүнүп кетти Жанболот (Ч.А.).- Булар эмнеден коркуп жүрөт? - деп сурады Нурбек (Ч.А.).\n" +
                        "6) Суроо - жооп иретинде айтылган сүйлөмдөр (диалогдор) да эки түрдүү берилет:\n" +
                        "а) Эгерде ар бир кишинин сөзү (тике сөз) жалпы тексттен бөлүнүп алынып, абзац менен берилсе, жол башынан сызыкча коюлуп, тырмакчага алынбай жазылат. Төл сөз кошо айтылса, тиешелүү тыныш белгилери сакталат:\n" +
                        "- Данике, сайдагы аттар сеникиби? -дедим. - Ал бурула берип, жай карап койду:\n" +
                        "- Экөө меники.\n" +
                        "- А беркилеричи?\n" +
                        "- Жанагы Жамийла дейби ... ошол келиндики. - Сенин ал жеңең беле?\n" +
                        "- Жеңем.\n" +
                        "- Бүгүн багып бересиң деп, бригад өзү таштап кетти (Ч.А.).\n" +
                        "б) Эгерде суроо - жооп иретинде айтылган сүйлөмдөр абзац менен бөлүнбөй, тексттин ичинде берилсе, ар бир кишинин сөзү (тике сөз) тырмакчага алынып, бири-биринен сызыкча аркылуу бөлүнөт. Төл сөз катышса, тиешелүү тыныш белгиси сакталат:\n" +
                        "- «Абуталип Куттыбаев деген иштеди беле силерде?» - «Бизде иштеген, эмне болуптур?» - «Мобу каттын келгенине үч күн болду. Ме, аялына алпарып бер» - деп, катты берди Чернов (Ч.А.).");
                break;
        }
    }
}
